INSERT INTO `v1_0036905810d4482541d7b240265c28d51356b363245160ace170eb5be55c2ad3` VALUES (20008, "なし", "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", "アキノのゲヘニアンチキンプレート", 52022, 3, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 0, 0, 2, 51022, 500, "アキノのゲヘニアン\nチキンプレート", 1, 0, 0, 1);
