INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 4, 4, 1, 12403, 4, 6, 6160, "ろくろくび", "ろくろくび", "首が長く伸びる妖怪。普通にしている分には、人間と変わらない姿をしている", 12429, "これ、何と言う？", 12429, 4, 4, 0, 12367, 12367, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 4, 4, 2, 12385, 4, 6, 6161, "かまいたち", "かまいたち", "つむじ風に乗って現れ、人を切りつける妖怪。痛みは無く、血も出ず、傷だけが残ると言われる", 12363, "これ、何と言う？", 12356, 4, 4, 0, 12414, 12383, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 5, 5, 3, 12523, 5, 6, 6162, "フェンリル", "フェンリル", "神話に登場する巨大な狼。ラグナロクではオーディンを丸呑みにしたとされる", 12501, "これ、何と言う？", 12531, 5, 5, 0, 12455, 12522, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (1, 4, 4, 2, 12377, 4, 2, 6164, "やばいです", "やばいですね☆", "ペコリーヌの口癖の一つ。良い意味でも悪い意味でもヤバイことが起きたときに言うことが多い", 12420, "これ、何と言う？", 12356, 4, 4, 1255, 12400, 12391, "");
