INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 4, 4, 3, 12400, 4, 6, 6169, "いときりば", "いときりば", "「犬歯」の通称として、「八重歯」以外になじみ深いもの。糸が切れるほど尖っているのが由来", 12356, "これ、何と言う？", 12365, 4, 4, 0, 12392, 12426, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 5, 5, 1, 12531, 5, 6, 6170, "カネション", "カーネーション", "「母の日」の贈り物としても親しまれる花。赤やピンクの鮮やかな花びらを持つ", 12459, "これ、何と言う？", 12471, 5, 5, 0, 12493, 12519, "カーネーション");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 5, 5, 2, 12494, 5, 6, 6171, "メイヨシノ", "ソメイヨシノ", "桜の木の種類として非常にポピュラーな一つ。一般に桜のイメージとして最も浸透している", 12513, "これ、何と言う？", 12520, 5, 5, 0, 12452, 12471, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 5, 5, 3, 12531, 5, 6, 6172, "デライオン", "ダンデライオン", "「タンポポ」の英名。とある哺乳類の動物に由来している", 12487, "これ、何と言う？", 12452, 5, 5, 0, 12521, 12458, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 4, 4, 3, 12358, 4, 6, 6173, "せいりゅう", "せいりゅう", "「四聖獣」と呼ばれる生き物、朱雀・白虎・玄武、あとは何？（ひらがなで）", 12379, "これ、何と言う？", 12426, 4, 4, 0, 12356, 12421, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 6, 6, 1, 12375, 4, 6, 6174, "座敷わらし", "座敷わらし", "小さな子どもの姿で、座敷や土間に現れる妖怪。遭遇した者には幸運が舞い込むとされる", 24231, "これ、何と言う？", 12431, 4, 4, 0, 25975, 12425, "");
