INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 4, 6, 1, 12375, 4, 6, 6178, "猫じゃらし", "猫じゃらし", "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", 29483, "これ、何と言う？", 12419, 4, 4, 0, 12376, 12425, "");
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2, 5, 5, 1, 12521, 5, 6, 6179, "ドラキュラ", "ドラキュラ", "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", 12489, "これ、何と言う？", 12461, 5, 5, 0, 12521, 12517, "");
