INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "クランポン", "これ、何と言う？", 2, 1068, 12509, 5, "", "クランポン", 12531, 12521, 5, 1, "登山において、雪や氷の上を歩く際に靴に装備する金属の爪", 5, 12463);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12464, "ハイキング", "これ、何と言う？", 2, 1070, 12531, 5, "", "ハイキング", 12461, 12452, 5, 1, "軽微な装備で、自然や景観を楽しみながら歩くアクティビティ", 5, 12495);
