INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12452, "バタフライ", "これ、何と言う？", 2, 1075, 12521, 5, "", "バタフライ", 12501, 12479, 5, 1, "水泳で両腕を同時に前後に、両足を同時に上下に動かす泳ぎ方。動きがとある生き物に似ている", 5, 12496);
