INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "デカスロン", "これ、何と言う？", 2, 1077, 12525, 5, "", "デカスロン", 12473, 12459, 5, 1, "「10種」の種目を行い、その合計点を競う陸上競技。「10」を意味する言葉が含まれる", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12370, "ともえなげ", "これ、何と言う？", 2, 1078, 12394, 4, "", "ともえなげ", 12360, 12418, 4, 1, "柔道で、相手の下にもぐりこみ、後方にけり投げる技。その姿勢が、「とある漢字」に似ている", 4, 12392);
