INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12489, "トルネード投法", "これ、何と言う？", 2, 1122, 12540, 5, "", "トルネード", 12493, 12523, 5, 1, "野球の投球方法の一つ。大きく身体をひねって投げる動きが、まるで竜巻を思わせる", 5, 12488);
