INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 3, 4, 21476, "掛かり稽古", "これ、何と言う？", 2, 1210, 31293, 6, "", "掛かり稽古", 12426, 12363, 6, 1, "剣道の練習法。ふた手に分かれて、一方がもう一方に対し、休憩をせずに繰り返しかかっていく", 4, 25499);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 3, 5, 25731, "クレー射撃", "これ、何と言う？", 2, 1211, 23556, 5, "", "クレー射撃", 12540, 12524, 6, 1, "射撃競技の一つ。高速で空中に射出された皿を、ショットガンで撃ち壊す精度を競う", 5, 12463);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12489, "イエローカード", "これ、何と言う？", 2, 1212, 12459, 5, "イエローカード", "イエロカド", 12525, 12456, 5, 1, "サッカーで悪質な反則に対して示される警告。これが2回出ると、さらに重いレッドカードとなる", 5, 12452);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12523, "フットサル", "これ、何と言う？", 2, 1213, 12469, 5, "", "フットサル", 12488, 12483, 5, 1, "サッカーから派生したスポーツで、5人対5人形式のサッカー。室内でプレイされることも。", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12370, "せおいなげ", "これ、何と言う？", 2, 1214, 12394, 4, "", "せおいなげ", 12356, 12362, 4, 1, "柔道の花形の技としてよく知られる投げ技。相手を背負うような形で、肩越しに投げる", 4, 12379);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 67, "ウルトラC", "これ、何と言う？", 2, 1215, 12521, 5, "", "ウルトラC", 12488, 12523, 2, 1, "最近では「秘密兵器」という意味でも使われるが、元々は体操競技で非常に難しい技を指す言葉", 5, 12454);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12488, "カタパルト", "これ、何と言う？", 2, 1216, 12523, 5, "", "カタパルト", 12497, 12479, 5, 1, "かつて使われた攻城兵器。てこの原理を利用して、遠くまで石を射出する", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 33382, "イージス艦", "これ、何と言う？", 2, 1217, 12473, 5, "", "イージス艦", 12472, 12540, 6, 1, "高度な対空射撃設備を搭載した軍艦。神話の中でゼウスが娘に与えた盾の名前が由来", 5, 12452);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12523, "ツーマンセル", "これ、何と言う？", 2, 1218, 12475, 5, "ツーマンセル", "ツマンセル", 12531, 12510, 5, 1, "軍隊や警察において使われる言葉で、「二人一組」のこと", 5, 12484);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12488, "ホロサイト", "これ、何と言う？", 2, 1219, 12452, 5, "", "ホロサイト", 12469, 12525, 5, 1, "銃で狙いを定めるための「照準器」の種類の一つ。いわゆる「ホログラム」の技術を使っている", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12469, "サイレンサー", "これ、何と言う？", 2, 1220, 12531, 5, "", "サイレンサ", 12524, 12452, 5, 1, "スパイ映画でもよく見る、銃の発砲音を抑えるために銃口につける装置。「サプレッサー」とも", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12488, "3点バースト", "これ、何と言う？", 2, 1221, 12473, 6, "", "点バースト", 12540, 12496, 5, 1, "ライフルなどの仕組みで、一度引き金を引くたびに、銃弾が3発ずつ発射されるもの", 5, 28857);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12540, "ホルスター", "これ、何と言う？", 2, 1222, 12479, 5, "", "ホルスター", 12473, 12523, 5, 1, "拳銃を収めるために、主に腰に装備するケース。西部劇での決闘シーンなどでもよく目立つ", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 5, 12358, "ハロンぼう", "これ、何と言う？", 2, 1223, 12412, 5, "", "ハロンぼう", 12531, 12525, 4, 1, "競馬で、ゴールから一定距離ごとに目印として置かれる棒。赤と白のシマシマ柄をしている", 5, 12495);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12540, "ステイヤー", "これ、何と言う？", 2, 1224, 12516, 5, "", "ステイヤー", 12452, 12486, 5, 1, "競馬で、スタミナが多く長距離レースを得意とする馬のこと", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (1225, 5, 2, 5, 12488, "テンペストブレード", "これ、何と言う？", 1, 1225, 12473, 5, "", "テンペスト", 12506, 12531, 5, 1, "『ジータ』のユニオンバーストの名前", 5, 12486);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12463, "フィジーク", "これ、何と言う？", 2, 1226, 12540, 5, "", "フィジーク", 12472, 12451, 5, 1, "肉体美を競うコンテスト。よくボディビルと比較されるが、評価の観点や、対象の部位が異なる", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12464, "レスリング", "これ、何と言う？", 2, 1227, 12531, 5, "", "レスリング", 12522, 12473, 5, 1, "「プロレス」の「レス」、何という言葉の略？", 5, 12524);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12488, "ストレート", "これ、何と言う？", 2, 1228, 12540, 5, "", "ストレート", 12524, 12488, 5, 1, "格闘技のパンチの種類の一つ。「ジャブ」と並んで重要となる、最も威力の出やすいもの", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12488, "ノックアウト", "これ、何と言う？", 2, 1229, 12454, 5, "ノックアウト", "ノクアウト", 12450, 12463, 5, 1, "格闘技での勝敗判定の一つ。相手をダウンさせたまま、既定の時間が過ぎたとき", 5, 12494);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12523, "テクニカル", "これ、何と言う？", 2, 1230, 12459, 5, "", "テクニカル", 12491, 12463, 5, 1, "格闘技で、ただのノックアウトとは異なった特殊な判定である「TKO」。この「T」は何の略？", 5, 12486);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12540, "ウェルター級", "これ、何と言う？", 2, 1231, 12479, 5, "", "ウェルター", 12523, 12455, 5, 1, "ボクシングの階級で「ライト」などのつくものを省いて考えた場合、「ミドル級とライト級の間」は何？", 5, 12454);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "ブーメラン", "これ、何と言う？", 2, 1232, 12521, 5, "", "ブーメラン", 12513, 12540, 5, 1, "狩猟のために生まれた武器。「くの字型」をしており、投げた後Uターンして手元に戻ってくる", 5, 12502);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "ネクロマンサー", "これ、何と言う？", 2, 1233, 12510, 5, "", "ネクロマン", 12525, 12463, 5, 1, "「死霊使い」とも呼ばれる、ファンタジーでもおなじみの存在。死者を蘇らせ操る術を持つ", 5, 12493);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12454, "グラディウス", "これ、何と言う？", 2, 1234, 12451, 5, "", "グラディウ", 12487, 12521, 5, 1, "かつての剣闘士（グラディエーター）が用いた、幅広の剣", 5, 12464);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12458, "コロッセオ", "これ、何と言う？", 2, 1235, 12475, 5, "", "コロッセオ", 12483, 12525, 5, 1, "「コロシアム」「コロセウム」とも表記される、非常によく知られた円形闘技場", 5, 12467);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12463, "ノールックパス", "これ、何と言う？", 2, 1236, 12483, 5, "", "ノールック", 12523, 12540, 5, 1, "球技スポーツで、そちらに目線を向けることなくパスをして、相手を出し抜くテクニックのこと", 5, 12494);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "スタンガン", "これ、何と言う？", 2, 1237, 12460, 5, "", "スタンガン", 12531, 12479, 5, 1, "護身用や警官が持つ装備として知られた、手持ちサイズの武器。高圧の電撃で相手を無力化する", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12358, "にょいぼう", "これ、何と言う？", 2, 1238, 12412, 4, "", "にょいぼう", 12356, 12423, 4, 1, "西遊記の「孫悟空」が武器に使った、自由自在に拡大・縮小できる棒", 4, 12395);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12474, "デスサイズ", "これ、何と言う？", 2, 1239, 12452, 5, "", "デスサイズ", 12469, 12473, 5, 1, "「死神」が持つと言われる大きな鎌のこと", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (1240, 4, 2, 4, 12435, "りゅうがん", "これ、何と言う？", 1, 1240, 12364, 4, "", "りゅうがん", 12358, 12421, 4, 1, "『ホマレ』が持つ能力で、発動中は遠く離れたものも見ることができる", 4, 12426);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12464, "ドーピング", "これ、何と言う？", 2, 1241, 12531, 5, "", "ドーピング", 12500, 12540, 5, 1, "スポーツにおいて、薬物などの禁止された手段を用いて、不当に能力を高めること", 5, 12489);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12540, "スライダー", "これ、何と言う？", 2, 1242, 12480, 5, "", "スライダー", 12452, 12521, 5, 1, "野球の投球における球種の一つ。文字通り、スルリと滑るような変化をすることから", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12512, "ツーシーム", "これ、何と言う？", 2, 1243, 12540, 5, "", "ツーシーム", 12471, 12540, 5, 1, "野球の投球における球種の一つ。球が1回転する間に、縫い目が合計で2回通過することから", 5, 12484);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12450, "アンパイア", "これ、何と言う？", 2, 1244, 12452, 5, "", "アンパイア", 12497, 12531, 5, 1, "広義にはスポーツ全般の審判員を指すが、一般には特に野球の審判員を指すことが多い", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12512, "ナイター⇔デイゲーム", "これ、何と言う？", 2, 1245, 12540, 5, "", "デイゲーム", 12466, 12452, 5, 1, "野球の試合の、開催形式の分類。時間帯によって、2つに分かれる", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12512, "ロスタイム", "これ、何と言う？", 2, 1246, 12452, 5, "", "ロスタイム", 12479, 12473, 5, 1, "サッカーで「アディショナルタイム」と呼ばれるルールの、かつての呼び方", 5, 12525);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "センタリング", "これ、何と言う？", 2, 1247, 12522, 5, "", "センタリン", 12479, 12531, 5, 1, "サッカーで、サイドから中央のゴール付近の味方へ大きなパスを出すこと", 5, 12475);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12523, "オウンゴール", "これ、何と言う？", 2, 1248, 12468, 5, "オウンゴール", "オウンゴル", 12531, 12454, 5, 1, "サッカーで、間違って自陣側のゴールにボールを入れてしまい、相手に得点が入ること", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12488, "フェイント", "これ、何と言う？", 2, 1249, 12531, 5, "", "フェイント", 12452, 12455, 5, 1, "多くのスポーツで、相手の意表を突くためにわざと行う「ふり動作」のこと。「フェイク」とも", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12435, "ごっつぁんです", "これ、何と言う？", 2, 1250, 12353, 4, "", "ごっつぁん", 12388, 12387, 4, 1, "力士の間で、お礼を言うときに広く使われるあいさつ。昨今は相撲以外の一般層にも浸透している", 4, 12372);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 6, 12540, "聖火リレー", "これ、何と言う？", 2, 1251, 12524, 6, "", "聖火リレー", 12522, 28779, 5, 1, "オリンピックで、トーチに灯した火をランナーが次々と受け継いで、会場までリレーをする催し", 5, 32854);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12464, "ダイビングキャッチ", "これ、何と言う？", 2, 1252, 12531, 5, "", "ダイビング", 12499, 12452, 5, 1, "野球の野手が、大きく頭から飛び込んで打球をキャッチするプレー。成功すると非常に盛り上がる", 5, 12480);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12464, "フレーミング", "これ、何と言う？", 2, 1253, 12531, 5, "フレーミング", "フレミング", 12511, 12524, 5, 1, "野球の捕手が、判定の際どいボールを、自分がうまく動くことで審判にストライクと判定させる技術", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (1255, 5, 1, 5, 12463, "プリンセスストライク", "これ、何と言う？", 1, 1255, 12452, 5, "", "ストライク", 12521, 12488, 5, 1, "『ペコリーヌ』のユニオンバースト", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 1, 6, 21147, "物理攻撃力", "これ、何と言う？", 1, 1256, 25731, 6, "", "物理攻撃力", 25915, 29702, 6, 1, "プリコネRのバトルで、「物理攻撃で与えられるダメージ」に大きくかかわるステータス", 6, 29289);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 1, 6, 21147, "魔法攻撃力", "これ、何と言う？", 1, 1257, 25731, 6, "", "魔法攻撃力", 25915, 27861, 6, 1, "プリコネRのバトルで、「魔法攻撃で与えられるダメージ」に大きくかかわるステータス", 6, 39764);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 1, 6, 21147, "物理防御力", "これ、何と言う？", 1, 1258, 24481, 6, "", "物理防御力", 38450, 29702, 6, 1, "プリコネRのバトルで、「物理攻撃で受けるダメージ」に大きくかかわるステータス", 6, 29289);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 1, 6, 21147, "魔法防御力", "これ、何と言う？", 1, 1259, 24481, 6, "", "魔法防御力", 38450, 27861, 6, 1, "プリコネRのバトルで、「魔法攻撃で受けるダメージ」に大きくかかわるステータス", 6, 39764);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "メモリーピース", "これ、何と言う？", 1, 1260, 12500, 5, "メモリーピース", "メモリピス", 12522, 12514, 5, 1, "プリコネRのバトルに関わる強化方法で、「才能開花」をするために必要なアイテム", 5, 12513);
