INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12488, "アラカルト", "これ、何と言う？", 2, 5006, 12523, 5, "", "アラカルト", 12459, 12521, 5, 5, "コース料理ではなく、好みの料理をメニューから選んで注文すること", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12476, "カプレーゼ", "これ、何と言う？", 2, 5007, 12540, 5, "", "カプレーゼ", 12524, 12503, 5, 5, "トマト、モッツァレラチーズ、バジルを盛りつけた、「カプリ風」という意味の前菜", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12540, "チェイサー", "これ、何と言う？", 2, 5010, 12469, 5, "", "チェイサー", 12452, 12455, 5, 5, "強いお酒を飲むときに、口直しするための飲み物。特に冷水が一般的", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12521, "フォアグラ", "これ、何と言う？", 2, 5011, 12464, 5, "", "フォアグラ", 12450, 12457, 5, 5, "世界三大珍味と呼ばれる食べ物。キャビア、トリュフ、あと一つは？", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12467, "バルサミコ酢", "これ、何と言う？", 2, 5013, 12511, 5, "", "バルサミコ", 12469, 12523, 5, 5, "ブドウの果汁を煮詰めて樽で熟成発酵させたとろみのあるお酢", 5, 12496);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12523, "オードブル", "これ、何と言う？", 2, 5014, 12502, 5, "", "オードブル", 12489, 12540, 5, 5, "「作品外」という意味で、コース料理の最初に食欲をそそるための軽い前菜", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12450, "サングリア", "これ、何と言う？", 2, 5015, 12522, 5, "", "サングリア", 12464, 12531, 5, 5, "「ワイン」をベースにして、リンゴやオレンジなどの果物やシナモンを加えた飲み物", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12365, "えほうまき", "これ、何と言う？", 2, 5016, 12414, 4, "", "えほうまき", 12358, 12411, 4, 5, "節分の日に食べる太巻き寿司。特定の方角を向いて、無言で食べると良いとされる", 4, 12360);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12519, "アヒージョ", "これ、何と言う？", 2, 5017, 12472, 5, "", "アヒージョ", 12540, 12498, 5, 5, "ニンニクとオリーブオイルで、エビやキノコなどの具材を煮込んだ料理", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "イートイン", "これ、何と言う？", 2, 5018, 12452, 5, "", "イートイン", 12488, 12540, 5, 5, "「テイクアウト」の逆で、店内で食事をすること", 5, 12452);
