INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12489, "プリンアラモード", "これ、何と言う？", 2, 5052, 12540, 5, "", "アラモード", 12514, 12521, 5, 5, "プリンを中心に、周りに様々な甘味を盛りつけたスイーツ", 5, 12450);
