INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 36074, "たんぱく質", "これ、何？", 2, 5054, 12367, 4, "", "たんぱく質", 12401, 12435, 6, 5, "「５大栄養素」、脂質・糖質・ビタミン・ミネラル、あと一つは？", 4, 12383);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12540, "麦⇒ウイスキー　果物⇒ブランデー", "これ、何？", 2, 5055, 12487, 5, "", "ブランデー", 12531, 12521, 5, 5, "", 5, 12502);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "カルダモン", "これ、何と言う？", 2, 5056, 12514, 5, "", "カルダモン", 12480, 12523, 5, 5, "「スパイスの女王」「香りの王様」などの別名を持つ香辛料。カレーやチャイには欠かせない", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12435, "かしわてん", "これ、何と言う？", 2, 5057, 12390, 4, "", "かしわてん", 12431, 12375, 4, 5, "鶏肉の天ぷらのこと", 4, 12363);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12523, "ボタニカル", "これ、何と言う？", 2, 5058, 12459, 5, "", "ボタニカル", 12491, 12479, 5, 5, "近年シャンプーや化粧品によく見る「植物由来の」の意味の言葉。オーガニックとはやや異なる", 5, 12508);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12426, "ごりんがり", "これ、何と言う？", 2, 5059, 12364, 4, "", "ごりんがり", 12435, 12426, 4, 5, "髪の毛を「２ミリメートル」ほどに短く丸刈りにした髪型", 4, 12372);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12463, "ツーブロック", "これ、何と言う？", 2, 5060, 12483, 5, "ツーブロック", "ツブロック", 12525, 12502, 5, 5, "髪型の一種。髪の毛を伸ばしてある部分と、短く刈り上げた部分の２つにきっぱり分かれている", 5, 12484);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12473, "ピラティス", "これ、何と言う？", 2, 5061, 12451, 5, "", "ピラティス", 12486, 12521, 5, 5, "マット上で体幹を鍛え、身体の機能を整えるエクササイズ。ヨガと近い要素もあるが別物である", 5, 12500);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12475, "デグラッセ", "これ、何と言う？", 2, 5062, 12483, 5, "", "デグラッセ", 12521, 12464, 5, 5, "洋食のテクニック。肉を焼いた鍋底に焦げ付いた旨味をワインでこそぎ取り、味付けに利用する", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "グルタミン酸", "これ、何と言う？", 2, 5063, 12511, 5, "", "グルタミン", 12479, 12523, 5, 5, "和食に欠かせない「旨味」のうち、三大旨味成分と呼ばれるのは、イノシン酸・グアニル酸と何？", 5, 12464);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "カフェイン", "これ、何と言う？", 2, 5064, 12452, 5, "", "カフェイン", 12455, 12501, 5, 5, "一般にコーヒーを飲んで摂取することが多い、眠気覚ましの効果がある成分", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "パルメザンチーズ", "これ、何と言う？", 2, 5065, 12470, 5, "", "パルメザン", 12513, 12523, 5, 5, "「粉チーズ」と言うと、これを指すのが一般的。パスタなどの風味づけに使われることが多い", 5, 12497);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12356, "しょっぱい", "これ、何と言う？", 2, 5066, 12401, 4, "", "しょっぱい", 12387, 12423, 4, 5, "「甘味」なら「あまい」、「酸味」なら「すっぱい」。では「塩味」なら？", 4, 12375);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12458, "花椒＝ホアジャオ", "これ、何と読む？", 2, 5067, 12515, 5, "", "ホアジャオ", 12472, 12450, 5, 5, "スパイスの一つ。「かしょう」以外の読み方は？", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "モンブラン", "これ、何と言う？", 2, 5068, 12521, 5, "", "モンブラン", 12502, 12531, 5, 5, "ケーキの種類の一つ。「栗（クリ）」のクリームを、しぼるように飾り付ける", 5, 12514);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12523, "セミダブル", "これ、何と言う？", 2, 5069, 12502, 5, "", "セミダブル", 12480, 12511, 5, 5, "ベッドの大きさの種類。「シングル」と「ダブル」の間の大きさは？", 5, 12475);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12489, "レモネード", "これ、何と言う？", 2, 5070, 12540, 5, "", "レモネード", 12493, 12514, 5, 5, "レモン果汁に砂糖やシロップ、はちみつなどを加えて薄めた飲み物", 5, 12524);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "ユニットバス", "これ、何と言う？", 2, 5071, 12496, 5, "ユニットバス", "ユニトバス", 12488, 12491, 5, 5, "一般には、浴槽とトイレと洗面台が一緒になったタイプのバスルームのこと", 5, 12518);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12540, "バルコニー", "これ、何と言う？", 2, 5072, 12491, 5, "", "バルコニー", 12467, 12523, 5, 5, "住宅の設備の一つ。ベランダと混同されるが、上に屋根が無く解放されているという点で異なる", 5, 12496);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12367, "ようしょく", "これ、何と言う？", 2, 5073, 12423, 4, "", "ようしょく", 12375, 12358, 4, 5, "マグロなどの食用の魚介で、「天然」の反対をなんと言う？", 4, 12424);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12473, "デトックス", "これ、何と言う？", 2, 5074, 12463, 5, "", "デトックス", 12483, 12488, 5, 5, "体内の老廃物や毒素を排出する健康法。食事やエクササイズ、マッサージなど、方法はさまざま", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12375, "てんぴぼし", "これ、何と言う？", 2, 5075, 12412, 4, "", "てんぴぼし", 12404, 12435, 4, 5, "布団や洗濯物を直射日光の下で干すこと。紫外線の殺菌効果で雑菌の繁殖を抑えることができる", 4, 12390);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12367, "こんにゃく", "これ、何と言う？", 2, 5076, 12419, 4, "", "こんにゃく", 12395, 12435, 4, 5, "糸□□□□□、玉□□□□□、刺身□□□□□。共通して当てはめられる食材は？", 4, 12371);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12463, "メタボリック・シンドローム", "これ、何と言う？", 2, 5077, 12522, 5, "メタボリック・シンドローム", "メタボリク", 12508, 12479, 5, 5, "「メタボ」を略さずに言うと？", 5, 12513);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "ブーケトス", "これ、何と言う？", 2, 5078, 12488, 5, "", "ブーケトス", 12465, 12540, 5, 5, "結婚式で花嫁が女性ゲストに向けて花束を投げること", 5, 12502);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "サーロイン", "これ、何と言う？", 2, 5079, 12452, 5, "", "サーロイン", 12525, 12540, 5, 5, "牛肉の部位の呼び方の一種。背中から腰にかけてを指す。ステーキなどで好まれる", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12540, "カトラリー", "これ、何と言う？", 2, 5081, 12522, 5, "", "カトラリー", 12521, 12488, 5, 5, "食卓用のナイフ、フォーク、スプーンなどの総称", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12451, "QOL＝クオリティオブライフ", "これ、何と言う？", 2, 5082, 12486, 5, "", "クオリティ", 12522, 12458, 5, 5, "「生活の質」のことを表した略語", 5, 12463);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12473, "デミグラスソース", "これ、何と言う？", 2, 5083, 12521, 5, "", "デミグラス", 12464, 12511, 5, 5, "洋食のソースの一つ。ブラウンソースを煮詰めて出汁やワインなどで風味付けしたもの", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12523, "ベシャメルソース", "これ、何と言う？", 2, 5084, 12513, 5, "", "ベシャメル", 12515, 12471, 5, 5, "洋食のソースの一つ。小麦粉とバターを熱しながら混ぜ、牛乳で溶かしてから味付けしたもの", 5, 12505);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5039, 6, 3, 6, 22269, "ディアボロス＝悪魔偽王国軍", "このギルド、漢字で書くと？", 1, 5088, 29579, 6, "", "悪魔偽王国", 20605, 39764, 6, 5, "", 6, 24746);
