INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12365, "かつらむき", "これ、何と言う？", 2, 5091, 12416, 4, "", "かつらむき", 12425, 12388, 4, 5, "大根などの野菜を薄く長くむいていく事を何という？", 4, 12363);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 33609, "ほうれん草", "これ、何と言う？", 2, 5092, 12435, 4, "", "ほうれん草", 12428, 12358, 6, 5, "カレーの一種である「サグカレー」。使われている緑の野菜の名前は？", 4, 12411);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "カレーライス", "これ、何？", 2, 5097, 12452, 5, "カレーライス", "カレライス", 12521, 12524, 5, 5, "戦隊モノのイエローの好物といえばどんな食べ物？", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "キャンパス", "これ、何と言う？", 2, 5098, 12497, 5, "", "キャンパス", 12531, 12515, 5, 5, "「○○○○○ライフ」などの言葉でもおなじみ。大学の敷地のことをなんと言う？", 5, 12461);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12435, "ひゃっきん", "これ、何と言う？", 2, 5099, 12365, 4, "", "ひゃっきん", 12387, 12419, 4, 5, "全ての商品が100円で統一されているタイプの生活用品店。一般に何と略す？", 4, 12402);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12435, "敷き布団⇔かけふとん", "これ、何と言う？", 2, 5100, 12392, 4, "", "かけふとん", 12405, 12369, 4, 5, "", 4, 12363);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2268, 5, 1, 5, 12523, "ゴーヤーチャンプルー", "これ、何と言う？", 1, 5101, 12503, 5, "", "チャンプル", 12531, 12515, 5, 5, "『カオリ』のキャラストーリーで作った料理。ゴーヤーの調理方法としても非常にポピュラー", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5102, 4, 1, 4, 12497, "焼きそばパン", "何を食べている場面？", 1, 5102, 12400, 6, "", "焼きそばパ", 12381, 12365, 5, 5, "", 4, 28988);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (2088, 5, 2, 5, 29579, "マホマホ王国", "これ、何と言う？", 1, 5103, 12507, 5, "", "マホマホ王", 12510, 12507, 6, 5, "『マホ』いわく、彼女の故郷の土地。地図には載っていないが、実在している…らしい", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5104, 6, 2, 4, 29702, "おせち料理", "これ、何？", 1, 5104, 26009, 4, "", "おせち料理", 12385, 12379, 6, 5, "シェフィが「どんな料理」\nの具材を集めに、おつ\nかいに出るストーリー？", 4, 12362);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 4, 12531, "あさシャン", "これ、何と言う？", 2, 5106, 12515, 4, "", "あさシャン", 12471, 12373, 5, 5, "毎日、朝の起きがけにシャンプーをする習慣、略してなんと言う？", 5, 12354);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5107, 5, 2, 5, 12473, "美食殿のギルドハウス", "この場所の名前は？", 1, 5107, 12454, 5, "", "ルドハウス", 12495, 12489, 5, 5, "", 5, 12523);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5108, 4, 2, 4, 12375, "うらないし", "これ、何？", 1, 5108, 12356, 4, "", "うらないし", 12394, 12425, 4, 5, "霊能力で物事を見通すことができる『シノブ』は、普段どんな仕事を生業にしている？", 4, 12358);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12385, "三が日＝さんがにち", "これ、何と言う？", 2, 5109, 12395, 4, "", "さんがにち", 12364, 12435, 4, 5, "正月の1月1日～1月3日までの期間", 4, 12373);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5110, 5, 1, 5, 12531, "ダンジョンコイン", "これ、何と言う？", 1, 5110, 12519, 5, "", "ダンジョン", 12472, 12531, 5, 5, "ショップで様々なアイテムを購入するのに使うアイテム。ダンジョンをプレイすると手に入る", 5, 12480);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5111, 5, 2, 5, 12522, "ミックスベリーケーキ", "これ、何と言う？", 1, 5111, 12505, 5, "ミックスベリーケーキ", "ミクスベリ", 12473, 12463, 5, 5, "キャラとの絆Ptがアップするアイテム", 5, 12511);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5112, 5, 2, 5, 23627, "クレープ屋", "これは何？", 1, 5112, 12503, 5, "", "クレープ屋", 12540, 12524, 6, 5, "『ラビリスタ』が店主を務めるのは、どんな飲食店？（とある洋菓子を販売している）", 5, 12463);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12383, "おとしぶた", "これ、何と言う？", 2, 5113, 12406, 4, "", "おとしぶた", 12375, 12392, 4, 5, "煮物料理で、鍋のフチに被せるフタではなく、鍋の中の食材に直接被せるようにするフタのこと", 4, 12362);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12517, "キャッシュレス決済", "これ、何と言う？", 2, 5114, 12471, 5, "", "キャッシュ", 12483, 12515, 5, 5, "買い物で、現金で直接決済するのではなく、電子マネー等を用いて決済する方法", 5, 12461);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "ウェルダン", "これ、何と言う？", 2, 5115, 12480, 5, "", "ウェルダン", 12523, 12455, 5, 5, "ステーキの焼き加減。レア⇒ミディアム⇒□□□□□", 5, 12454);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12435, "しちごさん", "これ、何と言う？", 2, 5116, 12373, 4, "", "しちごさん", 12372, 12385, 4, 5, "子どもの成長を祈願し、3歳・5歳・7歳のときに着物を着飾って、寺社を参拝する習慣", 4, 12375);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12472, "ソーセージ", "これ、何と言う？", 2, 5117, 12540, 5, "", "ソーセージ", 12475, 12540, 5, 5, "肉を腸詰めにした加工食品のこと。「ウインナー」は、これのうちの一種と定義づけられている", 5, 12477);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 2, 6, 37070, "一姫二太郎", "これ、何と言う？", 2, 5118, 22826, 6, "", "一姫二太郎", 20108, 23019, 6, 5, "1人目の子どもは女の子、2人目は男の子が良い。という意味の言い伝え", 6, 19968);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12378, "さんばいず", "これ、何と言う？", 2, 5119, 12356, 4, "", "さんばいず", 12400, 12435, 4, 5, "酢・醤油・みりんを同じ比率で合わせた調味料", 4, 12373);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "ゴーストレストラン", "これ、何と言う？", 2, 5120, 12521, 5, "", "レストラン", 12488, 12473, 5, 5, "実店舗を持たず、デリバリー専門として営業する飲食店のスタイル", 5, 12524);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12488, "LCC＝ローコストキャリア", "これ、略さずに言うと？", 2, 5121, 12473, 5, "", "ローコスト", 12467, 12540, 5, 5, "割安での航空券の提供をウリとしている、近年広く普及した形態の航空会社の通称", 5, 12525);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 3, 6, 29992, "トクホ＝特定保健用食品", "これ、略さずに言うと？", 2, 5122, 20581, 6, "", "特定保健用", 20445, 23450, 6, 5, "", 6, 29305);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12491, "マヌカハニー", "これ、何と言う？", 2, 5123, 12495, 5, "", "マヌカハニ", 12459, 12492, 5, 5, "特定の種の花からのみ採れる希少なハチミツで、殺菌・消毒・抗炎症など多くの健康作用がある", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 36786, "アイガモ農法", "これ、何と言う？", 2, 5124, 12514, 5, "", "アイガモ農", 12460, 12452, 6, 5, "米作りにおける、無農薬農法の一つ。とある鳥を田んぼに放ち、除草や害虫の駆除をさせる", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12517, "フォンデュ", "これ、何と言う？", 2, 5125, 12487, 5, "", "フォンデュ", 12531, 12457, 5, 5, "串に刺したパン・野菜・フルーツなどの食材を、チーズやチョコなどにくぐらせて食べる料理", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "バニラビーンズ", "これ、何と言う？", 2, 5126, 12499, 5, "バニラビーンズ", "バニラビン", 12521, 12491, 5, 5, "バニラの風味の素となる植物。バニラアイスなどをよく見ると含まれている黒い粒の正体", 5, 12496);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12525, "トゥンカロン", "これ、何と言う？", 2, 5127, 12459, 5, "", "トゥンカロ", 12531, 12453, 5, 5, "「太ったマカロン」という意味のお菓子。普通のマカロンよりサイズがひと回り大きい", 5, 12488);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12517, "フィッシュ&チップス", "これ、何と言う？", 2, 5128, 12471, 5, "", "フィッシュ", 12483, 12451, 5, 5, "白身魚のフライにポテトフライを添えた非常にポピュラーな料理", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12358, "とうにゅう", "これ、何と言う？", 2, 5129, 12421, 4, "", "とうにゅう", 12395, 12358, 4, 5, "大豆から作られる飲み物。大変体に良く、牛乳の代わりにする人も多い", 4, 12392);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12488, "チョコレート", "これ、何と言う？", 2, 5130, 12524, 5, "チョコレート", "チョコレト", 12467, 12519, 5, 5, "カカオ豆を原料とするポピュラーなお菓子。苦いものから甘いものまで種類はさまざま", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12517, "ノブレス・オブリージュ", "これ、何と言う？", 2, 5131, 12472, 5, "ノブレス・オブリージュ", "オブリジュ", 12522, 12502, 5, 5, "「高貴な身分は社会的義務を伴う」の意味を持つ、かつて貴族階級から生まれた言葉", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 2, 6, 33756, "緑黄色野菜", "これ、何と言う？", 2, 5132, 37326, 6, "", "緑黄色野菜", 33394, 40644, 6, 5, "ピーマンやカボチャなど、緑色や黄色の野菜の総称。定義上はカロテンを一定以上含む野菜を指す", 6, 32209);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12367, "はんじゅく卵", "これ、何と言う？", 2, 5133, 12421, 4, "", "はんじゅく", 12376, 12435, 4, 5, "ゆで卵の仲間で、黄身が完全に固まるまで火を通さず、あえてやわらかい状態を保った食べ物", 4, 12399);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12463, "シュンギク", "これ、何と言う？", 2, 5134, 12462, 5, "", "シュンギク", 12531, 12517, 5, 5, "鍋の具材としても人気の葉野菜。冬が旬の野菜だが、名前で「春の野菜」と誤解されることも", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12523, "バスタオル", "これ、何と言う？", 2, 5135, 12458, 5, "", "バスタオル", 12479, 12473, 5, 5, "大きなサイズのタオルで、文字通り風呂上りに身体を乾かすのに使う", 5, 12496);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12409, "ぼたんなべ", "これ、何と言う？", 2, 5136, 12394, 4, "", "ぼたんなべ", 12435, 12383, 4, 5, "イノシシの肉を使った鍋料理の、「猪鍋」以外の呼び方。縮れた肉がとある花に似ていることから", 4, 12412);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "ジンギスカン", "これ、何と言う？", 2, 5137, 12459, 5, "", "ンギスカン", 12473, 12462, 5, 5, "羊肉を使った料理で非常にポピュラーなもの。特殊な形の鉄板で、野菜と一緒に羊肉を焼く", 5, 12531);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12488, "メゾネット式", "これ、何と言う？", 2, 5138, 12483, 5, "", "メゾネット", 12493, 12478, 5, 5, "一般に、マンションやアパートでありながら、内部に階段があり2階構造になっている物件のこと", 5, 12513);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12523, "リバーシブル", "これ、何と言う？", 2, 5139, 12502, 5, "リバーシブル", "リバシブル", 12471, 12496, 5, 5, "裏返しでも着れるように作られている服のこと", 5, 12522);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12489, "アイランドキッチン", "これ、何と言う？", 2, 5140, 12531, 5, "", "アイランド", 12521, 12452, 5, 5, "普通の壁に向いた形のキッチンと異なり、島のように独立した形のキッチン", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 4, 12463, "海のミルク", "これ、何と言う？", 2, 5141, 12523, 6, "", "海のミルク", 12511, 12398, 5, 5, "「牡蠣」の別名。乳白色をしており、また栄養バランスの良さがとある飲み物を思わせることから", 5, 28023);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12358, "しんきゅう師", "これ、何と言う？", 2, 5142, 12421, 4, "", "しんきゅう", 12365, 12435, 4, 5, "「はり師」と「きゅう師」二つの資格を有する人の職業、何という？", 4, 12375);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12473, "タコライス", "これ、何と言う？", 2, 5143, 12452, 5, "", "タコライス", 12521, 12467, 5, 5, "「タコス」の具材を、生地で挟むのではなくご飯の上に乗せる形でアレンジした料理", 5, 12479);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12464, "ワーキングホリデー", "これ、何と言う？", 2, 5144, 12531, 5, "", "ワーキング", 12461, 12540, 5, 5, "若者が、他国で働いて資金を稼ぎながら、そこの文化に触れ長期休暇を過ごすという、公の制度", 5, 12527);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12458, "青椒肉絲＝チンジャオロース", "これ、何と読む？", 2, 5145, 12515, 5, "", "チンジャオ", 12472, 12531, 5, 5, "", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12464, "シーズニング", "これ、何と言う？", 2, 5146, 12531, 5, "シーズニング", "シズニング", 12491, 12474, 5, 5, "鉄のフライパンが錆びないように行う「空焼き」と「油ならし」の2工程を合わせてこう呼ぶ", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12525, "回鍋肉＝ホイコーロ", "これ、何と読む？", 2, 5147, 12540, 5, "", "ホイコーロ", 12467, 12452, 5, 5, "", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 4, 12462, "しらがネギ", "これ、何と言う？", 2, 5148, 12493, 4, "", "しらがネギ", 12364, 12425, 5, 5, "ネギの白い部分を、まるで髪の毛のようにごく細くせん切りにしたもの。和食の飾りに使われる", 4, 12375);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12516, "モロヘイヤ", "これ、何と言う？", 2, 5149, 12452, 5, "", "モロヘイヤ", 12504, 12525, 5, 5, "特別に栄養価が高いことから「野菜の王様」とも呼ばれる緑色の葉野菜", 5, 12514);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12540, "スムージー", "これ、何と言う？", 2, 5150, 12472, 5, "", "スムージー", 12540, 12512, 5, 5, "凍らせた果物や野菜などをミキサーにかけて作る飲み物で、朝食に好まれる", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "ナイトルーティン", "これ、何と言う？", 2, 5151, 12451, 5, "", "ルーティン", 12486, 12540, 5, 5, "毎日の夜に行う、自分ならではの習慣のこと。近年ではタレントが動画に撮って発信するのが人気", 5, 12523);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12472, "棒棒鶏＝バンバンジー", "これ、何と読む？", 2, 5152, 12531, 5, "", "バンバンジ", 12496, 12531, 5, 5, "蒸した鶏肉をごまだれで味付けた中華料理。キュウリを添えることが多い", 5, 12496);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12370, "いそべあげ", "これ、何と言う？", 2, 5153, 12354, 4, "", "いそべあげ", 12409, 12381, 4, 5, "天ぷらの一種。「青のり」を混ぜた衣で、ちくわなどの練り物を揚げた食べ物", 4, 12356);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5154, 4, 3, 4, 12374, "おにぎりざ", "このパネルの「プリコネ読み」は？", 1, 5154, 12426, 4, "", "おにぎりざ", 12366, 12395, 4, 5, "", 4, 12362);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "ダージリン", "これ、何と言う？", 2, 5155, 12522, 5, "", "ダージリン", 12472, 12540, 5, 5, "非常にポピュラーな紅茶の種類。「ウバ」「キーマン」に並び、「世界三大銘茶」と評される", 5, 12480);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12452, "アールグレイ", "これ、何と言う？", 2, 5156, 12524, 5, "アールグレイ", "アルグレイ", 12464, 12523, 5, 5, "ポピュラーな紅茶の一種で、お菓子作りの材料にも好まれる。伯爵という意味の言葉が含まれる", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12540, "ジンジャーエール", "これ、何と言う？", 2, 5157, 12515, 5, "", "ジンジャー", 12472, 12531, 5, 5, "非常にポピュラーな、炭酸飲料の一種。ショウガの風味が特徴的", 5, 12472);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 20418, "エンゲル係数", "これ、何と言う？", 2, 5158, 12523, 5, "", "エンゲル係", 12466, 12531, 6, 5, "家庭の支出額のうち、「食費」が占める割合のこと", 5, 12456);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 5, 12356, "プリンたい", "これ、何と言う？", 2, 5159, 12383, 5, "", "プリンたい", 12531, 12522, 4, 5, "一般にビールやレバー、エビ、牡蠣などに多く含まれる成分。あまりとり過ぎると痛風の原因に", 5, 12503);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12405, "きぬどうふ", "これ、何と言う？", 2, 5160, 12358, 4, "", "きぬどうふ", 12393, 12396, 4, 5, "豆腐の種類として特によく食べられる2種類。1つは「木綿豆腐」で、もう一つは？", 4, 12365);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12512, "インフォームド・コンセント", "これ、何と言う？", 2, 5161, 12457, 5, "インフォームド・コンセント", "インフォム", 12501, 12531, 5, 5, "患者が医師から十分な説明を受け、合意のうえで医療行為を受けること", 5, 12452);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12426, "だんしゃり", "これ、何と言う？", 2, 5162, 12419, 4, "", "だんしゃり", 12375, 12435, 4, 5, "元は「ヨガ」から生まれた習慣で、不要なものを捨て、物に執着せずに生きること", 4, 12384);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 5, 12435, "スギかふん", "これ、何と言う？", 2, 5163, 12405, 5, "", "スギかふん", 12363, 12462, 4, 5, "花粉症の原因として最も多いのは、どんな種類の花粉？", 4, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12473, "フレックスタイム制", "これ、何と言う？", 2, 5164, 12463, 5, "", "フレックス", 12483, 12524, 5, 5, "固定の時間帯で働く従来の働き方とは違い、始業と終業の時間を自由に決められる労働制度", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12435, "きゅうかん日", "これ、何と言う？", 2, 5165, 12363, 4, "", "きゅうかん", 12358, 12421, 4, 5, "アルコールをあまり毎日飲み過ぎず、肝臓を休める日を適度に設けることを推奨する言葉", 4, 12365);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12474, "マヨネーズ", "これ、何と言う？", 2, 5166, 12540, 5, "", "マヨネーズ", 12493, 12520, 5, 5, "卵・酢・油などから作られ、サラダなどにかけることが多い、定番の調味料", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12503, "ケチャップ", "これ、何と言う？", 2, 5167, 12483, 5, "", "ケチャップ", 12515, 12481, 5, 5, "主にトマトなどから作られる、定番の赤い調味料。オムライスやフライドポテトなどによく合う", 5, 12465);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "マーガリン", "これ、何と言う？", 2, 5168, 12522, 5, "", "マーガリン", 12460, 12540, 5, 5, "トーストなどに塗るのが定番の加工食品。バターの代替品として使われることも", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 3, 6, 37284, "砂糖・塩・酢・醤油・味噌", "これ、何と言う？", 2, 5169, 37218, 6, "砂糖・塩・酢・醤油・味噌", "砂糖塩酢醤", 22633, 31958, 6, 5, "料理の「さしすせそ」、順番に何と言う？", 6, 30722);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12356, "ごこくまい", "これ、何と言う？", 2, 5170, 12414, 4, "", "ごこくまい", 12367, 12371, 4, 5, "健康食品として知られるお米。米のほかに麦や粟など、合計5種類の穀物が合わさっている", 4, 12372);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12455, "ビュッフェ", "これ、何と言う？", 2, 5171, 12501, 5, "", "ビュッフェ", 12483, 12517, 5, 5, "ホテルなどで多い、セルフサービス形式での食事。「バイキング」と混同されることが少なくない", 5, 12499);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12417, "たかのつめ", "これ、何と言う？", 2, 5172, 12388, 4, "", "たかのつめ", 12398, 12363, 4, 5, "トウガラシの代表的な品種。名前は、とある鳥のかぎづめに似ていることから", 4, 12383);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12491, "タラバガニ", "これ、何と言う？", 2, 5173, 12460, 5, "", "タラバガニ", 12496, 12521, 5, 5, "高級食材としてよく知られるカニ。魚のタラが獲れる場所に生息していることが由来", 5, 12479);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12499, "サクラエビ", "これ、何と言う？", 2, 5174, 12456, 5, "", "サクラエビ", 12521, 12463, 5, 5, "食用として、かき揚げなどで好まれる小さなエビ。透き通ったピンク色の身体をしている", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12401, "さんきゅっぱ", "これ、何と言う？", 2, 5175, 12421, 4, "さんきゅっぱ", "さんきゅぱ", 12365, 12435, 4, 5, "売り物によく設定される「398円」という値段を読みやすくした読み方", 4, 12373);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12425, "かいばしら", "これ、何と言う？", 2, 5176, 12375, 4, "", "かいばしら", 12400, 12356, 4, 5, "ホタテなどの二枚貝で、円柱の形をした大きな可食部の通称", 4, 12363);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12488, "ファーストバイト", "これ、何と言う？", 2, 5177, 12473, 5, "", "ファースト", 12540, 12449, 5, 5, "結婚式で、ケーキ入刀のあとに新郎新婦がお互いにケーキを食べさせあう演出", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12375, "おいろなおし", "これ、何と言う？", 2, 5178, 12362, 4, "", "いろなおし", 12394, 12429, 4, 5, "結婚披露宴の途中で新郎新婦が離席し、衣装を変えること", 4, 12356);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 6, 12415, "剪定ばさみ", "これ、何と言う？", 2, 5179, 12373, 6, "", "剪定ばさみ", 12400, 23450, 4, 5, "庭木を切って整えるためのはさみで、普通のはさみなら切れない太い枝なども切ることができる", 4, 21098);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12464, "クーリング・オフ", "これ、何と言う？", 2, 5180, 12531, 5, "", "クーリング", 12522, 12540, 5, 5, "訪問販売などで考える暇なく購入してしまったものを、一定期間中ならキャンセルできる制度", 5, 12463);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12414, "ねこまんま", "これ、何と言う？", 2, 5181, 12435, 4, "", "ねこまんま", 12414, 12371, 4, 5, "「猫にあげるような飯」のこと。削り節をかけるか、味噌汁をかけるか、定義は地域で分かれる", 4, 12397);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 4, 12531, "おくション", "これ、何と言う？", 2, 5182, 12519, 4, "", "おくション", 12471, 12367, 5, 5, "分譲マンションで、価格が1億円を超えるような特に高級なものを指す俗語", 5, 12362);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12464, "パーキングエリア", "これ、何と言う？", 2, 5183, 12531, 5, "", "パーキング", 12461, 12540, 5, 5, "高速道路で、「SA」は「サービスエリア」。では「PA」は？", 5, 12497);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12519, "細マッチョ", "これ、何と言う？", 2, 5184, 12481, 6, "", "細マッチョ", 12483, 12510, 5, 5, "「ほどよく筋肉があり、脂肪は少なく引き締まった体型」を指す俗語。対義語は「ゴリマッチョ」", 5, 32048);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12464, "ハンバーグ", "これ、何と言う？", 2, 5185, 12540, 5, "", "ハンバーグ", 12496, 12531, 5, 5, "定番の肉料理。ひき肉をメインに、卵やパン粉、野菜などと一緒にこねて焼いたもの", 5, 12495);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12512, "ウェルカムボード", "これ、何と言う？", 2, 5186, 12459, 5, "", "ウェルカム", 12523, 12455, 5, 5, "結婚式やパーティなどで、ゲストを迎え入れるために入り口に置かれる案内板のこと", 5, 12454);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12365, "てっかまき", "これ、何と言う？", 2, 5187, 12414, 4, "", "てっかまき", 12363, 12387, 4, 5, "のり巻き寿司の一つで、マグロの赤身とわさびが巻かれたもの", 4, 12390);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12365, "かっぱまき", "これ、何と言う？", 2, 5188, 12414, 4, "", "かっぱまき", 12401, 12387, 4, 5, "のり巻き寿司の一つで、きゅうりが巻かれたもの", 4, 12363);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12435, "おやこどん", "これ、何と言う？", 2, 5189, 12393, 4, "", "おやこどん", 12371, 12420, 4, 5, "どんぶり物の料理の一つ。割下で甘く煮た鶏肉を、たまごでとじてご飯に乗せたもの", 4, 12362);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12375, "ちらしずし", "これ、何と言う？", 2, 5190, 12378, 4, "", "ちらしずし", 12375, 12425, 4, 5, "寿司の一種で、握ったり巻いたりしたものではなく、具材をご飯の上に散らすように盛るのが特徴", 4, 12385);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12400, "にはちそば", "これ、何と言う？", 2, 5191, 12381, 4, "", "にはちそば", 12385, 12399, 4, 5, "「そば」の分類の一つで、そば粉とうどん粉を２：８の配分で打ったそばのこと", 4, 12395);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "シュールストレミング", "これ、何と言う？", 2, 5192, 12511, 5, "", "ストレミン", 12524, 12488, 5, 5, "「世界一臭い缶詰め」として、良くも悪くも有名な食べ物。塩漬けのニシンを発酵させて作る", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 3, 6, 36784, "子・丑・寅・卯・辰・巳・午・未・申……", "これ、何と言う？", 2, 5193, 21359, 6, "子・丑・寅・卯・辰・巳・午・未・申……", "子丑寅卯辰", 23493, 19985, 6, 5, "「干支」を順番に並べると、最初にくる5つは何？", 6, 23376);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12415, "可燃ごみ⇔ふねんごみ", "これ、何と言う？", 2, 5194, 12372, 4, "", "ふねんごみ", 12435, 12397, 4, 5, "", 4, 12405);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "ベッドタウン", "これ、何と言う？", 2, 5195, 12454, 5, "ベッドタウン", "ベドタウン", 12479, 12489, 5, 5, "日中は大都市へ通勤し、夜だけ帰ってくるための場所として、その近郊に発展した街のこと", 5, 12505);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12450, "カーシェア", "これ、何と言う？", 2, 5196, 12455, 5, "", "カーシェア", 12471, 12540, 5, 5, "レンタカーに並び、近年急速に普及した貸し車サービス。短時間でも気軽に使えるのが長所", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12452, "焼売＝シュウマイ", "これ、何と読む？", 2, 5197, 12510, 5, "", "シュウマイ", 12454, 12517, 5, 5, "豚のひき肉を皮で包んで蒸した、中華料理の一つ", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "炒飯＝チャーハン", "この料理、何と読む？", 2, 5198, 12495, 5, "", "チャーハン", 12540, 12515, 5, 5, "", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12375, "うるうどし", "これ、何と言う？", 2, 5199, 12393, 4, "", "うるうどし", 12358, 12427, 4, 5, "太陽暦では実際の季節と暦とがわずかにズレるので、4年に一度、「366日」の年を設けること", 4, 12358);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12488, "バスソルト", "これ、何と言う？", 2, 5200, 12523, 5, "", "バスソルト", 12477, 12473, 5, 5, "入浴剤の一種で、ミネラルを多く含んだ塩系の成分を主として作られるものを特にこう呼ぶ", 5, 12496);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12423, "ばれいしょ", "これ、何と言う？", 2, 5201, 12375, 4, "", "ばれいしょ", 12356, 12428, 4, 5, "「じゃがいも」の別の呼び名。一般には馴染みが薄いが行政などではこちらが好まれることも", 4, 12400);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "オークション", "これ、何と言う？", 2, 5202, 12519, 5, "オークション", "オクション", 12471, 12463, 5, 5, "「競売」とも呼ばれる、物品の売買の方法の一つ。「入札」「落札」などの用語が使われる", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12540, "シェーバー", "これ、何と言う？", 2, 5203, 12496, 5, "", "シェーバー", 12540, 12455, 5, 5, "本来はカミソリ全般を指す言葉だが、一般には特に電動のひげ剃り機を指して使うことが多い", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12503, "マネークリップ", "これ、何と言う？", 2, 5204, 12522, 5, "マネークリップ", "マネクリプ", 12463, 12493, 5, 5, "紙幣やカードなどを挟んで持ち歩く道具で、財布と比べ気軽に携帯できるため使う人が増えている", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 4, 12525, "ちゅうトロ", "これ、何と言う？", 2, 5205, 12488, 4, "", "ちゅうトロ", 12358, 12421, 5, 5, "お寿司のネタとしてよく知られるマグロの部位。脂のノリがほどよく、テッパンの人気を誇る", 4, 12385);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12491, "バフンウニ", "これ、何と言う？", 2, 5206, 12454, 5, "", "バフンウニ", 12531, 12501, 5, 5, "ウニの一種。食べものとしては抵抗のある名前だが、非常に美味と言われる", 5, 12496);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12540, "サイドカー", "これ、何と言う？", 2, 5207, 12459, 5, "", "サイドカー", 12489, 12452, 5, 5, "オートバイの横に取り付ける自走しない車で、人を乗せることも可能", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6020, 5, 3, 5, 12489, "リッチモンド商工会", "これ、何と言う？", 1, 5209, 12531, 5, "リッチモンド商工会", "リチモンド", 12514, 12481, 5, 5, "『クレジッタ』の所属するギルドの名前は？", 5, 12522);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12385, "かがみもち", "これ、何と言う？", 2, 5211, 12418, 4, "", "かがみもち", 12415, 12364, 4, 5, "お正月に飾られる、ついたお餅を重ねたもの", 4, 12363);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 1, 6, 12365, "お好み焼き", "これ、何と言う？", 2, 5212, 28988, 4, "", "お好み焼き", 12415, 22909, 4, 5, "鉄板焼きの一種。由来は諸説あるが、具や焼き具合を、好みの通りにして味わったことなど", 4, 12362);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 21365, "だし巻き卵", "これ、何と言う？", 2, 5213, 12365, 4, "", "だし巻き卵", 24059, 12375, 6, 5, "卵焼きの一種で、だし汁をたっぷり含ませて焼いたものをこう呼ぶ", 6, 12384);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5214, 5, 2, 5, 12523, "みんなのゲームテーブル", "これ、何と言う？", 1, 5214, 12502, 5, "みんなのゲームテーブル", "ゲムテブル", 12486, 12512, 5, 5, "ミニゲームを遊べるギルドハウス家具", 5, 12466);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5215, 4, 3, 5, 26426, "カリンの机", "これ、何と言う？", 1, 5215, 12398, 5, "", "カリンの机", 12531, 12522, 6, 5, "『カリン』がスキップチケットを作ってくれるギルドハウス家具", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5216, 4, 3, 6, 12435, "至高のドラゴンテイルおでん", "これ、何と言う？", 1, 5216, 12391, 6, "至高のドラゴンテイルおでん", "至高おでん", 12362, 39640, 4, 5, "『緑竜の骸嶺』を登頂した証として美食殿一行が手にした料理、兼ギルドハウス家具", 4, 33267);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (5217, 6, 3, 6, 32905, "雲海の魔物肉", "これ、何と言う？", 1, 5217, 29289, 6, "雲海の魔物肉", "雲海魔物肉", 39764, 28023, 6, 5, "『雲海の山脈』を登頂した証として美食殿一行が手にした料理、兼ギルドハウス家具", 6, 38642);
