INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12429, "泣きぼくろ", "これ、何と言う？", 2, 6065, 12367, 6, "", "泣きぼくろ", 12412, 12365, 4, 6, "目のすぐ下のあたりにあるほくろの通称", 4, 27875);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12463, "シルバーバック", "これ、何と言う？", 2, 6066, 12496, 5, "シルバーバック", "シルババク", 12496, 12523, 5, 6, "年を重ねたオスのゴリラの背中の毛が白くなる現象。群れの中のボス個体によく見られる", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "カメレオン", "これ、何と言う？", 2, 6067, 12458, 5, "", "カメレオン", 12524, 12513, 5, 6, "身体の色を自在に変化させることでおなじみの爬虫類。演技の幅が広い俳優や声優のたとえにも", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12510, "オニヤンマ", "これ、何と言う？", 2, 6068, 12531, 5, "", "オニヤンマ", 12516, 12491, 5, 6, "非常に大きなトンボの一種。「鬼」の服装とよく似た、黒と黄色のシマシマ模様が特徴的", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12464, "グルーミング", "これ、何と言う？", 2, 6069, 12531, 5, "グルーミング", "グルミング", 12511, 12523, 5, 6, "猫などの動物の習性として知られる「毛づくろい」。英語でなんと言う？", 5, 12464);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "マングース", "これ、何と言う？", 2, 6070, 12540, 5, "", "マングース", 12464, 12531, 5, 6, "一般に「ハブ」の天敵というイメージを持たれている、体長数十センチほどの哺乳類", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12510, "ギンヤンマ", "これ、何と言う？", 2, 6071, 12531, 5, "", "ギンヤンマ", 12516, 12531, 5, 6, "トンボの一種。身体は黄緑色だが、よく見るとキレイな銀色の模様があることからこう呼ばれる", 5, 12462);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12523, "カモミール", "これ、何と言う？", 2, 6072, 12540, 5, "", "カモミール", 12511, 12514, 5, 6, "「カミツレ」とも呼ばれるハーブの一種。りんごのような香りがあり、お茶としてよく好まれる", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12459, "ホタルイカ", "これ、何と言う？", 2, 6073, 12452, 5, "", "ホタルイカ", 12523, 12479, 5, 6, "指先ほどのサイズの小さなイカ。青白く発光する性質があり、また食用としても好まれる", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12452, "キンメダイ", "これ、何と言う？", 2, 6074, 12480, 5, "", "キンメダイ", 12513, 12531, 5, 6, "高級魚や縁起物としておなじみのタイの一種。名前は、目が金色に輝くことから", 5, 12461);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12471, "ダイオウグソクムシ", "これ、何と言う？", 2, 6075, 12512, 5, "", "グソクムシ", 12463, 12477, 5, 6, "深海に生息する甲殻類。姿はダンゴムシに似ていて、大きなものは体長50cmを超える", 5, 12464);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12468, "チンアナゴ", "これ、何と言う？", 2, 6076, 12490, 5, "", "チンアナゴ", 12450, 12531, 5, 6, "水族館でも人気なアナゴの一種。砂底から身体を出し、群れで同じ方向をじっと向く姿が特徴的", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12450, "ラフレシア", "これ、何と言う？", 2, 6077, 12471, 5, "", "ラフレシア", 12524, 12501, 5, 6, "「世界最大の花」としておなじみの、熱帯地域に生息する花。強い悪臭を放つことでも有名", 5, 12521);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12464, "ベニテングタケ", "これ、何と言う？", 2, 6078, 12531, 5, "", "ベニテング", 12486, 12491, 5, 6, "真っ赤なカサに白い点のある、キノコの一種。毒があり、食べると幻覚・腹痛などの症状が出る", 5, 12505);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12463, "コノハズク", "これ、何と言う？", 2, 6079, 12474, 5, "", "コノハズク", 12495, 12494, 5, 6, "「フクロウ」の一種。身の危険を感じたときに身体を細く縮めて、木の枝に擬態する習性がある", 5, 12467);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12454, "サンショウウオ", "これ、何と言う？", 2, 6080, 12519, 5, "", "サンショウ", 12471, 12531, 5, 6, "両生類の一種。防衛のために出す粘液の匂いが、和食に使うとあるスパイスに似ているのが由来", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12454, "フキノトウ", "これ、何と言う？", 2, 6081, 12488, 5, "", "フキノトウ", 12494, 12461, 5, 6, "「蕗（フキ）」が大きく成長する前の、つぼみの部分。春の山菜として、天ぷらなどで好まれる", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12499, "サトウキビ", "これ、何と言う？", 2, 6082, 12461, 5, "", "サトウキビ", 12454, 12488, 5, 6, "砂糖の原料となる、イネ科の植物", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12358, "ふしちょう", "これ、何と言う？", 2, 6083, 12423, 4, "", "ふしちょう", 12385, 12375, 4, 6, "フェニックスとも呼ばれる、死しても再びよみがえるとされる伝説の鳥", 4, 12405);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12367, "きゅうかく", "これ、何と言う？", 2, 6084, 12363, 4, "", "きゅうかく", 12358, 12421, 4, 6, "「五感」と呼ばれる動物の機能、視覚・聴覚・触覚・味覚と、あと一つは？", 4, 12365);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12464, "ドラミング", "これ、何と言う？", 2, 6085, 12531, 5, "", "ドラミング", 12511, 12521, 5, 6, "ゴリラが手で胸をドコドコと叩いて音をたてる行動", 5, 12489);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12522, "ハムストリング", "これ、何と言う？", 2, 6086, 12488, 5, "", "ハムストリ", 12473, 12512, 5, 6, "太ももの裏にある、大きな筋肉の集まり。筋トレでも重要視されることが多い", 5, 12495);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12367, "ざっしょく", "これ、何と言う？", 2, 6087, 12423, 4, "", "ざっしょく", 12375, 12387, 4, 6, "肉を食べるのは肉食。植物を食べるのは草食。では、どちらかに限らずいろいろ食べるのは？", 4, 12374);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12358, "乳歯⇔えいきゅう歯", "これ、何と言う？", 2, 6088, 12421, 4, "", "えいきゅう", 12365, 12356, 4, 6, "", 4, 12360);
