INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "ケルベロス", "これ、何と言う？", 2, 6090, 12525, 5, "", "ケルベロス", 12505, 12523, 5, 6, "「地獄の番犬」と呼ばれる、三つ首の怪物", 5, 12465);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12473, "オルトロス", "これ、何と言う？", 2, 6091, 12525, 5, "", "オルトロス", 12488, 12523, 5, 6, "有名な三つ首の犬の怪物は「ケルベロス」。ではその兄弟で、二つ首の怪物は？", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12415, "つくもがみ", "これ、何と言う？", 2, 6092, 12364, 4, "", "つくもがみ", 12418, 12367, 4, 6, "漢字では「付喪神」と書く、長い年月が経った道具などに宿るとされる神様", 4, 12388);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12358, "のっぺらぼう", "これ、何と言う？", 2, 6093, 12412, 4, "のっぺらぼう", "のぺらぼう", 12425, 12410, 4, 6, "とても有名な妖怪。目も鼻も口も無いまっさらな顔をしている", 4, 12398);
