INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12356, "左ヒラメ　みぎかれい", "これ、何と言う？", 2, 6098, 12428, 4, "", "みぎかれい", 12363, 12366, 4, 6, "「ヒラメ」とよく似たとある魚の見分け方として、よく使われるフレーズ。目の向きがポイント", 4, 12415);
