INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12429, "弁慶のなきどころ", "これ、何と言う？", 2, 6122, 12371, 4, "", "なきどころ", 12393, 12365, 4, 6, "向こうずねのこと。転じて、屈強な人間であってもこの部位は痛みに弱いことから、急所のこと", 4, 12394);
