INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12515, "オタマジャクシ", "これ、何と言う？", 2, 6134, 12472, 5, "", "オタマジャ", 12510, 12479, 5, 6, "成長すると「カエル」になる生き物", 5, 12458);
