INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12494, "ナマケモノ", "これ、何と言う？", 2, 6146, 12514, 5, "", "ナマケモノ", 12465, 12510, 5, 6, "哺乳類の一種。その命名の通り、めったに活動せず、動くときも非常にゆっくりと動く", 5, 12490);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12513, "チョウザメ", "これ、何？", 2, 6147, 12470, 5, "", "チョウザメ", 12454, 12519, 5, 6, "世界三大珍味の一つ「キャビア」は何と言う生き物の卵？", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6151, 5, 3, 5, 12540, "魔法少女ピュアリー★シオリ", "このキャラの名前は？", 1, 6151, 12522, 5, "", "ピュアリー", 12450, 12517, 5, 6, "", 5, 12500);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6153, 5, 3, 5, 12540, "魔法少女シャイニー★トモ", "このキャラの名前は？", 1, 6153, 12491, 5, "", "シャイニー", 12452, 12515, 5, 6, "", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "ホモ・サピエンス", "これ、何と言う？", 2, 6155, 12531, 5, "", "サピエンス", 12456, 12500, 5, 6, "「賢い人間」を意味する、ヒト属の種の学名。現生人類もこれに属する", 5, 12469);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "グリフォン", "これ、何と言う？", 2, 6157, 12457, 5, "", "グリフォン", 12501, 12522, 5, 6, "鷲（ワシ）の翼と前脚と頭、そしてライオンの胴と後ろ脚を合わせ持つ、伝説の生き物", 5, 12464);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12403, "ろくろくび", "これ、何と言う？", 2, 6160, 12367, 4, "", "ろくろくび", 12429, 12367, 4, 6, "首が長く伸びる妖怪。普通にしている分には、人間と変わらない姿をしている", 4, 12429);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12385, "かまいたち", "これ、何と言う？", 2, 6161, 12383, 4, "", "かまいたち", 12356, 12414, 4, 6, "つむじ風に乗って現れ、人を切りつける妖怪。痛みは無く、血も出ず、傷だけが残ると言われる", 4, 12363);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12523, "フェンリル", "これ、何と言う？", 2, 6162, 12522, 5, "", "フェンリル", 12531, 12455, 5, 6, "神話に登場する巨大な狼。ラグナロクではオーディンを丸呑みにしたとされる", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (1255, 4, 2, 4, 12377, "やばいですね☆", "これ、何と言う？", 1, 6164, 12391, 4, "", "やばいです", 12356, 12400, 4, 2, "ペコリーヌの口癖の一つ。良い意味でも悪い意味でもヤバイことが起きたときに言うことが多い", 4, 12420);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6165, 4, 2, 4, 12414, "あるじさま", "左のキャラは右のキャラを何と呼ぶ？", 1, 6165, 12373, 4, "", "あるじさま", 12376, 12427, 4, 6, "", 4, 12354);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6166, 5, 2, 5, 12493, "クソメガネ", "左のキャラは右のキャラを何と呼ぶ？", 1, 6166, 12460, 5, "", "クソメガネ", 12513, 12477, 5, 6, "", 5, 12463);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "フレーメン反応", "これ、何と言う？", 2, 6167, 12513, 5, "", "フレーメン", 12540, 12524, 5, 6, "猫などの動物が強い匂いを嗅いだときに、目や口を開いて独特な表情をする現象", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "メガロドン", "これ、何と言う？", 2, 6168, 12489, 5, "", "メガロドン", 12525, 12460, 5, 6, "太古の海に存在していた、史上最大のサメ", 5, 12513);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12400, "いときりば", "これ、何と言う？", 2, 6169, 12426, 4, "", "いときりば", 12365, 12392, 4, 6, "「犬歯」の通称として、「八重歯」以外になじみ深いもの。糸が切れるほど尖っているのが由来", 4, 12356);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "カーネーション", "これ、何と言う？", 2, 6170, 12519, 5, "カーネーション", "カネション", 12471, 12493, 5, 6, "「母の日」の贈り物としても親しまれる花。赤やピンクの鮮やかな花びらを持つ", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12494, "ソメイヨシノ", "これ、何と言う？", 2, 6171, 12471, 5, "", "メイヨシノ", 12520, 12452, 5, 6, "桜の木の種類として非常にポピュラーな一つ。一般に桜のイメージとして最も浸透している", 5, 12513);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "ダンデライオン", "これ、何と言う？", 2, 6172, 12458, 5, "", "デライオン", 12452, 12521, 5, 6, "「タンポポ」の英名。とある哺乳類の動物に由来している", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12358, "せいりゅう", "これ、何と言う？", 2, 6173, 12421, 4, "", "せいりゅう", 12426, 12356, 4, 6, "「四聖獣」と呼ばれる生き物、朱雀・白虎・玄武、あとは何？（ひらがなで）", 4, 12379);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 6, 12375, "座敷わらし", "これ、何と言う？", 2, 6174, 12425, 6, "", "座敷わらし", 12431, 25975, 4, 6, "小さな子どもの姿で、座敷や土間に現れる妖怪。遭遇した者には幸運が舞い込むとされる", 4, 24231);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "マンチカン", "これ、何と言う？", 2, 6175, 12459, 5, "", "マンチカン", 12481, 12531, 5, 6, "猫の種類の一つ。「小さい」「子ども」という意味を持ち、小柄で脚が短いのが特徴", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "デュラハン", "これ、何と言う？", 2, 6176, 12495, 5, "", "デュラハン", 12521, 12517, 5, 6, "伝承に登場する、首の無い騎士", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12452, "オッドアイ", "これ、何と言う？", 2, 6177, 12450, 5, "", "オッドアイ", 12489, 12483, 5, 6, "両目でそれぞれ瞳の色が異なること。いろいろな動物に見られるが、特に猫に多い", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12375, "猫じゃらし", "これ、何と言う？", 2, 6178, 12425, 6, "", "猫じゃらし", 12419, 12376, 4, 6, "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", 4, 29483);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12521, "ドラキュラ", "これ、何と言う？", 2, 6179, 12517, 5, "", "ドラキュラ", 12461, 12521, 5, 6, "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", 5, 12489);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12471, "フンコロガシ", "これ、何と言う？", 2, 6180, 12460, 5, "", "ンコロガシ", 12525, 12467, 5, 6, "動物のフンをボール状にして、コロコロ転がすという変わった習性を持つ昆虫", 5, 12531);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12523, "ラグドール", "これ、何と言う？", 2, 6181, 12540, 5, "", "ラグドール", 12489, 12464, 5, 6, "「ぬいぐるみ」という意味を持つ、猫の種類。抱っこが好きで、毛並みがフサフサなことから", 5, 12521);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12463, "エキゾチック・ショートヘア", "これ、何と言う？", 2, 6182, 12481, 5, "エキゾチック・ショートヘア", "エキゾチク", 12478, 12461, 5, 6, "猫の種類の一つ。鼻が低く、ムスッとしたような愛嬌のある顔立ちが特徴的", 5, 12456);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12392, "でんしょばと", "これ、何と言う？", 2, 6183, 12400, 4, "", "んしょばと", 12423, 12375, 4, 6, "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", 4, 12435);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12519, "ホーホケキョ", "これ、何と言う？", 2, 6184, 12461, 5, "ホーホケキョ", "ホホケキョ", 12465, 12507, 5, 6, "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "ホトトギス", "これ、何と言う？", 2, 6185, 12462, 5, "", "ホトトギス", 12488, 12488, 5, 6, "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12468, "ヘビイチゴ", "これ、何と言う？", 2, 6186, 12481, 5, "", "ヘビイチゴ", 12452, 12499, 5, 6, "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", 5, 12504);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12426, "松ぼっくり", "これ、何と言う？", 2, 6187, 12367, 6, "", "松ぼっくり", 12387, 12412, 4, 6, "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", 4, 26494);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6188, 5, 1, 5, 12489, "アゾールド", "このキャラの名前は？", 1, 6188, 12523, 5, "", "アゾールド", 12540, 12478, 5, 6, "", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6189, 4, 3, 5, 12416, "ズナちょむ", "左のキャラは右のキャラを何と呼ぶ？", 1, 6189, 12423, 5, "", "ズナちょむ", 12385, 12490, 4, 6, "", 4, 12474);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12489, "スコティッシュフォールド", "これ、何と言う？", 2, 6190, 12523, 5, "", "フォールド", 12540, 12457, 5, 6, "猫の種類の一つ。垂れた耳が特徴的", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "ゴールデンレトリーバー", "これ、何と言う？", 2, 6191, 12487, 5, "", "ゴールデン", 12523, 12540, 5, 6, "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", 5, 12468);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "ドッグラン", "これ、何と言う？", 2, 6192, 12521, 5, "", "ドッグラン", 12464, 12483, 5, 6, "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", 5, 12489);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12523, "ラブラドールレトリーバー", "これ、何と言う？", 2, 6193, 12489, 5, "ラブラドールレトリーバー", "ラブラドル", 12521, 12502, 5, 6, "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", 5, 12521);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12464, "マーキング", "これ、何と言う？", 2, 6194, 12531, 5, "", "マーキング", 12461, 12540, 5, 6, "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 2, 4, 12385, "たいこ持ち", "これ、何と言う？", 2, 6195, 25345, 4, "", "たいこ持ち", 12371, 12356, 4, 6, "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", 4, 12383);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12473, "キリギリス", "これ、何と言う？", 2, 6196, 12522, 5, "", "キリギリス", 12462, 12522, 5, 6, "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", 5, 12461);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12499, "クルマエビ", "これ、何と言う？", 2, 6197, 12456, 5, "", "クルマエビ", 12510, 12523, 5, 6, "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", 5, 12463);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12454, "アオダイショウ", "これ、何と言う？", 2, 6198, 12519, 5, "", "ダイショウ", 12471, 12452, 5, 6, "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", 5, 12480);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12358, "しそちょう", "これ、何と言う？", 2, 6199, 12423, 4, "", "しそちょう", 12385, 12381, 4, 6, "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", 4, 12375);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12521, "アウストラロピテクス", "これ、何と言う？", 2, 6200, 12488, 5, "", "アウストラ", 12473, 12454, 5, 6, "「最古の人類」とされている種類の猿人", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "キュウカンチョウ", "これ、何と言う？", 2, 6201, 12459, 5, "", "キュウカン", 12454, 12517, 5, 6, "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", 5, 12461);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12452, "オットセイ", "これ、何と言う？", 2, 6202, 12475, 5, "", "オットセイ", 12488, 12483, 5, 6, "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12454, "アジアゾウ", "これ、何と言う？", 2, 6203, 12478, 5, "", "アジアゾウ", 12450, 12472, 5, 6, "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12511, "ベニシジミ", "これ、何と言う？", 2, 6204, 12472, 5, "", "ベニシジミ", 12471, 12491, 5, 6, "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", 5, 12505);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12531, "ジュウサンホシテントウ", "これ、何と言う？", 2, 6205, 12469, 5, "", "ジュウサン", 12454, 12517, 5, 6, "テントウムシの一種。身体に13個の点々模様がある", 5, 12472);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12516, "ニジュウヤホシテントウ", "これ、何と言う？", 2, 6206, 12454, 5, "", "ニジュウヤ", 12517, 12472, 5, 6, "テントウムシの一種。身体に28個の点々模様がある", 5, 12491);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12473, "アニサキス", "これ、何と言う？", 2, 6207, 12461, 5, "", "アニサキス", 12469, 12491, 5, 6, "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12426, "わたりどり", "これ、何と言う？", 2, 6208, 12393, 4, "", "わたりどり", 12426, 12383, 4, 6, "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", 4, 12431);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12488, "フンボルトペンギン", "これ、何と言う？", 2, 6209, 12523, 5, "", "フンボルト", 12508, 12531, 5, 6, "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12522, "エボシドリ", "これ、何と言う？", 2, 6210, 12489, 5, "", "エボシドリ", 12471, 12508, 5, 6, "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", 5, 12456);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12489, "ネザーランドドワーフ", "これ、何と言う？", 2, 6211, 12531, 5, "ネザーランドドワーフ", "ネザランド", 12521, 12470, 5, 6, "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", 5, 12493);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12489, "ホーランドロップ", "これ、何と言う？", 2, 6212, 12531, 5, "", "ホーランド", 12521, 12540, 5, 6, "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", 5, 12507);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12488, "フェレット", "これ、何と言う？", 2, 6213, 12483, 5, "", "フェレット", 12524, 12455, 5, 6, "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", 5, 12501);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12498, "マントヒヒ", "これ、何と言う？", 2, 6214, 12498, 5, "", "マントヒヒ", 12488, 12531, 5, 6, "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12473, "シロナガスクジラ", "これ、何と言う？", 2, 6215, 12460, 5, "", "シロナガス", 12490, 12525, 5, 6, "最も大きい動物としてよく知られる種類のクジラ", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12459, "シロイルカ", "これ、何と言う？", 2, 6216, 12523, 5, "", "シロイルカ", 12452, 12525, 5, 6, "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", 5, 12471);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12366, "いなばのしろうさぎ", "これ、何と言う？", 2, 6217, 12373, 4, "", "しろうさぎ", 12358, 12429, 4, 6, "神話に登場する、有名な動物", 4, 12375);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12531, "さそり＝スコーピオン", "これ、何と言う？", 2, 6218, 12458, 5, "さそり＝スコーピオン", "スコピオン", 12500, 12467, 5, 6, "カタカナでの呼び方は？", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12406, "ちからこぶ", "これ、何と言う？", 2, 6219, 12371, 4, "", "ちからこぶ", 12425, 12363, 4, 6, "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", 4, 12385);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 21512, "さるかに合戦", "これ、何と言う？", 2, 6220, 12395, 4, "", "さるかに合", 12363, 12427, 6, 6, "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", 4, 12373);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12472, "♪サル、ゴリラ、チンパンジー", "これ、何と言う？", 2, 6221, 12531, 5, "", "チンパンジ", 12497, 12531, 5, 6, "よく親しまれた替え歌の歌詞。ある動物の名前が入る", 5, 12481);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12513, "アオイソメ", "これ、何と言う？", 2, 6222, 12477, 5, "", "アオイソメ", 12452, 12458, 5, 6, "釣り人には釣り餌として非常におなじみの、ミミズのような虫", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12523, "鋼メンタル", "これ、何と言う？", 2, 6223, 12479, 6, "", "鋼メンタル", 12531, 12513, 5, 6, "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", 5, 37628);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6224, 5, 3, 5, 12488, "マゾヒスト", "これ、何と言う？", 1, 6224, 12473, 5, "", "マゾヒスト", 12498, 12478, 5, 6, "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", 5, 12510);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12473, "ケツァルコアトルス", "これ、何と言う？", 2, 6225, 12523, 5, "", "コアトルス", 12488, 12450, 5, 6, "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", 5, 12467);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12513, "アナツバメ", "これ、何と言う？", 2, 6226, 12496, 5, "", "アナツバメ", 12484, 12490, 5, 6, "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12454, "タンチョウヅル", "これ、何と言う？", 2, 6227, 12519, 5, "", "タンチョウ", 12481, 12531, 5, 6, "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", 5, 12479);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12495, "ルリタテハ", "これ、何と言う？", 2, 6228, 12486, 5, "", "ルリタテハ", 12479, 12522, 5, 6, "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", 5, 12523);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6229, 5, 1, 5, 12473, "プリンセスナイト", "これ、何と言う？", 1, 6229, 12475, 5, "", "プリンセス", 12531, 12522, 5, 6, "特定の相手の能力\nを高める権能を持\nった存在", 5, 12503);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6230, 4, 3, 5, 12417, "ネコむすめ", "このパネルの「プリコネ読み」は？", 1, 6230, 12377, 5, "", "ネコむすめ", 12416, 12467, 4, 6, "", 4, 12493);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 6, 2, 6, 31278, "絶滅危惧種", "これ、何と言う？", 2, 6231, 24807, 6, "", "絶滅危惧種", 21361, 28357, 6, 6, "数が減少し、絶滅の危機に晒されている生物", 6, 32118);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12511, "アブラゼミ", "これ、何と言う？", 2, 6232, 12476, 5, "", "アブラゼミ", 12521, 12502, 5, 6, "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 33137, "アキレス腱", "これ、何と言う？", 2, 6233, 12473, 5, "", "アキレス腱", 12524, 12461, 6, 6, "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6234, 5, 1, 5, 12488, "ナルシスト", "これ、何と言う？", 1, 6234, 12473, 5, "", "ナルシスト", 12471, 12523, 5, 6, "自己愛が非常に強く、自分に陶酔した人、英語にすると？", 5, 12490);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6235, 4, 3, 4, 12358, "だんちょう", "左のキャラは右のキャラを何と呼ぶ？", 1, 6235, 12423, 4, "", "だんちょう", 12385, 12435, 4, 6, "", 4, 12384);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12369, "許嫁＝いいなずけ", "これ、何と読む？", 2, 6236, 12378, 4, "", "いいなずけ", 12394, 12356, 4, 6, "若いうちから結婚が約束された相手のこと", 4, 12356);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 3, 4, 12377, "やたがらす", "これ、何と言う？", 2, 6237, 12425, 4, "", "やたがらす", 12364, 12383, 4, 6, "「古事記」などに登場する、三本足の神聖なカラス", 4, 12420);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 2, 4, 12358, "さごじょう", "これ、何と言う？", 2, 6238, 12423, 4, "", "さごじょう", 12376, 12372, 4, 6, "「西遊記」で孫悟空と行動を共にする、河童の姿のキャラクター", 4, 12373);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12540, "ディーラー", "これ、何と言う？", 2, 6239, 12521, 5, "", "ディーラー", 12540, 12451, 5, 6, "カジノで、ルーレットを回したりカードを配ったりとゲームの進行を行う役目", 5, 12487);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12473, "エンプレス", "これ、何と言う？", 2, 6240, 12524, 5, "", "エンプレス", 12503, 12531, 5, 6, "「皇帝」は「エンペラー」。では「女帝」は？", 5, 12456);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12473, "ガンスミス", "これ、何と言う？", 2, 6241, 12511, 5, "", "ガンスミス", 12473, 12531, 5, 6, "銃のメンテナンスを職業として行う人のこと", 5, 12460);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12459, "ハイビスカス", "これ、何と言う？", 1, 6242, 12473, 5, "", "ハイビスカ", 12499, 12452, 5, 6, "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", 5, 12495);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12503, "ウィル・オ・ウィスプ", "これ、何と言う？", 2, 6243, 12473, 5, "ウィル・オ・ウィスプ", "オウィスプ", 12451, 12454, 5, 6, "「ひとだま」と呼ばれる超常現象、英語で言うと？", 5, 12458);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12452, "ニシキゴイ", "これ、何と言う？", 2, 6244, 12468, 5, "", "ニシキゴイ", 12461, 12471, 5, 6, "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", 5, 12491);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12480, "レッサーパンダ", "これ、何と言う？", 2, 6245, 12531, 5, "レッサーパンダ", "レサパンダ", 12497, 12469, 5, 6, "「小さなパンダ」が由来の、動物園の人気者", 5, 12524);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12480, "アナコンダ", "これ、何と言う？", 2, 6246, 12531, 5, "", "アナコンダ", 12467, 12490, 5, 6, "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", 5, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12517, "タランチュラ", "これ、何と言う？", 2, 6247, 12481, 5, "", "タランチュ", 12531, 12521, 5, 6, "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", 5, 12479);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12454, "スイギュウ", "これ、何と言う？", 2, 6248, 12517, 5, "", "スイギュウ", 12462, 12452, 5, 6, "「牛」の一種で、地域によって家畜としてもなじみ深い動物。その名の通り、水辺を好む", 5, 12473);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12540, "ハムスター", "これ、何と言う？", 2, 6249, 12479, 5, "", "ハムスター", 12473, 12512, 5, 6, "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", 5, 12495);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 1, 5, 12511, "ハリネズミ", "これ、何と言う？", 2, 6250, 12474, 5, "", "ハリネズミ", 12493, 12522, 5, 6, "身を守るためのトゲトゲの体毛が非常に特徴的な動物。愛好家も多く、英名は「ヘッジホッグ」", 5, 12495);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 3, 5, 12531, "ヒョウモンダコ", "これ、何と言う？", 2, 6251, 12514, 5, "", "ヒョウモン", 12454, 12519, 5, 6, "猛毒を持つタコ。蛍光色のヒョウ柄の模様が、恐ろしさを助長している", 5, 12498);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6252, 5, 3, 5, 12531, "カメレオン", "これ、何と言う？", 1, 6252, 12458, 5, "", "カメレオン", 12524, 12513, 5, 6, "ネネカといつも一緒にいる動物", 5, 12459);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6048, 4, 3, 5, 12435, "アイちゃん", "これ、何と言う？", 1, 6253, 12419, 5, "", "アイちゃん", 12385, 12452, 4, 6, "『ミサキ』がいつも一緒にいる、生きた杖の名前", 4, 12450);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 4, 1, 4, 12358, "にくきゅう", "これ、何と言う？", 2, 6254, 12421, 4, "", "にくきゅう", 12365, 12367, 4, 6, "ネコ目の動物の足にある、ぷにぷにとやわらかい部分の通称", 4, 12395);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (6255, 6, 3, 6, 28304, "諸悪の根源", "初対面のとき何と呼んだ？", 1, 6255, 26681, 6, "", "諸悪の根源", 12398, 24746, 6, 6, "", 4, 35576);
INSERT INTO `v1_0580844199d234afe4f29cffd7a9eea884397b767e09b7ddb7be5bb783ade66d` VALUES (0, 5, 2, 5, 12512, "魚⇒アクアリウム　陸上生物⇒テラリウム", "これ、何と言う？", 2, 6256, 12454, 5, "", "テラリウム", 12522, 12521, 5, 6, "", 5, 12486);
