INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (160, 1.5, 1, 1, 105, 1, 110, 122601, 0, 0, 1, 101, 0.029, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (120, 2.5, 3, 2, 111, 0, 105, 122401, 0, 1, 2, 102, 0.014, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (350, 2.5, 2, 3, 105, 0, 110, 122501, 1, 0, 3, 103, 0.017, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 1.5, 3, 4, 105, 0, 110, 107501, 0, 1, 4, 104, 0.016, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (160, 2.5, 1, 5, 105, 1, 110, 107601, 0, 0, 5, 105, 0.014, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (255, 3.5, 2, 6, 103, 0, 120, 107801, 1, 0, 6, 106, 0.029, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (160, 3.5, 1, 7, 103, 1, 120, 110601, 0, 0, 7, 107, 0.013, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (350, 2.5, 2, 8, 103, 0, 110, 110401, 1, 0, 8, 108, 0.014, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 1.5, 3, 9, 100, 0, 105, 110501, 0, 1, 9, 109, 0.012, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (200, 2.5, 1, 10, 103, 1, 105, 115701, 0, 0, 10, 110, 0.014, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (350, 2.5, 2, 11, 103, 0, 105, 113601, 1, 0, 11, 111, 0.071, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 1.5, 3, 12, 103, 0, 120, 107901, 0, 1, 12, 112, 0.014, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (200, 1.5, 2, 13, 105, 1, 110, 108001, 0, 0, 13, 113, 0.022, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (160, 2.5, 1, 14, 108, 1, 110, 110301, 0, 0, 14, 114, 0.031, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 0.5, 1, 15, 105, 0, 110, 107701, 0, 1, 15, 115, 0.036, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (350, 3.5, 2, 16, 95, 0, 110, 113101, 1, 0, 16, 116, 0.048, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 1.5, 3, 17, 105, 0, 105, 113301, 0, 1, 17, 117, 0.043, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 1.5, 2, 18, 95, 0, 105, 113201, 0, 1, 18, 118, 0.018, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (160, 2.5, 2, 19, 108, 1, 110, 117001, 0, 0, 19, 119, 0.021, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (255, 2.5, 1, 20, 105, 0, 110, 113501, 1, 0, 20, 120, 0.036, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (350, 2.5, 3, 21, 105, 0, 105, 113401, 1, 0, 21, 121, 0.02, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 2.5, 1, 22, 105, 0, 110, 110101, 0, 1, 22, 122, 0.018, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (160, 3.5, 3, 23, 111, 1, 105, 110001, 0, 0, 23, 123, 0.03, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 1.5, 1, 24, 103, 0, 110, 117201, 0, 1, 24, 124, 0.027, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (160, 3.5, 2, 25, 108, 1, 105, 117301, 0, 0, 25, 125, 0.02, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (350, 2.5, 3, 26, 105, 0, 110, 117401, 1, 0, 26, 126, 0.04, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (350, 3.5, 1, 27, 105, 0, 110, 117101, 1, 0, 27, 127, 0.009, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (255, 3.5, 1, 28, 105, 0, 120, 122901, 1, 0, 28, 128, 0.022, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 2.5, 3, 29, 105, 0, 110, 122801, 0, 1, 29, 129, 0.028, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？");
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (130, 3.5, 2, 30, 108, 1, 105, 122701, 0, 0, 30, 130, 0.023, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。");
