INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 1, 0, 122601, 0.029, 0, 1, 1, 101, 105, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1, 160, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 0, 0, 122401, 0.014, 1, 2, 2, 102, 111, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 3, 120, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 1, 122501, 0.017, 0, 3, 3, 103, 105, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 2, 350, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 0, 107501, 0.016, 1, 4, 4, 104, 105, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 3, 130, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 1, 0, 107601, 0.014, 0, 5, 5, 105, 105, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 1, 160, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (120, 0, 1, 107801, 0.029, 0, 6, 6, 106, 103, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 2, 255, 3.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (120, 1, 0, 110601, 0.013, 0, 7, 7, 107, 103, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 1, 160, 3.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 1, 110401, 0.014, 0, 8, 8, 108, 103, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 2, 350, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 0, 0, 110501, 0.012, 1, 9, 9, 109, 100, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 3, 130, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 1, 0, 115701, 0.014, 0, 10, 10, 110, 103, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 1, 200, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 0, 1, 113601, 0.071, 0, 11, 11, 111, 103, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 2, 350, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (120, 0, 0, 107901, 0.014, 1, 12, 12, 112, 103, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 3, 130, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 1, 0, 108001, 0.022, 0, 13, 13, 113, 105, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 2, 200, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 1, 0, 110301, 0.031, 0, 14, 14, 114, 108, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 1, 160, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 0, 107701, 0.036, 1, 15, 15, 115, 105, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 1, 130, 0.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 1, 113101, 0.048, 0, 16, 16, 116, 95, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 2, 350, 3.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 0, 0, 113301, 0.043, 1, 17, 17, 117, 105, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 3, 130, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 0, 0, 113201, 0.018, 1, 18, 18, 118, 95, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 2, 130, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 1, 0, 117001, 0.021, 0, 19, 19, 119, 108, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 2, 160, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 1, 113501, 0.036, 0, 20, 20, 120, 105, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 1, 255, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 0, 1, 113401, 0.02, 0, 21, 21, 121, 105, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 3, 350, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 0, 110101, 0.018, 1, 22, 22, 122, 105, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 1, 130, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 1, 0, 110001, 0.03, 0, 23, 23, 123, 111, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 3, 160, 3.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 0, 117201, 0.027, 1, 24, 24, 124, 103, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 1, 130, 1.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 1, 0, 117301, 0.02, 0, 25, 25, 125, 108, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 2, 160, 3.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 1, 117401, 0.04, 0, 26, 26, 126, 105, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 3, 350, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 1, 117101, 0.009, 0, 27, 27, 127, 105, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 1, 350, 3.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (120, 0, 1, 122901, 0.022, 0, 28, 28, 128, 105, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 1, 255, 3.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (110, 0, 0, 122801, 0.028, 1, 29, 29, 129, 105, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 3, 130, 2.5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (105, 1, 0, 122701, 0.023, 0, 30, 30, 130, 108, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 2, 130, 3.5);
