INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 0.029, 1, 0, 1, 101, 110, 122601, 1.5, 160, 105, 0, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 0.014, 0, 0, 3, 102, 105, 122401, 2.5, 120, 111, 1, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 2);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 0.017, 0, 1, 2, 103, 110, 122501, 2.5, 350, 105, 0, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 3);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (4, 0.016, 0, 0, 3, 104, 110, 107501, 1.5, 130, 105, 1, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 4);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (5, 0.014, 1, 0, 1, 105, 110, 107601, 2.5, 160, 105, 0, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (6, 0.029, 0, 1, 2, 106, 120, 107801, 3.5, 255, 103, 0, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 6);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (7, 0.013, 1, 0, 1, 107, 120, 110601, 3.5, 160, 103, 0, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 7);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (8, 0.014, 0, 1, 2, 108, 110, 110401, 2.5, 350, 103, 0, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 8);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (9, 0.012, 0, 0, 3, 109, 105, 110501, 1.5, 130, 100, 1, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 9);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (10, 0.014, 1, 0, 1, 110, 105, 115701, 2.5, 200, 103, 0, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 10);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (11, 0.071, 0, 1, 2, 111, 105, 113601, 2.5, 350, 103, 0, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 11);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (12, 0.014, 0, 0, 3, 112, 120, 107901, 1.5, 130, 103, 1, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 12);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (13, 0.022, 1, 0, 2, 113, 110, 108001, 1.5, 200, 105, 0, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 13);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (14, 0.031, 1, 0, 1, 114, 110, 110301, 2.5, 160, 108, 0, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 14);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (15, 0.036, 0, 0, 1, 115, 110, 107701, 0.5, 130, 105, 1, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 15);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (16, 0.048, 0, 1, 2, 116, 110, 113101, 3.5, 350, 95, 0, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 16);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (17, 0.043, 0, 0, 3, 117, 105, 113301, 1.5, 130, 105, 1, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 17);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (18, 0.018, 0, 0, 2, 118, 105, 113201, 1.5, 130, 95, 1, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 18);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (19, 0.021, 1, 0, 2, 119, 110, 117001, 2.5, 160, 108, 0, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 19);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (20, 0.036, 0, 1, 1, 120, 110, 113501, 2.5, 255, 105, 0, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 20);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (21, 0.02, 0, 1, 3, 121, 105, 113401, 2.5, 350, 105, 0, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 21);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (22, 0.018, 0, 0, 1, 122, 110, 110101, 2.5, 130, 105, 1, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 22);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (23, 0.03, 1, 0, 3, 123, 105, 110001, 3.5, 160, 111, 0, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 23);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (24, 0.027, 0, 0, 1, 124, 110, 117201, 1.5, 130, 103, 1, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 24);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (25, 0.02, 1, 0, 2, 125, 105, 117301, 3.5, 160, 108, 0, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 25);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (26, 0.04, 0, 1, 3, 126, 110, 117401, 2.5, 350, 105, 0, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 26);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (27, 0.009, 0, 1, 1, 127, 110, 117101, 3.5, 350, 105, 0, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 27);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (28, 0.022, 0, 1, 1, 128, 120, 122901, 3.5, 255, 105, 0, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 28);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (29, 0.028, 0, 0, 3, 129, 110, 122801, 2.5, 130, 105, 1, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 29);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (30, 0.023, 1, 0, 2, 130, 105, 122701, 3.5, 130, 108, 0, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 30);
