INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 1.5, 122601, 0.029, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 101, 0, 0, 105, 1, 160, 1, 110, 1);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 2.5, 122401, 0.014, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 102, 0, 1, 111, 2, 120, 0, 105, 2);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 2.5, 122501, 0.017, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 103, 1, 0, 105, 3, 350, 0, 110, 3);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 1.5, 107501, 0.016, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 104, 0, 1, 105, 4, 130, 0, 110, 4);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 2.5, 107601, 0.014, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 105, 0, 0, 105, 5, 160, 1, 110, 5);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 3.5, 107801, 0.029, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 106, 1, 0, 103, 6, 255, 0, 120, 6);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 3.5, 110601, 0.013, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 107, 0, 0, 103, 7, 160, 1, 120, 7);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 2.5, 110401, 0.014, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 108, 1, 0, 103, 8, 350, 0, 110, 8);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 1.5, 110501, 0.012, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 109, 0, 1, 100, 9, 130, 0, 105, 9);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 2.5, 115701, 0.014, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 110, 0, 0, 103, 10, 200, 1, 105, 10);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 2.5, 113601, 0.071, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 111, 1, 0, 103, 11, 350, 0, 105, 11);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 1.5, 107901, 0.014, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 112, 0, 1, 103, 12, 130, 0, 120, 12);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 1.5, 108001, 0.022, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 113, 0, 0, 105, 13, 200, 1, 110, 13);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 2.5, 110301, 0.031, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 114, 0, 0, 108, 14, 160, 1, 110, 14);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 0.5, 107701, 0.036, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 115, 0, 1, 105, 15, 130, 0, 110, 15);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 3.5, 113101, 0.048, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 116, 1, 0, 95, 16, 350, 0, 110, 16);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 1.5, 113301, 0.043, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 117, 0, 1, 105, 17, 130, 0, 105, 17);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 1.5, 113201, 0.018, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 118, 0, 1, 95, 18, 130, 0, 105, 18);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 2.5, 117001, 0.021, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 119, 0, 0, 108, 19, 160, 1, 110, 19);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 2.5, 113501, 0.036, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 120, 1, 0, 105, 20, 255, 0, 110, 20);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 2.5, 113401, 0.02, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 121, 1, 0, 105, 21, 350, 0, 105, 21);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 2.5, 110101, 0.018, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 122, 0, 1, 105, 22, 130, 0, 110, 22);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 3.5, 110001, 0.03, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 123, 0, 0, 111, 23, 160, 1, 105, 23);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 1.5, 117201, 0.027, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 124, 0, 1, 103, 24, 130, 0, 110, 24);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 3.5, 117301, 0.02, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 125, 0, 0, 108, 25, 160, 1, 105, 25);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 2.5, 117401, 0.04, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 126, 1, 0, 105, 26, 350, 0, 110, 26);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 3.5, 117101, 0.009, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 127, 1, 0, 105, 27, 350, 0, 110, 27);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (1, 3.5, 122901, 0.022, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 128, 1, 0, 105, 28, 255, 0, 120, 28);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (3, 2.5, 122801, 0.028, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 129, 0, 1, 105, 29, 130, 0, 110, 29);
INSERT INTO `v1_0bd319e5ff18555a901bda7f8a9cc5f3268065dc0c94df1ae69144d3a5ad7b1b` VALUES (2, 3.5, 122701, 0.023, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 130, 0, 0, 108, 30, 130, 1, 105, 30);
