INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190804, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 60000, 1, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 1, 60000, 2, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 1, 60000, 3, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 2, 60000, 4, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 1, 60000, 5, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 1, 60000, 6, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 2, 60000, 7, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 1, 60000, 8, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 1, 60000, 9, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 1, 60000, 10, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 1, 60000, 11, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 2, 60000, 12, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 1, 60000, 13, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 1, 60000, 14, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 1, 60000, 15, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 1, 60000, 16, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 2, 60000, 17, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "カリン", 190804, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 0, 60000, 18, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "カリン", 190804, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 0, 60000, 19, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ハツネ", 101201, 1, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 0, 80001, 20, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ハツネ", 101201, 2, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 0, 80001, 21, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "シオリ", 103801, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 0, 80001, 22, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "シオリ", 103801, 2, "来年も、\n一緒にいられたら…\nうれしいな", 0, 0, 80001, 23, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キョウカ", 103601, 1, "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 0, 80001, 24, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キョウカ", 103601, 2, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 0, 80001, 25, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ミミ", 102001, 1, "おにいちゃんとあってから、\n1年たったよ～！", 0, 0, 80001, 26, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ミミ", 102001, 2, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 0, 80001, 27, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "ミソギ", 100401, 1, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 0, 80001, 28, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "ミソギ", 100401, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 0, 80001, 29, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "マホ", 101001, 1, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 0, 80001, 30, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "マホ", 101001, 2, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 0, 80001, 31, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "イリヤ", 104401, 1, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 0, 80001, 32, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "イリヤ", 104401, 2, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 0, 80001, 33, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カオリ", 101701, 1, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 0, 80001, 34, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カオリ", 101701, 2, "これからも、どうぞよろしく\nお願いするさ～！", 0, 0, 80001, 35, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "サレン", 102801, 1, "あんたと出会ってからもうそんなに\nたつのね～", 0, 0, 80001, 36, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "サレン", 102801, 2, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 0, 80001, 37, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "スズメ", 102501, 1, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 0, 80001, 38, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "スズメ", 102501, 2, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 0, 80001, 39, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "タマキ", 104601, 1, "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 0, 80001, 40, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "タマキ", 104601, 2, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 0, 80001, 41, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ミフユ", 104801, 1, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 0, 80001, 42, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ミフユ", 104801, 2, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 0, 80001, 43, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "アキノ", 103201, 1, "あなたさまと出会ってもう1年…", 0, 0, 80001, 44, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "アキノ", 103201, 2, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 0, 80001, 45, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ユカリ", 103401, 1, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 0, 80001, 46, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ユカリ", 103401, 2, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 0, 80001, 47, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "クリスティーナ", 107101, 1, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 0, 80001, 48, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "クリスティーナ", 107101, 2, "ともかくこれからも\n愉しませてくれよ☆", 0, 0, 80001, 49, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ジュン", 104701, 1, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 0, 80001, 50, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ジュン", 104701, 2, "見えないから分からないって？\nそれもそうだな…", 0, 0, 80001, 51, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "トモ", 103701, 1, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 0, 80001, 52, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "トモ", 103701, 2, "これからもよろしくね！", 0, 0, 80001, 53, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "マツリ", 100501, 1, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 0, 80001, 54, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "マツリ", 100501, 2, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 0, 80001, 55, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ミヤコ", 100701, 1, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 0, 80001, 56, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ミヤコ", 100701, 2, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 0, 80001, 57, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "シノブ", 103101, 1, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 0, 80001, 58, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "シノブ", 103101, 2, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 0, 80001, 59, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "アンナ", 100901, 1, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 0, 80001, 60, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "アンナ", 100901, 2, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 0, 80001, 61, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ナナカ", 101301, 1, "君と出会ってもう1年だって！\n早いねー！", 0, 0, 80001, 62, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ナナカ", 101301, 2, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 0, 80001, 63, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ミツキ", 105101, 1, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 0, 80001, 64, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ミツキ", 105101, 2, "これからも、いろんなところを\n調べさせてね？", 0, 0, 80001, 65, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ルカ", 105601, 1, "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 0, 80001, 66, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ルカ", 105601, 2, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 0, 80001, 67, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "エリコ", 102701, 1, "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 0, 80001, 68, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "エリコ", 102701, 2, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 0, 80001, 69, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "アヤネ", 102301, 1, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 0, 80001, 70, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "アヤネ", 102301, 2, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 0, 80001, 71, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "チカ", 104201, 1, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 0, 80001, 72, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "チカ", 104201, 2, "私のことを見守ってください\nできることなら、ずっと…", 0, 0, 80001, 73, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "クルミ", 102101, 1, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 0, 80001, 74, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "クルミ", 102101, 2, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 0, 80001, 75, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シズル", 104901, 1, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 0, 80001, 76, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シズル", 104901, 2, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 0, 80001, 77, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "リノ", 101101, 1, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 0, 80001, 78, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "リノ", 101101, 2, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 0, 80001, 79, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ユイ", 100201, 1, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 0, 80001, 80, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ユイ", 100201, 2, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 0, 80001, 81, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ヒヨリ", 100101, 1, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 0, 80001, 82, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ヒヨリ", 100101, 2, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 0, 80001, 83, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "レイ", 100301, 1, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 0, 80001, 84, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "レイ", 100301, 2, "キミに背中を預けられる日が、\n早くきますように", 0, 0, 80001, 85, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 1, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 0, 80001, 86, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 2, "これからもずっと、覚えていて\nくださいね！", 0, 0, 80001, 87, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 1, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 0, 80001, 88, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 2, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 0, 80001, 89, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 1, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 0, 80001, 90, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 2, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 0, 80001, 91, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "スズメ", 112101, 1, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0, 1, 60001, 92, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 1, 60001, 93, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 1, 60001, 94, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 1, 60001, 95, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 1, 60001, 96, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 2, 60001, 97, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 1, 60001, 98, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 1, 60001, 99, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 1, 60001, 100, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 1, 60001, 101, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 2, 60001, 102, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 1, 60001, 103, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 1, 60001, 104, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 1, 60001, 105, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 1, 60001, 106, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 3, 1, 60001, 107, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 3, 1, 60001, 108, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 2, 60001, 109, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 1, 60001, 110, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 1, 60001, 111, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 1, 60001, 112, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 1, 60001, 113, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 1, 60001, 114, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 4, 1, 60001, 115, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 2, 60001, 116, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "スズメ", 112101, 2, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 0, 60001, 117, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "スズメ", 112101, 3, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 0, 60001, 118, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 1, "ヒューヒュー♪\nドンドンパフパフー♪", 0, 0, 80003, 119, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 0, 80003, 120, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 2, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 0, 80003, 121, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 1, "え？　ちょっと\nいきなりなにっ？", 0, 0, 80003, 122, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 2, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 0, 80003, 123, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 2, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 0, 80003, 124, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 3, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 0, 80003, 125, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 3, "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 0, 80003, 126, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 3, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 0, 80003, 127, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 4, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 0, 80003, 128, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 4, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 0, 80003, 129, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 4, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 0, 80003, 130, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 5, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 0, 80003, 131, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 5, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 0, 80003, 132, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190801, 1, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 0, 80003, 133, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 6, "げっ…カリンじゃない\nなんでもないわよ！", 0, 0, 80003, 134, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190801, 2, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 0, 80003, 135, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 7, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 0, 80003, 136, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190801, 3, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 0, 80003, 137, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 8, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 0, 80003, 138, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 9, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 0, 80003, 139, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190801, 4, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 0, 80003, 140, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 10, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 0, 80003, 141, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190801, 5, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 0, 80003, 142, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 11, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 0, 80003, 143, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 6, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 0, 80003, 144, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ペコリーヌ", 105801, 5, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 0, 80003, 145, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 7, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 0, 80003, 146, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 8, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 0, 80003, 147, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ペコリーヌ", 105801, 6, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 0, 80003, 148, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ペコリーヌ", 105801, 7, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 0, 80003, 149, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 9, "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 0, 80003, 150, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ペコリーヌ", 105801, 8, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 0, 80003, 151, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 10, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 0, 80003, 152, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ペコリーヌ", 105801, 9, "えへへ、ありがとうございます\nコッコロちゃん！", 0, 0, 80003, 153, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 11, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 0, 80003, 154, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "ペコリーヌ", 105801, 10, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 0, 80003, 155, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 11, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 0, 80003, 156, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 12, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 0, 80003, 157, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 12, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 0, 80003, 158, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 13, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 0, 80003, 159, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 13, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 0, 80003, 160, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 14, "…積極的？\nなんの話よ？", 0, 0, 80003, 161, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 14, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 0, 80003, 162, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 15, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 0, 80003, 163, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 15, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 0, 80003, 164, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 16, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 0, 80003, 165, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 16, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 0, 80003, 166, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 17, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 0, 80003, 167, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 18, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 0, 80003, 168, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 12, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 0, 80003, 169, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 19, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 0, 80003, 170, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 13, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 0, 80003, 171, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 20, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 0, 80003, 172, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 14, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 0, 80003, 173, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 21, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 0, 80003, 174, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 15, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 0, 80003, 175, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 22, "かくし芸？", 0, 0, 80003, 176, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 16, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 0, 80003, 177, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 23, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 0, 80003, 178, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 17, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 0, 80003, 179, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 24, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 0, 80003, 180, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 17, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 0, 80003, 181, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 18, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 0, 80003, 182, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 25, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 0, 80003, 183, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 18, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 0, 80003, 184, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 26, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 0, 80003, 185, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 27, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 0, 80003, 186, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 28, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 0, 80003, 187, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 19, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 0, 80003, 188, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 19, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 0, 80003, 189, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 29, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 0, 80003, 190, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 30, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 0, 80003, 191, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 31, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 0, 80003, 192, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 32, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 0, 80003, 193, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 33, "あー\nあー\n…声、これで大丈夫かしら？", 0, 0, 80003, 194, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 34, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 0, 80003, 195, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 35, "オイッスー☆", 0, 0, 80003, 196, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 36, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 0, 80003, 197, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 37, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 0, 80003, 198, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 38, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 0, 80003, 199, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 20, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 0, 80003, 200, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 21, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 0, 80003, 201, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 22, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 0, 80003, 202, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 23, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 0, 80003, 203, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 24, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 0, 80003, 204, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 25, "オイッスー☆", 0, 0, 80003, 205, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 26, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 0, 80003, 206, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 27, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 0, 80003, 207, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 28, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 0, 80003, 208, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 20, "オイッスー☆", 0, 0, 80003, 209, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 21, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 0, 80003, 210, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 22, "あと６日で2.5周年ですか…", 0, 0, 80003, 211, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 23, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 0, 80003, 212, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 24, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 0, 80003, 213, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 25, "オイッスー☆", 0, 0, 80003, 214, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 26, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 0, 80003, 215, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 27, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 0, 80003, 216, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 28, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 0, 80003, 217, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 29, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 0, 80003, 218, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 30, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 0, 80003, 219, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 31, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 0, 80003, 220, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 29, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 0, 80003, 221, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 32, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 0, 80003, 222, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 39, "…………", 0, 0, 80003, 223, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 30, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 0, 80003, 224, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 40, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 0, 80003, 225, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 33, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 0, 80003, 226, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 41, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 0, 80003, 227, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 34, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 0, 80003, 228, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 35, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 0, 80003, 229, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 42, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 0, 80003, 230, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 31, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 0, 80003, 231, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 43, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 0, 80003, 232, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 36, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 0, 80003, 233, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 6, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 0, 80003, 234, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 7, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 0, 80003, 235, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 8, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 0, 80003, 236, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 9, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 0, 80003, 237, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 10, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 0, 80003, 238, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 11, "次にコッコロさんですが\nこちらは発声練習をしています", 0, 0, 80003, 239, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 12, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 0, 80003, 240, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 13, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 0, 80003, 241, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 14, "あれ？\nもしかして緊張で気を失って…？", 0, 0, 80003, 242, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 15, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 0, 80003, 243, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 16, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 0, 80003, 244, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 17, "それでは\nまた明日お会いしましょう♪", 0, 0, 80003, 245, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 44, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 0, 80003, 246, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "ペコリーヌ", 105801, 37, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 0, 80003, 247, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 32, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 0, 80003, 248, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 45, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 0, 80003, 249, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "ペコリーヌ", 105801, 38, "ひどいですよぉキャルちゃ～ん！", 0, 0, 80003, 250, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 33, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 0, 80003, 251, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "ペコリーヌ", 105801, 39, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 0, 80003, 252, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 46, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 0, 80003, 253, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 34, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 0, 80003, 254, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "ペコリーヌ", 105801, 40, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 0, 80003, 255, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 41, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 0, 80003, 256, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 47, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 0, 80003, 257, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 42, "忘れてきちゃいました☆", 0, 0, 80003, 258, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 48, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 0, 80003, 259, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 35, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 0, 80003, 260, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 49, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 0, 80003, 261, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 43, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 0, 80003, 262, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 50, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 0, 80003, 263, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 36, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 0, 80003, 264, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 51, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 0, 80003, 265, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 37, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 0, 80003, 266, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 52, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 0, 80003, 267, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 44, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 0, 80003, 268, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 38, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 0, 80003, 269, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 45, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 0, 80003, 270, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 53, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 0, 80003, 271, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 46, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 0, 80003, 272, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 18, "こんにちは\n【ギルド管理協会】のカリンです", 0, 0, 80003, 273, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 19, "昨日の生放送は\nいかがだったでしょうか？", 0, 0, 80003, 274, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 20, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 0, 80003, 275, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 21, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 0, 80003, 276, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 22, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 0, 80003, 277, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 23, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 0, 80003, 278, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 24, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 0, 80003, 279, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 25, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 0, 80003, 280, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 26, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 0, 80003, 281, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "カリン", 190801, 27, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 0, 80003, 282, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 47, "準備はいいですかっ？\n…いっきますよー！", 0, 0, 80003, 283, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 54, "祝！", 0, 0, 80003, 284, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 39, "2.5周年！", 0, 0, 80003, 285, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 48, "おめでとうございま～す☆", 0, 0, 80003, 286, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 55, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 0, 80003, 287, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 40, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 0, 80003, 288, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 49, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 0, 80003, 289, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 56, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 0, 80003, 290, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 41, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 0, 80003, 291, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 57, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 0, 80003, 292, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 50, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 0, 80003, 293, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 58, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 0, 80003, 294, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 51, "記念日だから\n平気平気♪\nやばいですね☆", 0, 0, 80003, 295, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 59, "意味分かんないわよ！", 0, 0, 80003, 296, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 42, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 0, 80003, 297, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 43, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 0, 80003, 298, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 52, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 0, 80003, 299, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "キャル", 106001, 60, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 0, 80003, 300, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 44, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 0, 80003, 301, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 53, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 0, 80003, 302, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "105901,45,106001,61", "ペコリーヌ・コッコロ・キャル", 105801, 54, "2.5周年\nありがとうございます！", 0, 0, 80003, 303, 1, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190804, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 60002, 304, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 1, 60002, 305, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 1, 60002, 306, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 1, 60002, 307, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 1, 60002, 308, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 1, 2, 60002, 309, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 1, 60002, 310, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 1, 60002, 311, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 1, 60002, 312, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 1, 60002, 313, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 2, 60002, 314, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 3, 1, 60002, 315, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 3, 1, 60002, 316, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 1, 60002, 317, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 3, 1, 60002, 318, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 3, 1, 60002, 319, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 1, 60002, 320, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 3, 2, 60002, 321, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 4, 1, 60002, 322, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 4, 1, 60002, 323, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 4, 1, 60002, 324, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 1, 60002, 325, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 1, 60002, 326, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 4, 1, 60002, 327, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 2, 60002, 328, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "カリン", 190804, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 0, 60002, 329, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "カリン", 190804, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 0, 60002, 330, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 1, "ささ、シェフィさまはこちらへ…", 0, 0, 80005, 331, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 1, "コッコロさん、どこへ行くの…？\n…え？", 0, 0, 80005, 332, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 1, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 0, 80005, 333, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 2, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 0, 80005, 334, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 2, "ええと…\n二人とも何をしているの？", 0, 0, 80005, 335, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 0, 80005, 336, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 3, "あの日…？", 0, 0, 80005, 337, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 2, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 0, 80005, 338, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 3, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 0, 80005, 339, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 1, "やばいですね☆", 0, 0, 80005, 340, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 2, "って、なにしれっと\n参加してんのよカリン！", 0, 0, 80005, 341, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 2, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 0, 80005, 342, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 3, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 0, 80005, 343, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 4, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 0, 80005, 344, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 4, "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 0, 80005, 345, 0, 1);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 5, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 0, 80005, 346, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 3, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 0, 80005, 347, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 4, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 0, 80005, 348, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 6, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 0, 80005, 349, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 5, "さすがでございます\nペコリーヌさま", 0, 0, 80005, 350, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 3, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 0, 80005, 351, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 5, "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 0, 80005, 352, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 7, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 0, 80005, 353, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 6, "さ、次はキャルさまの\n番でございますよ", 0, 0, 80005, 354, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 4, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 0, 80005, 355, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 6, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 0, 80005, 356, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 5, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 0, 80005, 357, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 6, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 0, 80005, 358, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 7, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 0, 80005, 359, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 7, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 0, 80005, 360, 0, 2);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 8, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 0, 80005, 361, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 8, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 0, 80005, 362, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 9, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 0, 80005, 363, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 7, "聖学祭…って、なにかしら？", 0, 0, 80005, 364, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 10, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 0, 80005, 365, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 8, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 0, 80005, 366, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 9, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 0, 80005, 367, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 9, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 0, 80005, 368, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 10, "いや、そのまんますぎでしょ…", 0, 0, 80005, 369, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 8, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 0, 80005, 370, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 10, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 0, 80005, 371, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 9, "えっ？　ええ…\nぎゅーっ…？", 0, 0, 80005, 372, 0, 3);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 11, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 0, 80005, 373, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 10, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 0, 80005, 374, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 12, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 0, 80005, 375, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 11, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 0, 80005, 376, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 13, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 0, 80005, 377, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 11, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 0, 80005, 378, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 14, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 0, 80005, 379, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 15, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 0, 80005, 380, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 11, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 0, 80005, 381, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 12, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 0, 80005, 382, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 12, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 0, 80005, 383, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 16, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 0, 80005, 384, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 12, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 0, 80005, 385, 0, 4);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 13, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 0, 80005, 386, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 14, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 0, 80005, 387, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 15, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 0, 80005, 388, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 17, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 0, 80005, 389, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 18, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 0, 80005, 390, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 13, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 0, 80005, 391, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 13, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 0, 80005, 392, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 16, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 0, 80005, 393, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 19, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 0, 80005, 394, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 17, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 0, 80005, 395, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 18, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 0, 80005, 396, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 19, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 0, 80005, 397, 0, 5);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 4, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 0, 80005, 398, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 5, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 0, 80005, 399, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 20, "…えっ！？", 0, 0, 80005, 400, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 20, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 0, 80005, 401, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 14, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 0, 80005, 402, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 14, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 0, 80005, 403, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 15, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 0, 80005, 404, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 21, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 0, 80005, 405, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 21, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 0, 80005, 406, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 22, "ぶっ殺すぞ！？", 0, 0, 80005, 407, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 6, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 0, 80005, 408, 0, 6);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 15, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 0, 80005, 409, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 23, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 0, 80005, 410, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 16, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 0, 80005, 411, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 22, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 0, 80005, 412, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 24, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 0, 80005, 413, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 17, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 0, 80005, 414, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 16, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 0, 80005, 415, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 18, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 0, 80005, 416, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 17, "もちろんでございます\n仲良くお勉強いたしましょう", 0, 0, 80005, 417, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 25, "しれっと教育ママが\n生まれてる…？", 0, 0, 80005, 418, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 23, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 0, 80005, 419, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 26, "え～～～～！？", 0, 0, 80005, 420, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 27, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 0, 80005, 421, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 19, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 0, 80005, 422, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 18, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 0, 80005, 423, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 24, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 0, 80005, 424, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 20, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 0, 80005, 425, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 28, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 0, 80005, 426, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 19, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 0, 80005, 427, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 21, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 0, 80005, 428, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 29, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 0, 80005, 429, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 30, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 0, 80005, 430, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 25, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 0, 80005, 431, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 31, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 0, 80005, 432, 0, 8);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 22, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 0, 80005, 433, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 23, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 0, 80005, 434, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 24, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 0, 80005, 435, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 25, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 0, 80005, 436, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 26, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 0, 80005, 437, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 27, "それから、それから…！\nぅ、ぅぅっ…！", 0, 0, 80005, 438, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 28, "―びえぇぇ～んっ！！！", 0, 0, 80005, 439, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 20, "どうされましたか、シェフィさま", 0, 0, 80005, 440, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 29, "ううっ…ひっく…\nママッ…！", 0, 0, 80005, 441, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 21, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 0, 80005, 442, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 22, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 0, 80005, 443, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 30, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 0, 80005, 444, 0, 9);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 23, "主さま\n3.5周年まであと５日\nでございます", 0, 0, 80005, 445, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 24, "ですがその前に\n生放送がありますね", 0, 0, 80005, 446, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 25, "…生放送に向けた\n意気込みでございますか？", 0, 0, 80005, 447, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 26, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 0, 80005, 448, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 27, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 0, 80005, 449, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 28, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 0, 80005, 450, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 29, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 0, 80005, 451, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 30, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 0, 80005, 452, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "コッコロ", 105901, 31, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 0, 80005, 453, 0, 10);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 26, "3.5周年まであと４日！\nやばいですね☆", 0, 0, 80005, 454, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 32, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 0, 80005, 455, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 27, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 0, 80005, 456, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 28, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 0, 80005, 457, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 29, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 0, 80005, 458, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 33, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 0, 80005, 459, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 30, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 0, 80005, 460, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 31, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 0, 80005, 461, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 32, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 0, 80005, 462, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 34, "だからあたしはやるなんて一言も…", 0, 0, 80005, 463, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "ペコリーヌ", 105801, 33, "オイッスー☆", 0, 0, 80005, 464, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 35, "聞きなさいっての！！", 0, 0, 80005, 465, 0, 11);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 31, "3.5周年まであと３日…\nそして生放送は、明日…", 0, 0, 80005, 466, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 32, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 0, 80005, 467, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 33, "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 0, 80005, 468, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 34, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 0, 80005, 469, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 35, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 0, 80005, 470, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 34, "やってますねシェフィちゃん！", 0, 0, 80005, 471, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 36, "なによ、考えることは\nみんな同じってこと？", 0, 0, 80005, 472, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 32, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 0, 80005, 473, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 36, "みんな…もちろんよ！", 0, 0, 80005, 474, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 35, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 0, 80005, 475, 0, 12);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 7, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 0, 80005, 476, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 8, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 0, 80005, 477, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 36, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 0, 80005, 478, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 9, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 0, 80005, 479, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 33, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 0, 80005, 480, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 10, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 0, 80005, 481, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 37, "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 0, 80005, 482, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 11, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 0, 80005, 483, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 37, "あうー？", 0, 0, 80005, 484, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "105901,34,106001,38,190801,12", "みんな", 105801, 37, "あ…", 0, 0, 80005, 485, 1, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 39, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 0, 80005, 486, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 35, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 0, 80005, 487, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 38, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 0, 80005, 488, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 13, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 0, 80005, 489, 0, 13);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 38, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 0, 80005, 490, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 39, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 0, 80005, 491, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 40, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 0, 80005, 492, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 36, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 0, 80005, 493, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 39, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 0, 80005, 494, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 40, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 0, 80005, 495, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "コッコロ", 105901, 37, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 0, 80005, 496, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "キャル", 106001, 41, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 0, 80005, 497, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 40, "へ、へぇ…", 0, 0, 80005, 498, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 41, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 0, 80005, 499, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 41, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 0, 80005, 500, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "シェフィ", 106401, 42, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 0, 80005, 501, 0, 14);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 42, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 0, 80005, 502, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 42, "祝！", 0, 0, 80005, 503, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 38, "3.5周年！", 0, 0, 80005, 504, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 43, "おめでとうございます！", 0, 0, 80005, 505, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 14, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 0, 80005, 506, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "カリン", 190801, 15, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 0, 80005, 507, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 43, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 0, 80005, 508, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 44, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 0, 80005, 509, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 43, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 0, 80005, 510, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "キャル", 106001, 44, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 0, 80005, 511, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 44, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 0, 80005, 512, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (0, "", "シェフィ", 106401, 45, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 0, 80005, 513, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 39, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 0, 80005, 514, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 40, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 0, 80005, 515, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "", "コッコロ", 105901, 41, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 0, 80005, 516, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "ペコリーヌ", 105801, 45, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 0, 80005, 517, 0, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, "105901,42,106001,45,106401,46,190801,16", "みんな", 105801, 46, "これからも\nよろしくおねがいします！", 0, 0, 80005, 518, 1, 15);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190804, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 60003, 519, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 1, 60003, 520, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 1, 60003, 521, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 1, 1, 60003, 522, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 1, 1, 60003, 523, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 1, 2, 60003, 524, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 1, 60003, 525, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 1, 60003, 526, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 2, 1, 60003, 527, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 2, 1, 60003, 528, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 2, 60003, 529, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 3, 1, 60003, 530, 0, 0);
