INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 1, 60005, 588, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　争いごとは勝つが油断は大敵！　　　　　　ま、このあたしと一緒なら、向かうところ敵はねーですよっ！", 3, 2, 60005, 589, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　ラッキーアイテムは「光るもの」！　　　　ハツネちゃんの「きらーんっ☆」な魔法、見せちゃおうかなっ？", 3, 2, 60005, 590, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　のんびり穏やかな一日となるでしょう！　　あなた様を邪魔するものは全てエリコが排除しますわ、クスクス♪", 4, 1, 60005, 591, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　小さな勇気で踏み出す一歩が大事！\n遠くから見てるだけじゃダメ。今日は、少しだけ近づいてみます！", 4, 1, 60005, 592, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 1, 60005, 593, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 4, 1, 60005, 594, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　難しい頼み事をされるかもしれません！　　うまくやれればきっと成長につながります、挑戦してみましょう！", 4, 1, 60005, 595, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　空を眺めると気持ちが晴れやかに！　　　　眺めるだけじゃなくて、ひとっ飛びしちゃいましょうかっ♪", 4, 2, 60005, 596, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　掃除をすると運勢が開けそう！　　　　　　「わ、めんど」「ヴルムっ！　新年早々サボっちゃダメなんだからね！」", 4, 2, 60005, 597, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "カリン", 190804, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 0, 60005, 598, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, "", "カリン", 190804, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 0, 60005, 599, 0, 7);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, "", "カリン", 190804, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 60006, 600, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　勇気を持って、新しいことに挑戦しよう！\n幸先がいいねえ少年！　今年もいい一年になるよう祈ってるよ。", 1, 2, 60006, 601, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　どんなピンチも簡単に乗り越えられそう！\nよい結果が出たようで何よりです。さて、待人の方は……ふふ。", 1, 2, 60006, 602, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は姫吉！　あの人と仲直り……できちゃうかも！？\nはあ……この私があなたの運勢に興味を抱くわけないでしょう？", 1, 2, 60006, 603, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　身近な友達との絆が深まる予感……？\n騎士様は、どなたと友誼を結ぶのでしょう？　気になります……", 2, 1, 60006, 604, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は大吉！　ラッキーアイテムは「蛇の抜け殻」！\nこのアタシにお目にかかれるなんて……アンタ、幸せものね♪", 2, 2, 60006, 605, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　気になるあの人に会えちゃうかも！？\n『初夢を正夢にする枕』であなたに会え……な、なんでもない！", 3, 2, 60006, 606, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　子供に戻って、一日中遊びつくそう！\nお年玉でけん玉ゲット！　おじちゃん、もしかめ一万回やって！", 3, 2, 60006, 607, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は中吉！　今日はおうちで、ゆーっくりくつろごう。\nお雑煮よし。みかんよし。さ、こたつで寝正月だ。君も入る？", 3, 1, 60006, 608, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉！　突然のドッキリに注意！\nおや、後ろに髪の長い女の霊が──フフ、冗談です。私ですよ。", 4, 2, 60006, 609, 0, 0);
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, "", "", 0, 0, "今日の運勢は小吉。慌てなくても、幸運はきっとやってくる。\n運は自分で掴むもの──媛が言ってました。でも、どうやって？", 4, 2, 60006, 610, 0, 0);
