INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (1, 0, 1, 60000, "カリン", 190804, 1, 0, "", 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (2, 1, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (3, 1, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (4, 1, 2, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (5, 2, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (6, 2, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (7, 2, 2, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (8, 3, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (9, 3, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (10, 3, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (11, 3, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (12, 3, 2, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (13, 4, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (14, 4, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (15, 4, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (16, 4, 1, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (17, 4, 2, 60000, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (18, 0, 0, 60000, "カリン", 190804, 3, 0, "", 2, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (19, 0, 0, 60000, "カリン", 190804, 3, 0, "", 3, 7, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (20, 0, 0, 80001, "ハツネ", 101201, 0, 0, "", 1, 1, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (21, 0, 0, 80001, "ハツネ", 101201, 0, 0, "", 2, 1, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (22, 0, 0, 80001, "シオリ", 103801, 1, 0, "", 1, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (23, 0, 0, 80001, "シオリ", 103801, 1, 0, "", 2, 1, "来年も、\n一緒にいられたら…\nうれしいな");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (24, 0, 0, 80001, "キョウカ", 103601, 0, 0, "", 1, 2, "あなたと一緒にいるようになって、\nもう1年ですか…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (25, 0, 0, 80001, "キョウカ", 103601, 0, 0, "", 2, 2, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (26, 0, 0, 80001, "ミミ", 102001, 1, 0, "", 1, 2, "おにいちゃんとあってから、\n1年たったよ～！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (27, 0, 0, 80001, "ミミ", 102001, 1, 0, "", 2, 2, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (28, 0, 0, 80001, "ミソギ", 100401, 2, 0, "", 1, 2, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (29, 0, 0, 80001, "ミソギ", 100401, 2, 0, "", 2, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (30, 0, 0, 80001, "マホ", 101001, 0, 0, "", 1, 3, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (31, 0, 0, 80001, "マホ", 101001, 0, 0, "", 2, 3, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (32, 0, 0, 80001, "イリヤ", 104401, 1, 0, "", 1, 3, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (33, 0, 0, 80001, "イリヤ", 104401, 1, 0, "", 2, 3, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (34, 0, 0, 80001, "カオリ", 101701, 2, 0, "", 1, 3, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (35, 0, 0, 80001, "カオリ", 101701, 2, 0, "", 2, 3, "これからも、どうぞよろしく\nお願いするさ～！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (36, 0, 0, 80001, "サレン", 102801, 0, 0, "", 1, 4, "あんたと出会ってからもうそんなに\nたつのね～");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (37, 0, 0, 80001, "サレン", 102801, 0, 0, "", 2, 4, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (38, 0, 0, 80001, "スズメ", 102501, 1, 0, "", 1, 4, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (39, 0, 0, 80001, "スズメ", 102501, 1, 0, "", 2, 4, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (40, 0, 0, 80001, "タマキ", 104601, 0, 0, "", 1, 5, "振り返れば、今までいろんなことが\nあったにゃあ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (41, 0, 0, 80001, "タマキ", 104601, 0, 0, "", 2, 5, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (42, 0, 0, 80001, "ミフユ", 104801, 1, 0, "", 1, 5, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (43, 0, 0, 80001, "ミフユ", 104801, 1, 0, "", 2, 5, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (44, 0, 0, 80001, "アキノ", 103201, 0, 0, "", 1, 6, "あなたさまと出会ってもう1年…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (45, 0, 0, 80001, "アキノ", 103201, 0, 0, "", 2, 6, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (46, 0, 0, 80001, "ユカリ", 103401, 1, 0, "", 1, 6, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (47, 0, 0, 80001, "ユカリ", 103401, 1, 0, "", 2, 6, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (48, 0, 0, 80001, "クリスティーナ", 107101, 0, 0, "", 1, 7, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (49, 0, 0, 80001, "クリスティーナ", 107101, 0, 0, "", 2, 7, "ともかくこれからも\n愉しませてくれよ☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (50, 0, 0, 80001, "ジュン", 104701, 1, 0, "", 1, 7, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (51, 0, 0, 80001, "ジュン", 104701, 1, 0, "", 2, 7, "見えないから分からないって？\nそれもそうだな…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (52, 0, 0, 80001, "トモ", 103701, 0, 0, "", 1, 8, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (53, 0, 0, 80001, "トモ", 103701, 0, 0, "", 2, 8, "これからもよろしくね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (54, 0, 0, 80001, "マツリ", 100501, 1, 0, "", 1, 8, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (55, 0, 0, 80001, "マツリ", 100501, 1, 0, "", 2, 8, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (56, 0, 0, 80001, "ミヤコ", 100701, 0, 0, "", 1, 9, "オマエと会って1年なの！\n全然成仏もできなかったし、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (57, 0, 0, 80001, "ミヤコ", 100701, 0, 0, "", 2, 9, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (58, 0, 0, 80001, "シノブ", 103101, 1, 0, "", 1, 9, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (59, 0, 0, 80001, "シノブ", 103101, 1, 0, "", 2, 9, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (60, 0, 0, 80001, "アンナ", 100901, 0, 0, "", 1, 10, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (61, 0, 0, 80001, "アンナ", 100901, 0, 0, "", 2, 10, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (62, 0, 0, 80001, "ナナカ", 101301, 1, 0, "", 1, 10, "君と出会ってもう1年だって！\n早いねー！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (63, 0, 0, 80001, "ナナカ", 101301, 1, 0, "", 2, 10, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (64, 0, 0, 80001, "ミツキ", 105101, 0, 0, "", 1, 11, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (65, 0, 0, 80001, "ミツキ", 105101, 0, 0, "", 2, 11, "これからも、いろんなところを\n調べさせてね？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (66, 0, 0, 80001, "ルカ", 105601, 1, 0, "", 1, 11, "お前さんと出会ってもう1年か\n助太刀するつもりが");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (67, 0, 0, 80001, "ルカ", 105601, 1, 0, "", 2, 11, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (68, 0, 0, 80001, "エリコ", 102701, 2, 0, "", 1, 11, "早いものですわね、あなた様と\nお会いして、もう1年…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (69, 0, 0, 80001, "エリコ", 102701, 2, 0, "", 2, 11, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (70, 0, 0, 80001, "アヤネ", 102301, 0, 0, "", 1, 12, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (71, 0, 0, 80001, "アヤネ", 102301, 0, 0, "", 2, 12, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (72, 0, 0, 80001, "チカ", 104201, 1, 0, "", 1, 12, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (73, 0, 0, 80001, "チカ", 104201, 1, 0, "", 2, 12, "私のことを見守ってください\nできることなら、ずっと…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (74, 0, 0, 80001, "クルミ", 102101, 2, 0, "", 1, 12, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (75, 0, 0, 80001, "クルミ", 102101, 2, 0, "", 2, 12, "で、でもこれからもずっと…\n一緒にいたいなぁ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (76, 0, 0, 80001, "シズル", 104901, 0, 0, "", 1, 13, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (77, 0, 0, 80001, "シズル", 104901, 0, 0, "", 2, 13, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (78, 0, 0, 80001, "リノ", 101101, 1, 0, "", 1, 13, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (79, 0, 0, 80001, "リノ", 101101, 1, 0, "", 2, 13, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (80, 0, 0, 80001, "ユイ", 100201, 0, 0, "", 1, 14, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (81, 0, 0, 80001, "ユイ", 100201, 0, 0, "", 2, 14, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (82, 0, 0, 80001, "ヒヨリ", 100101, 1, 0, "", 1, 14, "騎士くんと出会って、まだ1年しか\nたってないんだ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (83, 0, 0, 80001, "ヒヨリ", 100101, 1, 0, "", 2, 14, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (84, 0, 0, 80001, "レイ", 100301, 2, 0, "", 1, 14, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (85, 0, 0, 80001, "レイ", 100301, 2, 0, "", 2, 14, "キミに背中を預けられる日が、\n早くきますように");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (86, 0, 0, 80001, "ペコリーヌ", 105801, 0, 0, "", 1, 15, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (87, 0, 0, 80001, "ペコリーヌ", 105801, 0, 0, "", 2, 15, "これからもずっと、覚えていて\nくださいね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (88, 0, 0, 80001, "コッコロ", 105901, 1, 0, "", 1, 15, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (89, 0, 0, 80001, "コッコロ", 105901, 1, 0, "", 2, 15, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (90, 0, 0, 80001, "キャル", 106001, 2, 0, "", 1, 15, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (91, 0, 0, 80001, "キャル", 106001, 2, 0, "", 2, 15, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (92, 0, 1, 60001, "スズメ", 112101, 1, 0, "", 1, 0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (93, 1, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (94, 1, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (95, 1, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (96, 1, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (97, 1, 2, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (98, 2, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (99, 2, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (100, 2, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (101, 2, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (102, 2, 2, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (103, 3, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (104, 3, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (105, 3, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (106, 3, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (107, 3, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (108, 3, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (109, 3, 2, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (110, 4, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (111, 4, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (112, 4, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (113, 4, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (114, 4, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (115, 4, 1, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (116, 4, 2, 60001, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (117, 0, 0, 60001, "スズメ", 112101, 3, 0, "", 2, 0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (118, 0, 0, 60001, "スズメ", 112101, 3, 0, "", 3, 7, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (119, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 1, 1, "ヒューヒュー♪\nドンドンパフパフー♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (120, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 1, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (121, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 2, 1, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (122, 0, 0, 80003, "キャル", 106001, 2, 0, "", 1, 1, "え？　ちょっと\nいきなりなにっ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (123, 0, 0, 80003, "キャル", 106001, 2, 0, "", 2, 1, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (124, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 2, 1, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (125, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 3, 1, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (126, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 3, 1, "ですからその日に向けて\n今から気分を盛り上げているのです");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (127, 0, 0, 80003, "キャル", 106001, 2, 0, "", 3, 1, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (128, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 4, 1, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (129, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 4, 1, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (130, 0, 0, 80003, "キャル", 106001, 2, 0, "", 4, 1, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (131, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 5, 1, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (132, 0, 0, 80003, "キャル", 106001, 0, 0, "", 5, 2, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (133, 0, 0, 80003, "カリン", 190801, 1, 0, "", 1, 2, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (134, 0, 0, 80003, "キャル", 106001, 0, 0, "", 6, 2, "げっ…カリンじゃない\nなんでもないわよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (135, 0, 0, 80003, "カリン", 190801, 1, 0, "", 2, 2, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (136, 0, 0, 80003, "キャル", 106001, 0, 0, "", 7, 2, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (137, 0, 0, 80003, "カリン", 190801, 1, 0, "", 3, 2, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (138, 0, 0, 80003, "キャル", 106001, 0, 0, "", 8, 2, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (139, 0, 0, 80003, "キャル", 106001, 0, 0, "", 9, 2, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (140, 0, 0, 80003, "カリン", 190801, 1, 0, "", 4, 2, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (141, 0, 0, 80003, "キャル", 106001, 0, 0, "", 10, 2, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (142, 0, 0, 80003, "カリン", 190801, 1, 0, "", 5, 2, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (143, 0, 0, 80003, "キャル", 106001, 0, 0, "", 11, 2, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (144, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 6, 3, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (145, 0, 0, 80003, "ペコリーヌ", 105801, 1, 0, "", 5, 3, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (146, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 7, 3, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (147, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 8, 3, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (148, 0, 0, 80003, "ペコリーヌ", 105801, 1, 0, "", 6, 3, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (149, 0, 0, 80003, "ペコリーヌ", 105801, 1, 0, "", 7, 3, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (150, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 9, 3, "あとは、牧場に素敵な温泉が\n出来たとか…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (151, 0, 0, 80003, "ペコリーヌ", 105801, 1, 0, "", 8, 3, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (152, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 10, 3, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (153, 0, 0, 80003, "ペコリーヌ", 105801, 1, 0, "", 9, 3, "えへへ、ありがとうございます\nコッコロちゃん！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (154, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 11, 3, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (155, 0, 0, 80003, "ペコリーヌ", 105801, 1, 0, "", 10, 3, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (156, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 11, 4, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (157, 0, 0, 80003, "キャル", 106001, 1, 0, "", 12, 4, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (158, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 12, 4, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (159, 0, 0, 80003, "キャル", 106001, 1, 0, "", 13, 4, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (160, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 13, 4, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (161, 0, 0, 80003, "キャル", 106001, 1, 0, "", 14, 4, "…積極的？\nなんの話よ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (162, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 14, 4, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (163, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 15, 4, "ムーサの町で\nきゃるきゃる～ん☆　って！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (164, 0, 0, 80003, "キャル", 106001, 1, 0, "", 15, 4, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (165, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 16, 4, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (166, 0, 0, 80003, "キャル", 106001, 1, 0, "", 16, 4, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (167, 0, 0, 80003, "キャル", 106001, 1, 0, "", 17, 4, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (168, 0, 0, 80003, "キャル", 106001, 0, 0, "", 18, 5, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (169, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 12, 5, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (170, 0, 0, 80003, "キャル", 106001, 0, 0, "", 19, 5, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (171, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 13, 5, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (172, 0, 0, 80003, "キャル", 106001, 0, 0, "", 20, 5, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (173, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 14, 5, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (174, 0, 0, 80003, "キャル", 106001, 0, 0, "", 21, 5, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (175, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 15, 5, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (176, 0, 0, 80003, "キャル", 106001, 0, 0, "", 22, 5, "かくし芸？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (177, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 16, 5, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (178, 0, 0, 80003, "キャル", 106001, 0, 0, "", 23, 5, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (179, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 17, 6, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (180, 0, 0, 80003, "キャル", 106001, 2, 0, "", 24, 6, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (181, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 17, 6, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (182, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 18, 6, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (183, 0, 0, 80003, "キャル", 106001, 2, 0, "", 25, 6, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (184, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 18, 6, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (185, 0, 0, 80003, "キャル", 106001, 2, 0, "", 26, 6, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (186, 0, 0, 80003, "キャル", 106001, 2, 0, "", 27, 6, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (187, 0, 0, 80003, "キャル", 106001, 2, 0, "", 28, 6, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (188, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 19, 6, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (189, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 19, 6, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (190, 0, 0, 80003, "キャル", 106001, 2, 0, "", 29, 6, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (191, 0, 0, 80003, "キャル", 106001, 0, 0, "", 30, 7, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (192, 0, 0, 80003, "キャル", 106001, 0, 0, "", 31, 7, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (193, 0, 0, 80003, "キャル", 106001, 0, 0, "", 32, 7, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (194, 0, 0, 80003, "キャル", 106001, 0, 0, "", 33, 7, "あー\nあー\n…声、これで大丈夫かしら？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (195, 0, 0, 80003, "キャル", 106001, 0, 0, "", 34, 7, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (196, 0, 0, 80003, "キャル", 106001, 0, 0, "", 35, 7, "オイッスー☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (197, 0, 0, 80003, "キャル", 106001, 0, 0, "", 36, 7, "……\nいや、ないわ\nいつもどおりが一番ね、うん");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (198, 0, 0, 80003, "キャル", 106001, 0, 0, "", 37, 7, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (199, 0, 0, 80003, "キャル", 106001, 0, 0, "", 38, 7, "ど、どど、どうしよう！？\n緊張してきちゃった…！！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (200, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 20, 8, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (201, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 21, 8, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (202, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 22, 8, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (203, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 23, 8, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (204, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 24, 8, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (205, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 25, 8, "オイッスー☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (206, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 26, 8, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (207, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 27, 8, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (208, 0, 0, 80003, "コッコロ", 105901, 0, 0, "", 28, 8, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (209, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 20, 9, "オイッスー☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (210, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 21, 9, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (211, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 22, 9, "あと６日で2.5周年ですか…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (212, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 23, 9, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (213, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 24, 9, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (214, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 25, 9, "オイッスー☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (215, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 26, 9, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (216, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 27, 9, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (217, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 28, 9, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (218, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 29, 9, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (219, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 30, 9, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (220, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 31, 10, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (221, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 29, 10, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (222, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 32, 10, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (223, 0, 0, 80003, "キャル", 106001, 2, 0, "", 39, 10, "…………");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (224, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 30, 10, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (225, 0, 0, 80003, "キャル", 106001, 2, 0, "", 40, 10, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (226, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 33, 10, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (227, 0, 0, 80003, "キャル", 106001, 2, 0, "", 41, 10, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (228, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 34, 10, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (229, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 35, 10, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (230, 0, 0, 80003, "キャル", 106001, 2, 0, "", 42, 10, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (231, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 31, 10, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (232, 0, 0, 80003, "キャル", 106001, 2, 0, "", 43, 10, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (233, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 36, 10, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (234, 0, 0, 80003, "カリン", 190801, 0, 0, "", 6, 11, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (235, 0, 0, 80003, "カリン", 190801, 0, 0, "", 7, 11, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (236, 0, 0, 80003, "カリン", 190801, 0, 0, "", 8, 11, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (237, 0, 0, 80003, "カリン", 190801, 0, 0, "", 9, 11, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (238, 0, 0, 80003, "カリン", 190801, 0, 0, "", 10, 11, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (239, 0, 0, 80003, "カリン", 190801, 0, 0, "", 11, 11, "次にコッコロさんですが\nこちらは発声練習をしています");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (240, 0, 0, 80003, "カリン", 190801, 0, 0, "", 12, 11, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (241, 0, 0, 80003, "カリン", 190801, 0, 0, "", 13, 11, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (242, 0, 0, 80003, "カリン", 190801, 0, 0, "", 14, 11, "あれ？\nもしかして緊張で気を失って…？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (243, 0, 0, 80003, "カリン", 190801, 0, 0, "", 15, 11, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (244, 0, 0, 80003, "カリン", 190801, 0, 0, "", 16, 11, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (245, 0, 0, 80003, "カリン", 190801, 0, 0, "", 17, 11, "それでは\nまた明日お会いしましょう♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (246, 0, 0, 80003, "キャル", 106001, 0, 0, "", 44, 12, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (247, 0, 0, 80003, "ペコリーヌ", 105801, 2, 0, "", 37, 12, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (248, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 32, 12, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (249, 0, 0, 80003, "キャル", 106001, 0, 0, "", 45, 12, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (250, 0, 0, 80003, "ペコリーヌ", 105801, 2, 0, "", 38, 12, "ひどいですよぉキャルちゃ～ん！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (251, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 33, 12, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (252, 0, 0, 80003, "ペコリーヌ", 105801, 2, 0, "", 39, 12, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (253, 0, 0, 80003, "キャル", 106001, 0, 0, "", 46, 12, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (254, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 34, 12, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (255, 0, 0, 80003, "ペコリーヌ", 105801, 2, 0, "", 40, 12, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (256, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 41, 13, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (257, 0, 0, 80003, "キャル", 106001, 2, 0, "", 47, 13, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (258, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 42, 13, "忘れてきちゃいました☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (259, 0, 0, 80003, "キャル", 106001, 2, 0, "", 48, 13, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (260, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 35, 13, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (261, 0, 0, 80003, "キャル", 106001, 2, 0, "", 49, 13, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (262, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 43, 13, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (263, 0, 0, 80003, "キャル", 106001, 2, 0, "", 50, 13, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (264, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 36, 13, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (265, 0, 0, 80003, "キャル", 106001, 2, 0, "", 51, 13, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (266, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 37, 13, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (267, 0, 0, 80003, "キャル", 106001, 2, 0, "", 52, 13, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (268, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 44, 13, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (269, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 38, 13, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (270, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 45, 13, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (271, 0, 0, 80003, "キャル", 106001, 2, 0, "", 53, 13, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (272, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 46, 13, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (273, 0, 0, 80003, "カリン", 190801, 0, 0, "", 18, 14, "こんにちは\n【ギルド管理協会】のカリンです");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (274, 0, 0, 80003, "カリン", 190801, 0, 0, "", 19, 14, "昨日の生放送は\nいかがだったでしょうか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (275, 0, 0, 80003, "カリン", 190801, 0, 0, "", 20, 14, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (276, 0, 0, 80003, "カリン", 190801, 0, 0, "", 21, 14, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (277, 0, 0, 80003, "カリン", 190801, 0, 0, "", 22, 14, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (278, 0, 0, 80003, "カリン", 190801, 0, 0, "", 23, 14, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (279, 0, 0, 80003, "カリン", 190801, 0, 0, "", 24, 14, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (280, 0, 0, 80003, "カリン", 190801, 0, 0, "", 25, 14, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (281, 0, 0, 80003, "カリン", 190801, 0, 0, "", 26, 14, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (282, 0, 0, 80003, "カリン", 190801, 0, 0, "", 27, 14, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (283, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 47, 15, "準備はいいですかっ？\n…いっきますよー！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (284, 0, 0, 80003, "キャル", 106001, 2, 0, "", 54, 15, "祝！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (285, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 39, 15, "2.5周年！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (286, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 48, 15, "おめでとうございま～す☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (287, 0, 0, 80003, "キャル", 106001, 2, 0, "", 55, 15, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (288, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 40, 15, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (289, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 49, 15, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (290, 0, 0, 80003, "キャル", 106001, 2, 0, "", 56, 15, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (291, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 41, 15, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (292, 0, 0, 80003, "キャル", 106001, 2, 0, "", 57, 15, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (293, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 50, 15, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (294, 0, 0, 80003, "キャル", 106001, 2, 0, "", 58, 15, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (295, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 51, 15, "記念日だから\n平気平気♪\nやばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (296, 0, 0, 80003, "キャル", 106001, 2, 0, "", 59, 15, "意味分かんないわよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (297, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 42, 15, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (298, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 43, 15, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (299, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 52, 15, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (300, 0, 0, 80003, "キャル", 106001, 2, 0, "", 60, 15, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (301, 0, 0, 80003, "コッコロ", 105901, 1, 0, "", 44, 15, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (302, 0, 0, 80003, "ペコリーヌ", 105801, 0, 0, "", 53, 15, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (303, 0, 0, 80003, "ペコリーヌ・コッコロ・キャル", 105801, 0, 1, "105901,45,106001,61", 54, 15, "2.5周年\nありがとうございます！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (304, 0, 1, 60002, "カリン", 190804, 1, 0, "", 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (305, 1, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (306, 1, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (307, 1, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (308, 1, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (309, 1, 2, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (310, 2, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (311, 2, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (312, 2, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (313, 2, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (314, 2, 2, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (315, 3, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (316, 3, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (317, 3, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (318, 3, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (319, 3, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (320, 3, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (321, 3, 2, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (322, 4, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (323, 4, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (324, 4, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (325, 4, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (326, 4, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (327, 4, 1, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (328, 4, 2, 60002, "", 0, 2, 0, "", 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (329, 0, 0, 60002, "カリン", 190804, 3, 0, "", 2, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (330, 0, 0, 60002, "カリン", 190804, 3, 0, "", 3, 7, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (331, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 1, 1, "ささ、シェフィさまはこちらへ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (332, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 1, 1, "コッコロさん、どこへ行くの…？\n…え？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (333, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 1, 1, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (334, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 2, 1, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (335, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 2, 1, "ええと…\n二人とも何をしているの？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (336, 0, 0, 80005, "キャル", 106001, 1, 0, "", 1, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (337, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 3, 1, "あの日…？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (338, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 2, 1, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (339, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 3, 1, "『プリンセスコネクト！\nRe:Dive』3.5周年！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (340, 0, 0, 80005, "カリン", 190801, 2, 0, "", 1, 1, "やばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (341, 0, 0, 80005, "キャル", 106001, 1, 0, "", 2, 1, "って、なにしれっと\n参加してんのよカリン！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (342, 0, 0, 80005, "カリン", 190801, 2, 0, "", 2, 1, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (343, 0, 0, 80005, "カリン", 190801, 2, 0, "", 3, 1, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (344, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 4, 1, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (345, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 4, 1, "えっ！？　ええとええと…！\nあ、あと14日…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (346, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 5, 2, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (347, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 3, 2, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (348, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 4, 2, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (349, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 6, 2, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (350, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 5, 2, "さすがでございます\nペコリーヌさま");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (351, 0, 0, 80005, "キャル", 106001, 0, 0, "", 3, 2, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (352, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 5, 2, "しゃなり、しゃなり…\nふふ、案外できるものね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (353, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 7, 2, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (354, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 6, 2, "さ、次はキャルさまの\n番でございますよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (355, 0, 0, 80005, "キャル", 106001, 0, 0, "", 4, 2, "あ、あたしはいいわよ！\nそういうの全然分かんないし…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (356, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 6, 2, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (357, 0, 0, 80005, "キャル", 106001, 0, 0, "", 5, 2, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (358, 0, 0, 80005, "キャル", 106001, 0, 0, "", 6, 2, "いたたた…もぉお～！\nなんであたしがこんな目に～！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (359, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 7, 2, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (360, 0, 0, 80005, "キャル", 106001, 0, 0, "", 7, 2, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (361, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 8, 3, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (362, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 8, 3, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (363, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 9, 3, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (364, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 7, 3, "聖学祭…って、なにかしら？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (365, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 10, 3, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (366, 0, 0, 80005, "キャル", 106001, 0, 0, "", 8, 3, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (367, 0, 0, 80005, "キャル", 106001, 0, 0, "", 9, 3, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (368, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 9, 3, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (369, 0, 0, 80005, "キャル", 106001, 0, 0, "", 10, 3, "いや、そのまんますぎでしょ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (370, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 8, 3, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (371, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 10, 3, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (372, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 9, 3, "えっ？　ええ…\nぎゅーっ…？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (373, 0, 0, 80005, "キャル", 106001, 0, 0, "", 11, 4, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (374, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 10, 4, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (375, 0, 0, 80005, "キャル", 106001, 0, 0, "", 12, 4, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (376, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 11, 4, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (377, 0, 0, 80005, "キャル", 106001, 0, 0, "", 13, 4, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (378, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 11, 4, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (379, 0, 0, 80005, "キャル", 106001, 0, 0, "", 14, 4, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (380, 0, 0, 80005, "キャル", 106001, 0, 0, "", 15, 4, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (381, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 11, 4, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (382, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 12, 4, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (383, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 12, 4, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (384, 0, 0, 80005, "キャル", 106001, 0, 0, "", 16, 4, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (385, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 12, 4, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (386, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 13, 5, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (387, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 14, 5, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (388, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 15, 5, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (389, 0, 0, 80005, "キャル", 106001, 0, 0, "", 17, 5, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (390, 0, 0, 80005, "キャル", 106001, 0, 0, "", 18, 5, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (391, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 13, 5, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (392, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 13, 5, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (393, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 16, 5, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (394, 0, 0, 80005, "キャル", 106001, 0, 0, "", 19, 5, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (395, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 17, 5, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (396, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 18, 5, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (397, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 19, 5, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (398, 0, 0, 80005, "カリン", 190801, 2, 0, "", 4, 6, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (399, 0, 0, 80005, "カリン", 190801, 2, 0, "", 5, 6, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (400, 0, 0, 80005, "キャル", 106001, 1, 0, "", 20, 6, "…えっ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (401, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 20, 6, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (402, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 14, 6, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (403, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 14, 6, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (404, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 15, 6, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (405, 0, 0, 80005, "キャル", 106001, 1, 0, "", 21, 6, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (406, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 21, 6, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (407, 0, 0, 80005, "キャル", 106001, 1, 0, "", 22, 6, "ぶっ殺すぞ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (408, 0, 0, 80005, "カリン", 190801, 2, 0, "", 6, 6, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (409, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 15, 7, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (410, 0, 0, 80005, "キャル", 106001, 0, 0, "", 23, 7, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (411, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 16, 7, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (412, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 22, 7, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (413, 0, 0, 80005, "キャル", 106001, 0, 0, "", 24, 7, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (414, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 17, 7, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (415, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 16, 7, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (416, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 18, 7, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (417, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 17, 7, "もちろんでございます\n仲良くお勉強いたしましょう");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (418, 0, 0, 80005, "キャル", 106001, 0, 0, "", 25, 7, "しれっと教育ママが\n生まれてる…？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (419, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 23, 7, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (420, 0, 0, 80005, "キャル", 106001, 0, 0, "", 26, 7, "え～～～～！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (421, 0, 0, 80005, "キャル", 106001, 0, 0, "", 27, 8, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (422, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 19, 8, "…\nねぇコッコロさん\nキャル、どうしちゃったの？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (423, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 18, 8, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (424, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 24, 8, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (425, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 20, 8, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (426, 0, 0, 80005, "キャル", 106001, 0, 0, "", 28, 8, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (427, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 19, 8, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (428, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 21, 8, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (429, 0, 0, 80005, "キャル", 106001, 0, 0, "", 29, 8, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (430, 0, 0, 80005, "キャル", 106001, 0, 0, "", 30, 8, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (431, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 25, 8, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (432, 0, 0, 80005, "キャル", 106001, 0, 0, "", 31, 8, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (433, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 22, 9, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (434, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 23, 9, "…えっ？\n生放送の意気込み！？\n突然なに！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (435, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 24, 9, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (436, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 25, 9, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (437, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 26, 9, "とにかく落ち着いて\nスタッフさんの指示を聞いて…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (438, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 27, 9, "それから、それから…！\nぅ、ぅぅっ…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (439, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 28, 9, "―びえぇぇ～んっ！！！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (440, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 20, 9, "どうされましたか、シェフィさま");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (441, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 29, 9, "ううっ…ひっく…\nママッ…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (442, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 21, 9, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (443, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 22, 9, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (444, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 30, 9, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (445, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 23, 10, "主さま\n3.5周年まであと５日\nでございます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (446, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 24, 10, "ですがその前に\n生放送がありますね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (447, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 25, 10, "…生放送に向けた\n意気込みでございますか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (448, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 26, 10, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (449, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 27, 10, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (450, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 28, 10, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (451, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 29, 10, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (452, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 30, 10, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (453, 0, 0, 80005, "コッコロ", 105901, 0, 0, "", 31, 10, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (454, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 26, 11, "3.5周年まであと４日！\nやばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (455, 0, 0, 80005, "キャル", 106001, 1, 0, "", 32, 11, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (456, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 27, 11, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (457, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 28, 11, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (458, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 29, 11, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (459, 0, 0, 80005, "キャル", 106001, 1, 0, "", 33, 11, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (460, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 30, 11, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (461, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 31, 11, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (462, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 32, 11, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (463, 0, 0, 80005, "キャル", 106001, 1, 0, "", 34, 11, "だからあたしはやるなんて一言も…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (464, 0, 0, 80005, "ペコリーヌ", 105801, 0, 0, "", 33, 11, "オイッスー☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (465, 0, 0, 80005, "キャル", 106001, 1, 0, "", 35, 11, "聞きなさいっての！！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (466, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 31, 12, "3.5周年まであと３日…\nそして生放送は、明日…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (467, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 32, 12, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (468, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 33, 12, "おいっす！\n…こうじゃなかったわね\nええと確か…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (469, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 34, 12, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (470, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 35, 12, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (471, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 34, 12, "やってますねシェフィちゃん！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (472, 0, 0, 80005, "キャル", 106001, 0, 0, "", 36, 12, "なによ、考えることは\nみんな同じってこと？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (473, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 32, 12, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (474, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 36, 12, "みんな…もちろんよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (475, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 35, 12, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (476, 0, 0, 80005, "カリン", 190801, 2, 0, "", 7, 13, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (477, 0, 0, 80005, "カリン", 190801, 2, 0, "", 8, 13, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (478, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 36, 13, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (479, 0, 0, 80005, "カリン", 190801, 2, 0, "", 9, 13, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (480, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 33, 13, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (481, 0, 0, 80005, "カリン", 190801, 2, 0, "", 10, 13, "さすが【美食殿】のママですね！\nそしてキャルさんは…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (482, 0, 0, 80005, "キャル", 106001, 1, 0, "", 37, 13, "大丈夫…大丈夫…\nあたしは大丈夫…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (483, 0, 0, 80005, "カリン", 190801, 2, 0, "", 11, 13, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (484, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 37, 13, "あうー？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (485, 0, 0, 80005, "みんな", 105801, 4, 1, "105901,34,106001,38,190801,12", 37, 13, "あ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (486, 0, 0, 80005, "キャル", 106001, 1, 0, "", 39, 13, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (487, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 35, 13, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (488, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 38, 13, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (489, 0, 0, 80005, "カリン", 190801, 2, 0, "", 13, 13, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (490, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 38, 14, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (491, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 39, 14, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (492, 0, 0, 80005, "キャル", 106001, 0, 0, "", 40, 14, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (493, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 36, 14, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (494, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 39, 14, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (495, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 40, 14, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (496, 0, 0, 80005, "コッコロ", 105901, 1, 0, "", 37, 14, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (497, 0, 0, 80005, "キャル", 106001, 0, 0, "", 41, 14, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (498, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 40, 14, "へ、へぇ…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (499, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 41, 14, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (500, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 41, 14, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (501, 0, 0, 80005, "シェフィ", 106401, 2, 0, "", 42, 14, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (502, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 42, 15, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (503, 0, 0, 80005, "キャル", 106001, 1, 0, "", 42, 15, "祝！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (504, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 38, 15, "3.5周年！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (505, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 43, 15, "おめでとうございます！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (506, 0, 0, 80005, "カリン", 190801, 2, 0, "", 14, 15, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (507, 0, 0, 80005, "カリン", 190801, 2, 0, "", 15, 15, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (508, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 43, 15, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (509, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 44, 15, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (510, 0, 0, 80005, "キャル", 106001, 1, 0, "", 43, 15, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (511, 0, 0, 80005, "キャル", 106001, 1, 0, "", 44, 15, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (512, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 44, 15, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (513, 0, 0, 80005, "シェフィ", 106401, 0, 0, "", 45, 15, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (514, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 39, 15, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (515, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 40, 15, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (516, 0, 0, 80005, "コッコロ", 105901, 4, 0, "", 41, 15, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (517, 0, 0, 80005, "ペコリーヌ", 105801, 3, 0, "", 45, 15, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (518, 0, 0, 80005, "みんな", 105801, 4, 1, "105901,42,106001,45,106401,46,190801,16", 46, 15, "これからも\nよろしくおねがいします！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (519, 0, 1, 60003, "カリン", 190804, 1, 0, "", 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (520, 1, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (521, 1, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (522, 1, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (523, 1, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (524, 1, 2, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (525, 2, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (526, 2, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (527, 2, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (528, 2, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (529, 2, 2, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！");
INSERT INTO `v1_0d550bb2467d606f5a34d3f7f2560982b18db95862186ffb874d02a7ee3fc01d` VALUES (530, 3, 1, 60003, "", 0, 2, 0, "", 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？");
