INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (24001, "2020/07/31 12:00:00", 102301, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 2, 10046, 2, "アヤネ", 401, 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0);
