INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (24013, "2020/07/31 12:00:00", 113501, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 6, 10046, 2, "ミサト", 111, 24, 0.0, 5.33, "2030/12/17 14:59:59", 0.0);
