INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (24018, "2020/07/31 12:00:00", 192401, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 2, 10046, 2, "ダイチ", 613, 24, 0.0, 0.0, "2030/12/17 14:59:59", 0.0);
