INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 102301, 24, 2, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24001, 0.0, 401, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 10046, "アヤネ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 102301, 24, 2, "2030/12/17 14:59:59", 2.21, "2020/07/31 12:00:00", 24002, 0.0, 402, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 10046, "アヤネ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 102301, 24, 2, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24003, 0.0, 403, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 10046, "アヤネ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 192201, 24, 1, "2030/12/17 14:59:59", 1.99, "2020/07/31 12:00:00", 24004, 0.0, 701, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 10046, "アサヒ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192201, 24, 2, "2030/12/17 14:59:59", 2.21, "2020/07/31 12:00:00", 24005, 0.0, 702, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 10046, "アサヒ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 192201, 24, 1, "2030/12/17 14:59:59", 3.33, "2020/07/31 12:00:00", 24006, 0.0, 703, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 10046, "アサヒ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 192301, 24, 2, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24007, 0.0, 801, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 10046, "部員３", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192301, 24, 2, "2030/12/17 14:59:59", 2.68, "2020/07/31 12:00:00", 24008, 0.0, 802, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 10046, "部員３", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 192301, 24, 2, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24009, 0.0, 803, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 10046, "部員３", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192501, 24, 1, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24010, 0.0, 501, "やった！", 10046, "", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192501, 24, 1, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24011, 0.0, 502, "勝ったよ！", 10046, "", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192501, 24, 1, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24012, 0.0, 503, "いい調子！", 10046, "", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (6, 113501, 24, 2, "2030/12/17 14:59:59", 5.33, "2020/07/31 12:00:00", 24013, 0.0, 111, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 10046, "ミサト", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 113501, 24, 2, "2030/12/17 14:59:59", 6.85, "2020/07/31 12:00:00", 24014, 0.0, 112, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 10046, "ミサト", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 113501, 24, 2, "2030/12/17 14:59:59", 3.0, "2020/07/31 12:00:00", 24015, 0.0, 113, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 10046, "ミサト", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192401, 24, 2, "2030/12/17 14:59:59", 6.11, "2020/07/31 12:00:00", 24016, 0.0, 611, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 10046, "ダイチ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192401, 24, 2, "2030/12/17 14:59:59", 6.75, "2020/07/31 12:00:00", 24017, 0.0, 612, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 10046, "ダイチ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 192401, 24, 2, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24018, 0.0, 613, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 10046, "ダイチ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 113401, 24, 1, "2030/12/17 14:59:59", 4.52, "2020/07/31 12:00:00", 24019, 0.0, 211, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 10046, "ハツネ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 113401, 24, 1, "2030/12/17 14:59:59", 3.89, "2020/07/31 12:00:00", 24020, 0.0, 212, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 10046, "ハツネ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 113401, 24, 2, "2030/12/17 14:59:59", 13.49, "2020/07/31 12:00:00", 24021, 0.0, 213, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 10046, "ハツネ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 106501, 24, 2, "2030/12/17 14:59:59", 3.17, "2020/07/31 12:00:00", 24022, 0.0, 311, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 10046, "カヤ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 106501, 24, 2, "2030/12/17 14:59:59", 4.42, "2020/07/31 12:00:00", 24023, 0.0, 312, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 10046, "カヤ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (6, 106501, 24, 1, "2030/12/17 14:59:59", 2.89, "2020/07/31 12:00:00", 24024, 0.0, 313, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 10046, "カヤ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (4, 102301, 24, 3, "2030/12/17 14:59:59", 3.71, "2020/07/31 12:00:00", 24025, 0.0, 421, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 10046, "アヤネ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 192201, 24, 1, "2030/12/17 14:59:59", 5.1, "2020/07/31 12:00:00", 24026, 0.0, 721, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 10046, "アサヒ", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192301, 24, 3, "2030/12/17 14:59:59", 4.34, "2020/07/31 12:00:00", 24027, 0.0, 821, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 10046, "部員３", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 192501, 24, 1, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24028, 0.0, 521, "いい勝負だったよ\nまたやろうね", 10046, "", 0.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 113501, 24, 2, "2030/12/17 14:59:59", 5.62, "2020/07/31 12:00:00", 24029, 0.0, 101, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 10046, "ミサト", -360.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 113501, 24, 2, "2030/12/17 14:59:59", 9.58, "2020/07/31 12:00:00", 24030, 0.0, 102, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 10046, "ミサト", -360.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 113501, 24, 2, "2030/12/17 14:59:59", 0.0, "2020/07/31 12:00:00", 24031, 0.0, 103, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 10046, "ミサト", -360.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (2, 113501, 24, 1, "2030/12/17 14:59:59", 9.13, "2020/07/31 12:00:00", 24032, 0.0, 104, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 10046, "ミサト", -360.0);
INSERT INTO `v1_0dde03f7d3a18236e8b384e3f7db262e441086ccd8bd935a31ef6f42b2045863` VALUES (1, 113501, 24, 2, "2030/12/17 14:59:59", 8.27, "2020/07/31 12:00:00", 24033, 0.0, 105, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 10046, "ミサト", -360.0);
