INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (100, 1, "やばいですね☆なの！", 1, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "ペコリーヌのプリン", 3, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (101, 1, "主さま、なの！", 2, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "コッコロのプリン", 3, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (102, 0, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "キャルのプリン", 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (103, 1, "おにーたん♪なの！", 4, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "シェフィのプリン", 3, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (104, 1, "ハーハッハッハ！\n世界征服なのじゃなのー！", 5, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "イリヤのプリン", 3, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (105, 1, "お兄ちゃんのエッチ！　なの！", 6, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "アカリのプリン", 3, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (106, 1, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 7, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "ヨリのプリン", 3, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (107, 0, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 8, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "シノブのプリン", 3, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (108, 1, "インフェルノシールド！なの！", 9, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "ジュンのプリン", 3, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (109, 1, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 10, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "トモのプリン", 3, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (110, 1, "タイガーヒーローボンバー！\nなの！", 11, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "マツリのプリン", 3, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (111, 1, "ま、いいんじゃん？\nしらんけどなの", 13, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "クロエのプリン", 3, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (112, 1, "ぷりーん★なの！", 14, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "チエルのプリン", 3, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (113, 1, "端的に換言すれば\n青春の味なの？", 15, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "ユニのプリン", 3, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (114, 1, "幼なじみはあんた…なの？", 16, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "サレンのプリン", 3, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (115, 1, "ひゃああんなの～！", 17, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "スズメのプリン", 3, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (116, 0, "サルキデバレヤ！なの！", 18, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "クルミのプリン", 3, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (117, 1, "プリンフルスイングなの～！", 19, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "アヤネのプリン", 3, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (118, 1, "みらくるみやりん\nくるりんぱ～☆なの！", 20, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "マホのプリン", 3, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (119, 1, "ウルフェンバイトなの！", 21, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "マコトのプリン", 3, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (120, 1, "なんくるないさ～！なの！", 22, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "カオリのプリン", 3, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (121, 1, "ふふん。簡単な推理なの", 23, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "カスミのプリン", 3, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (122, 1, "エリザベス～！なの～！", 24, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "マヒルのプリン", 3, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (123, 1, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 25, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "シオリのプリン", 3, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (124, 1, "もふもふにしてあげるなの！", 26, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "リマのプリン", 3, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (125, 0, "めんどくさいな～なの…", 27, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "リンのプリン", 3, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (126, 1, "プリンの権能を解放するの～！", 28, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "ラビリスタのプリン", 3, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (127, 0, "お姉ちゃんパワー全開！なの！", 29, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "シズルのプリン", 3, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (128, 1, "お兄ちゃん、プリンをよこすの！", 30, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "リノのプリン", 3, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (129, 1, "困った時はお互いさまさま！\nなの！", 31, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "ヒヨリのプリン", 3, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (130, 1, "フラワリングヒールなの～♪", 32, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "ユイのプリン", 3, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (131, 1, "スラッシュテンペスト！なの。", 33, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "レイのプリン", 3, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (132, 1, "おーほっほほほ！なの！", 34, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "アキノのプリン", 3, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (133, 1, "プリしゅわ最高～！なの！", 35, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "ユカリのプリン", 3, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (134, 1, "にゃにゃにゃ！なの！", 36, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "タマキのプリン", 3, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (135, 0, "効率全開！なの！", 37, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "ミフユのプリン", 3, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (136, 1, "あらあら～なの～♪", 38, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "ミサトのプリン", 3, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (137, 1, "スヤスヤ～…なの…", 39, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "ハツネのプリン", 3, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (138, 1, "ぼっちは卒業なの～！", 40, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "アオイのプリン", 3, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (139, 1, "リズムあげるのー！", 41, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "ノゾミのプリン", 3, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (140, 1, "風の加護を！なの！", 42, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "チカのプリン", 3, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (141, 1, "騎士さんのために\nしたんじゃありません！なの！", 43, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "ツムギのプリン", 3, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (142, 0, "宴の始まりなの！", 12, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "クリスティーナのプリン", 3, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (143, 1, "一刀両断、覚悟しな！なの！", 44, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "ルカのプリン", 3, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (144, 1, "きゅぴーん！キタコレ！なの！", 45, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "ナナカのプリン", 3, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (145, 1, "人呼んで、疾風のミヤコなの！", 46, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "アンナのプリン", 3, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (146, 1, "クスクス…クスクスクス…なの", 47, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "エリコのプリン", 3, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (147, 1, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 48, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "ミツキのプリン", 3, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (148, 1, "私は子どもではない！なの！", 49, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "モニカのプリン", 3, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (149, 1, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 50, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ニノンのプリン", 3, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (150, 0, "…ぐふふ、じゅるり～なの", 51, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "クウカのプリン", 3, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (151, 1, "あぁ、ミヤコはなんて美しいの～", 52, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "ユキのプリン", 3, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (152, 0, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 53, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "アユミのプリン", 3, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (153, 0, "にひひ…イタズラの時間なの！", 54, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "ミソギのプリン", 3, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (154, 0, "ぴょんぴょこぴょーん、なの♪", 55, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ミミのプリン", 3, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (155, 0, "へんたいふしんしゃさんなの！", 56, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "キョウカのプリン", 3, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (156, 1, "ハート狙い撃ち！なの！", 57, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "スズナのプリン", 3, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (157, 0, "授業を始めます♪なの！", 58, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "イオのプリン", 3, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (158, 1, "立派なレディ、なの！", 59, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "ミサキのプリン", 3, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (159, 1, "プリンを持ってこないと\nお仕置きだよ～☆なの！", 60, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "ホマレのプリン", 3, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (160, 1, "たぎってきたの！", 61, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "カヤのプリン", 3, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (161, 0, "下剋上、やってやるですなの！", 62, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "イノリのプリン", 3, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (162, 1, "どけどけ～！なの～！", 63, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "ムイミのプリン", 3, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (163, 1, "ミラーミラー！なの！", 64, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ネネカのプリン", 3, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (164, 1, "プリンに忠誠を誓いますわ！\nなの！", 65, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "クレジッタのプリン", 3, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 5);
INSERT INTO `v1_0e73c6548040b134fd6a8d53f7a16ce7e640a5d392715fb7ea86b55d96bf87d2` VALUES (165, 1, "ギルド管理協会へようこそ！\nなの！", 66, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "カリンのプリン", 3, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 5);
