INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1, 1, 101, 122601, 1, 110, 0, 0, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 105, 1.5, 0.029, 160, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2, 2, 102, 122401, 3, 105, 0, 1, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 111, 2.5, 0.014, 120, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3, 3, 103, 122501, 2, 110, 1, 0, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 105, 2.5, 0.017, 350, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (4, 4, 104, 107501, 3, 110, 0, 1, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 105, 1.5, 0.016, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (5, 5, 105, 107601, 1, 110, 0, 0, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 105, 2.5, 0.014, 160, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (6, 6, 106, 107801, 2, 120, 1, 0, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 103, 3.5, 0.029, 255, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (7, 7, 107, 110601, 1, 120, 0, 0, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 103, 3.5, 0.013, 160, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (8, 8, 108, 110401, 2, 110, 1, 0, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 103, 2.5, 0.014, 350, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (9, 9, 109, 110501, 3, 105, 0, 1, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 100, 1.5, 0.012, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (10, 10, 110, 115701, 1, 105, 0, 0, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 103, 2.5, 0.014, 200, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (11, 11, 111, 113601, 2, 105, 1, 0, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 103, 2.5, 0.071, 350, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (12, 12, 112, 107901, 3, 120, 0, 1, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 103, 1.5, 0.014, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (13, 13, 113, 108001, 2, 110, 0, 0, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 105, 1.5, 0.022, 200, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (14, 14, 114, 110301, 1, 110, 0, 0, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 108, 2.5, 0.031, 160, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (15, 15, 115, 107701, 1, 110, 0, 1, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 105, 0.5, 0.036, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (16, 16, 116, 113101, 2, 110, 1, 0, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 95, 3.5, 0.048, 350, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (17, 17, 117, 113301, 3, 105, 0, 1, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 105, 1.5, 0.043, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (18, 18, 118, 113201, 2, 105, 0, 1, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 95, 1.5, 0.018, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (19, 19, 119, 117001, 2, 110, 0, 0, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 108, 2.5, 0.021, 160, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (20, 20, 120, 113501, 1, 110, 1, 0, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 105, 2.5, 0.036, 255, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (21, 21, 121, 113401, 3, 105, 1, 0, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 105, 2.5, 0.02, 350, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (22, 22, 122, 110101, 1, 110, 0, 1, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 105, 2.5, 0.018, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (23, 23, 123, 110001, 3, 105, 0, 0, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 111, 3.5, 0.03, 160, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (24, 24, 124, 117201, 1, 110, 0, 1, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 103, 1.5, 0.027, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (25, 25, 125, 117301, 2, 105, 0, 0, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 108, 3.5, 0.02, 160, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (26, 26, 126, 117401, 3, 110, 1, 0, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 105, 2.5, 0.04, 350, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (27, 27, 127, 117101, 1, 110, 1, 0, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 105, 3.5, 0.009, 350, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (28, 28, 128, 122901, 1, 120, 1, 0, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 105, 3.5, 0.022, 255, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (29, 29, 129, 122801, 3, 110, 0, 1, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 105, 2.5, 0.028, 130, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (30, 30, 130, 122701, 2, 105, 0, 0, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 108, 3.5, 0.023, 130, 1);
