INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 1, 160, 101, 1, 122601, 1, 0, 110, 105, 0.029, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 2, 120, 102, 2, 122401, 0, 0, 105, 111, 0.014, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 3, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 3, 350, 103, 3, 122501, 0, 1, 110, 105, 0.017, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 4, 130, 104, 4, 107501, 0, 0, 110, 105, 0.016, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 3, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 5, 160, 105, 5, 107601, 1, 0, 110, 105, 0.014, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 6, 255, 106, 6, 107801, 0, 1, 120, 103, 0.029, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 7, 160, 107, 7, 110601, 1, 0, 120, 103, 0.013, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 8, 350, 108, 8, 110401, 0, 1, 110, 103, 0.014, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 9, 130, 109, 9, 110501, 0, 0, 105, 100, 0.012, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 3, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 10, 200, 110, 10, 115701, 1, 0, 105, 103, 0.014, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 11, 350, 111, 11, 113601, 0, 1, 105, 103, 0.071, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 12, 130, 112, 12, 107901, 0, 0, 120, 103, 0.014, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 3, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 13, 200, 113, 13, 108001, 1, 0, 110, 105, 0.022, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 14, 160, 114, 14, 110301, 1, 0, 110, 108, 0.031, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (0.5, 15, 130, 115, 15, 107701, 0, 0, 110, 105, 0.036, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 1, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 16, 350, 116, 16, 113101, 0, 1, 110, 95, 0.048, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 17, 130, 117, 17, 113301, 0, 0, 105, 105, 0.043, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 3, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 18, 130, 118, 18, 113201, 0, 0, 105, 95, 0.018, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 2, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 19, 160, 119, 19, 117001, 1, 0, 110, 108, 0.021, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 20, 255, 120, 20, 113501, 0, 1, 110, 105, 0.036, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 21, 350, 121, 21, 113401, 0, 1, 105, 105, 0.02, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 3, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 22, 130, 122, 22, 110101, 0, 0, 110, 105, 0.018, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 1, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 23, 160, 123, 23, 110001, 1, 0, 105, 111, 0.03, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 3, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (1.5, 24, 130, 124, 24, 117201, 0, 0, 110, 103, 0.027, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 1, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 25, 160, 125, 25, 117301, 1, 0, 105, 108, 0.02, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 2, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 26, 350, 126, 26, 117401, 0, 1, 110, 105, 0.04, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 3, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 27, 350, 127, 27, 117101, 0, 1, 110, 105, 0.009, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 28, 255, 128, 28, 122901, 0, 1, 120, 105, 0.022, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 1, 0);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (2.5, 29, 130, 129, 29, 122801, 0, 0, 110, 105, 0.028, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 3, 1);
INSERT INTO `v1_13de4c3dc313949ab80cd285375a9f2fad386bdf06760b18eedd0429ef9ce42e` VALUES (3.5, 30, 130, 130, 30, 122701, 1, 0, 105, 108, 0.023, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 2, 0);
