INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.029, 101, 160, 110, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1.5, 1, 1, 122601, 1, 1, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.014, 102, 120, 105, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 2.5, 3, 2, 122401, 0, 2, 0, 111);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.017, 103, 350, 110, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 2.5, 2, 3, 122501, 0, 3, 1, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.016, 104, 130, 110, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 1.5, 3, 4, 107501, 0, 4, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.014, 105, 160, 110, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 2.5, 1, 5, 107601, 1, 5, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.029, 106, 255, 120, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 3.5, 2, 6, 107801, 0, 6, 1, 103);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.013, 107, 160, 120, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 3.5, 1, 7, 110601, 1, 7, 0, 103);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.014, 108, 350, 110, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 2.5, 2, 8, 110401, 0, 8, 1, 103);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.012, 109, 130, 105, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 1.5, 3, 9, 110501, 0, 9, 0, 100);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.014, 110, 200, 105, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 2.5, 1, 10, 115701, 1, 10, 0, 103);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.071, 111, 350, 105, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 2.5, 2, 11, 113601, 0, 11, 1, 103);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.014, 112, 130, 120, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 1.5, 3, 12, 107901, 0, 12, 0, 103);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.022, 113, 200, 110, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 1.5, 2, 13, 108001, 1, 13, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.031, 114, 160, 110, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 2.5, 1, 14, 110301, 1, 14, 0, 108);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.036, 115, 130, 110, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 0.5, 1, 15, 107701, 0, 15, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.048, 116, 350, 110, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 3.5, 2, 16, 113101, 0, 16, 1, 95);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.043, 117, 130, 105, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 1.5, 3, 17, 113301, 0, 17, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.018, 118, 130, 105, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 1.5, 2, 18, 113201, 0, 18, 0, 95);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.021, 119, 160, 110, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 2.5, 2, 19, 117001, 1, 19, 0, 108);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.036, 120, 255, 110, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 2.5, 1, 20, 113501, 0, 20, 1, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.02, 121, 350, 105, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 2.5, 3, 21, 113401, 0, 21, 1, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.018, 122, 130, 110, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 2.5, 1, 22, 110101, 0, 22, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.03, 123, 160, 105, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 3.5, 3, 23, 110001, 1, 23, 0, 111);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.027, 124, 130, 110, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 1.5, 1, 24, 117201, 0, 24, 0, 103);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.02, 125, 160, 105, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 3.5, 2, 25, 117301, 1, 25, 0, 108);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.04, 126, 350, 110, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 2.5, 3, 26, 117401, 0, 26, 1, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.009, 127, 350, 110, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 3.5, 1, 27, 117101, 0, 27, 1, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.022, 128, 255, 120, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 3.5, 1, 28, 122901, 0, 28, 1, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (1, 0.028, 129, 130, 110, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 2.5, 3, 29, 122801, 0, 29, 0, 105);
INSERT INTO `v1_15d3a875523b226ff12aedfc55f67cb595840fde0902c28fd3272a6b37abeb46` VALUES (0, 0.023, 130, 130, 105, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 3.5, 2, 30, 122701, 1, 30, 0, 108);
