INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "ペコリーヌの満腹ツインおにぎり", 10, 1, 0, 1, 52001, "500マイルを獲得する。", 51001, 2, 2, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", "ペコリーヌの\n満腹ツインおにぎり", 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, "コッコロのゆるりと休憩サラダ", 10, 1, 0, 3, 52002, "500マイルを獲得する。", 51002, 2, 30001, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", "コッコロの\nゆるりと休憩サラダ", 1, "次に振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "キャルの絶品五つ星バーガー", 10, 1, 0, 2, 52003, "平凡な未鑑定トレジャーを1個獲得する。", 51003, 1, 20004, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", "キャルの\n絶品五つ星バーガー", 1, "次に振るダイスの目が必ず「5」になる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "リリのコツコツまい進かしわ餅", 10, 1, 0, 2, 52004, "800マイルを獲得する。", 51004, 4, 20001, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", "リリのコツコツ\nまい進かしわ餅", 3, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "クリアの彩り華やかバラエティ弁当", 11, 3, 0, 4, 52005, "次にダイスを振った際、1マス多く進める。", 51005, 1, 1, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", "クリアの彩り華やか\nバラエティ弁当", 0, "ランダムなイベントを発生させる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "プレシアの食いしん坊ぶーぶー肉まん", 10, 1, 0, 2, 52006, "800マイルを獲得する。", 51006, 4, 20002, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", "プレシアの食いしん坊\nぶーぶー肉まん", 1, "次に振るダイスの目が「4・5・6」のみになる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "カヤとイノリの食欲倍増ケバブサンド", 10, 1, 0, 7, 52007, "福引券を1枚獲得する。", 51007, 3, 1, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", "カヤとイノリの\n食欲倍増ケバブサンド", 2, "次に振るダイスの目を必ず「3」に、その次に振るダイスの目を必ず「6」にする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, "タマキの金運招くレインボーたい焼き", 10, 2, 1, 5, 52008, "500マイルを獲得する。", 51008, 2, 200, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", "タマキの金運招く\nレインボーたい焼き", 0, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (1, 2, "クローチェのフルパワーポテトサラダ", 10, 1, 0, 7, 52009, "平凡な未鑑定トレジャーを1個獲得する。", 51009, 1, 1, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", "クローチェの\nフルパワーポテトサラダ", 2, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「8」に、その次に振るダイスの目を必ず「1」にする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "アヤネとクルミの青空ピクニックセット", 10, 2, 2, 6, 52010, "500マイルを獲得する。", 51010, 2, 1, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", "アヤネとクルミの\n青空ピクニックセット", 0, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "リマの不思議なスイートアップル", 10, 1, 0, 7, 52011, "福引券を1枚獲得する。", 51011, 3, 1, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", "リマの不思議な\nスイートアップル", 2, "次に振るダイスの目を必ず「2」に、その次に振るダイスの目を必ず「7」にする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, "ニノンのオリジナル一二三寿司", 10, 1, 0, 3, 52012, "平凡な未鑑定トレジャーを1個獲得する。", 51012, 1, 30002, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", "ニノンのオリジナル\n一二三寿司", 1, "次に振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "クレジッタの開運お菓子盛り合わせ", 10, 3, 0, 8, 52013, "800マイルを獲得する。", 51013, 4, 2, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", "クレジッタの開運\nお菓子盛り合わせ", 0, "RANK2のマイルショップを開く。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, "リノの「時は金時」かき氷", 11, 1, 0, 9, 52014, "次にダイスを振った際、1マス多く進める。", 51014, 1, 1, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "リノの\n「時は金時」かき氷", 2, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "シズルの愛情盛り盛りハンバーグ", 11, 1, 0, 1, 52015, "次にダイスを振った際、1マス多く進める。", 51015, 1, 2, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", "シズルの愛情\n盛り盛りハンバーグ", 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "アオイの疾走サンドイッチ", 0, 1, 0, 2, 52016, "なし", 51016, 0, 20005, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", "アオイの疾走\nサンドイッチ", 1, "次に振るダイスの目が必ず「6」になる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (1, 3, "クウカの緊縛触手活け造り", 10, 1, 0, 2, 52017, "なし", 51017, 5, 1, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", "クウカの緊縛\n触手活け造り", 2, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "ランファの世話焼き三昧肉じゃが", 10, 1, 0, 2, 52018, "1000マイルを獲得する。", 51018, 6, 20006, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", "ランファの世話焼き三昧肉じゃが", 1, "次に振るダイスの目が必ず「3」になる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "ワカナの節約腹八分目カレー", 0, 1, 0, 2, 52019, "なし", 0, 0, 20007, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", "ワカナの節約腹八分目カレー", 1, "次に振るダイスの目が必ず「8」になる。", 500);
