INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "アキノのゲヘニアンチキンプレート", 0, 1, 0, 2, 52022, "なし", 51022, 0, 20008, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", "アキノのゲヘニアン\nチキンプレート", 3, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (1, 1, "ミミのコロコロお手玉おにぎり", 10, 1, 0, 7, 52023, "平凡な未鑑定トレジャーを1個獲得する。", 51023, 1, 1, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", "ミミのコロコロ\nお手玉おにぎり", 2, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「6」に、その次に振るダイスの目を必ず「3」にする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "アユミの隠密ガトーインビジブル", 11, 3, 0, 8, 52024, "次にダイスを振った際、3マス多く進める。", 51024, 3, 2, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", "アユミの隠密\nガトーインビジブル", 0, "RANK2のマイルショップを開く。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "アネモネの花爛漫ステーキサラダ", 0, 2, 0, 9, 52025, "なし", 51025, 0, 3, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", "アネモネの\n花爛漫ステーキサラダ", 1, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "アンナの煉獄蕃茄煮混沌卵", 10, 3, 0, 4, 52026, "福引券を2枚獲得する。", 51026, 8, 1, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", "アンナの\n煉獄蕃茄煮混沌卵", 0, "ランダムなイベントを発生させる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "ユイの栄養満点まごころ弁当", 0, 1, 0, 2, 52027, "なし", 51027, 0, 20004, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", "ユイの\n栄養満点まごころ弁当", 2, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "リンのナッツ入りぐうたらあんパン", 10, 1, 0, 2, 52028, "なし", 51028, 9, 30003, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", "リンの\nナッツ入りぐうたらあんパン", 1, "次に振るダイスの目が「1・2」のみになる\n代わりに、その場で福引券を3枚獲得する。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, "シェフィの氷晶煌めく手作りアイス", 0, 1, 0, 3, 52029, "なし", 51029, 0, 30004, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", "シェフィの\n氷晶煌めく手作りアイス", 1, "次に振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, "ミソラの気まぐれ味噌ラーメン★", 0, 3, 0, 12, 52030, "なし", 51030, 0, 1, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", "ミソラの\n気まぐれ味噌ラーメン★", 3, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "イロハの最強クワガタゼリー（人間用）", 0, 1, 0, 2, 52031, "なし", 51031, 0, 20009, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", "イロハの\n最強クワガタゼリー（人間用）", 1, "次に振るダイスの目が「1・6・6・6・8・8」になる。", 500);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, "グレイスの目ん玉飛び出るカップケーキ", 11, 2, 0, 9, 52032, "次にダイスを振った際、3マス多く進める。", 51032, 3, 4, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", "グレイスの\n目ん玉飛び出るカップケーキ", 2, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 500);
