INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "リンのナッツ入りぐうたらあんパン", "リンの\nナッツ入りぐうたらあんパン", 2, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 9, 0, "次に振るダイスの目が「1・2」のみになる\n代わりに、その場で福引券を3枚獲得する。", 10, "なし", 3, 52028, 1, 1, 30003, 0, 51028);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "シェフィの氷晶煌めく手作りアイス", "シェフィの\n氷晶煌めく手作りアイス", 3, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 0, 0, "次に振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 0, "なし", 2, 52029, 1, 1, 30004, 0, 51029);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "ミソラの気まぐれ味噌ラーメン★", "ミソラの\n気まぐれ味噌ラーメン★", 12, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 0, 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 0, "なし", 1, 52030, 3, 3, 1, 0, 51030);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "イロハの最強クワガタゼリー（人間用）", "イロハの\n最強クワガタゼリー（人間用）", 2, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 0, 0, "次に振るダイスの目が「1・6・6・6・8・8」になる。", 0, "なし", 3, 52031, 1, 1, 20009, 0, 51031);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "グレイスの目ん玉飛び出るカップケーキ", "グレイスの\n目ん玉飛び出るカップケーキ", 9, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 3, 0, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 11, "次にダイスを振った際、3マス多く進める。", 3, 52032, 2, 2, 4, 0, 51032);
