INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, 500, "ペコリーヌの\n満腹ツインおにぎり", 52001, 1, 0, "500マイルを獲得する。", "ペコリーヌの満腹ツインおにぎり", 2, 1, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 2, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 10, 1, 51001);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, 500, "コッコロの\nゆるりと休憩サラダ", 52002, 1, 0, "500マイルを獲得する。", "コッコロのゆるりと休憩サラダ", 30001, 1, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", 2, "次に振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 10, 2, 51002);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "キャルの\n絶品五つ星バーガー", 52003, 1, 0, "平凡な未鑑定トレジャーを1個獲得する。", "キャルの絶品五つ星バーガー", 20004, 1, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", 1, "次に振るダイスの目が必ず「5」になる。", 10, 1, 51003);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "リリのコツコツ\nまい進かしわ餅", 52004, 3, 0, "800マイルを獲得する。", "リリのコツコツまい進かしわ餅", 20001, 1, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", 4, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 10, 3, 51004);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 4, 500, "クリアの彩り華やか\nバラエティ弁当", 52005, 0, 0, "次にダイスを振った際、1マス多く進める。", "クリアの彩り華やかバラエティ弁当", 1, 3, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", 1, "ランダムなイベントを発生させる。", 11, 3, 51005);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "プレシアの食いしん坊\nぶーぶー肉まん", 52006, 1, 0, "800マイルを獲得する。", "プレシアの食いしん坊ぶーぶー肉まん", 20002, 1, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", 4, "次に振るダイスの目が「4・5・6」のみになる。", 10, 1, 51006);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 7, 500, "カヤとイノリの\n食欲倍増ケバブサンド", 52007, 2, 0, "福引券を1枚獲得する。", "カヤとイノリの食欲倍増ケバブサンド", 1, 1, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", 3, "次に振るダイスの目を必ず「3」に、その次に振るダイスの目を必ず「6」にする。", 10, 1, 51007);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 5, 500, "タマキの金運招く\nレインボーたい焼き", 52008, 0, 1, "500マイルを獲得する。", "タマキの金運招くレインボーたい焼き", 200, 2, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", 2, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 10, 2, 51008);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (1, 7, 500, "クローチェの\nフルパワーポテトサラダ", 52009, 2, 0, "平凡な未鑑定トレジャーを1個獲得する。", "クローチェのフルパワーポテトサラダ", 1, 1, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", 1, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「8」に、その次に振るダイスの目を必ず「1」にする。", 10, 2, 51009);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 6, 500, "アヤネとクルミの\n青空ピクニックセット", 52010, 0, 2, "500マイルを獲得する。", "アヤネとクルミの青空ピクニックセット", 1, 2, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", 2, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 10, 3, 51010);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 7, 500, "リマの不思議な\nスイートアップル", 52011, 2, 0, "福引券を1枚獲得する。", "リマの不思議なスイートアップル", 1, 1, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", 3, "次に振るダイスの目を必ず「2」に、その次に振るダイスの目を必ず「7」にする。", 10, 3, 51011);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, 500, "ニノンのオリジナル\n一二三寿司", 52012, 1, 0, "平凡な未鑑定トレジャーを1個獲得する。", "ニノンのオリジナル一二三寿司", 30002, 1, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", 1, "次に振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 10, 2, 51012);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 8, 500, "クレジッタの開運\nお菓子盛り合わせ", 52013, 0, 0, "800マイルを獲得する。", "クレジッタの開運お菓子盛り合わせ", 2, 3, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", 4, "RANK2のマイルショップを開く。", 10, 3, 51013);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 9, 500, "リノの\n「時は金時」かき氷", 52014, 2, 0, "次にダイスを振った際、1マス多く進める。", "リノの「時は金時」かき氷", 1, 1, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 11, 2, 51014);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 1, 500, "シズルの愛情\n盛り盛りハンバーグ", 52015, 1, 0, "次にダイスを振った際、1マス多く進める。", "シズルの愛情盛り盛りハンバーグ", 2, 1, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 11, 1, 51015);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "アオイの疾走\nサンドイッチ", 52016, 1, 0, "なし", "アオイの疾走サンドイッチ", 20005, 1, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 0, "次に振るダイスの目が必ず「6」になる。", 0, 3, 51016);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (1, 2, 500, "クウカの緊縛\n触手活け造り", 52017, 2, 0, "なし", "クウカの緊縛触手活け造り", 1, 1, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 5, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 10, 3, 51017);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "ランファの世話焼き三昧肉じゃが", 52018, 1, 0, "1000マイルを獲得する。", "ランファの世話焼き三昧肉じゃが", 20006, 1, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 6, "次に振るダイスの目が必ず「3」になる。", 10, 1, 51018);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "ワカナの節約腹八分目カレー", 52019, 1, 0, "なし", "ワカナの節約腹八分目カレー", 20007, 1, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 0, "次に振るダイスの目が必ず「8」になる。", 0, 1, 0);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 9, 500, "リノの\n「時は金時」かき氷", 52020, 2, 0, "次にダイスを振った際、1マス多く進める。", "リノの「時は金時」かき氷", 1, 2, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 11, 2, 51014);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 9, 500, "カスミの真実見抜く\nボンボン・ショコラ", 52021, 3, 0, "	平凡な未鑑定トレジャーを2個獲得する。", "カスミの真実見抜くボンボン・ショコラ", 2, 2, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", 7, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 10, 3, 51021);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "アキノのゲヘニアン\nチキンプレート", 52022, 3, 0, "なし", "アキノのゲヘニアンチキンプレート", 20008, 1, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", 0, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 0, 1, 51022);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (1, 7, 500, "ミミのコロコロ\nお手玉おにぎり", 52023, 2, 0, "平凡な未鑑定トレジャーを1個獲得する。", "ミミのコロコロお手玉おにぎり", 1, 1, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 1, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「6」に、その次に振るダイスの目を必ず「3」にする。", 10, 1, 51023);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 8, 500, "アユミの隠密\nガトーインビジブル", 52024, 0, 0, "次にダイスを振った際、3マス多く進める。", "アユミの隠密ガトーインビジブル", 2, 3, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 3, "RANK2のマイルショップを開く。", 11, 3, 51024);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 9, 500, "アネモネの\n花爛漫ステーキサラダ", 52025, 1, 0, "なし", "アネモネの花爛漫ステーキサラダ", 3, 2, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 0, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 0, 3, 51025);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 4, 500, "アンナの\n煉獄蕃茄煮混沌卵", 52026, 0, 0, "福引券を2枚獲得する。", "アンナの煉獄蕃茄煮混沌卵", 1, 3, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 8, "ランダムなイベントを発生させる。", 10, 1, 51026);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "ユイの\n栄養満点まごころ弁当", 52027, 2, 0, "なし", "ユイの栄養満点まごころ弁当", 20004, 1, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", 0, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 0, 3, 51027);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "リンの\nナッツ入りぐうたらあんパン", 52028, 1, 0, "なし", "リンのナッツ入りぐうたらあんパン", 30003, 1, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 9, "次に振るダイスの目が「1・2」のみになる\n代わりに、その場で福引券を3枚獲得する。", 10, 3, 51028);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 3, 500, "シェフィの\n氷晶煌めく手作りアイス", 52029, 1, 0, "なし", "シェフィの氷晶煌めく手作りアイス", 30004, 1, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 0, "次に振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 0, 2, 51029);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 12, 500, "ミソラの\n気まぐれ味噌ラーメン★", 52030, 3, 0, "なし", "ミソラの気まぐれ味噌ラーメン★", 1, 3, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 0, 1, 51030);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 2, 500, "イロハの\n最強クワガタゼリー（人間用）", 52031, 1, 0, "なし", "イロハの最強クワガタゼリー（人間用）", 20009, 1, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 0, "次に振るダイスの目が「1・6・6・6・8・8」になる。", 0, 3, 51031);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (0, 9, 500, "グレイスの\n目ん玉飛び出るカップケーキ", 52032, 2, 0, "次にダイスを振った際、3マス多く進める。", "グレイスの目ん玉飛び出るカップケーキ", 4, 2, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 3, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 11, 3, 51032);
