INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "なし", 1, 52028, 30003, 0, 9, "リンのナッツ入りぐうたらあんパン", 10, "リンの\nナッツ入りぐうたらあんパン", 1, 51028, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 0, "次に振るダイスの目が「1・2」のみになる\n代わりに、その場で福引券を3枚獲得する。", 2, 3);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "なし", 1, 52029, 30004, 0, 0, "シェフィの氷晶煌めく手作りアイス", 0, "シェフィの\n氷晶煌めく手作りアイス", 1, 51029, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 0, "次に振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 3, 2);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "なし", 3, 52030, 1, 0, 0, "ミソラの気まぐれ味噌ラーメン★", 0, "ミソラの\n気まぐれ味噌ラーメン★", 3, 51030, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 12, 1);
INSERT INTO `v1_18e9d6f4121de8280dbbca098fc7e9c9569823275adc2a2b32042c2eae280b5b` VALUES (500, "なし", 1, 52031, 20009, 0, 0, "イロハの最強クワガタゼリー（人間用）", 0, "イロハの\n最強クワガタゼリー（人間用）", 1, 51031, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 0, "次に振るダイスの目が「1・6・6・6・8・8」になる。", 2, 3);
