INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 410, 1, 270, 0, 11, "サンドワーム", 250500, 0, 1.3, 250500, 0, "【物理】【遠距離】\n砂地の地中で獲物を待ち、\n一気に大顎で仕留めるムカデの魔物\n\n・後ろから１番目に移動して近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン");
