INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 25, 1, 270, 0, 16, "ブルーゴーレム", 250800, 0, 5.0, 250800, 0, "【物理】【魔法防御】\n魔力を秘めた岩から生まれた、\n魔法耐性のあるゴーレム。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン");
