INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 85, 1, 270, 0, 15, "ゴブリンガード", 251200, 0, 4.0, 251200, 0, "【物理】【物理防御】\n身の丈ほどもある、巨大な盾で\n仲間を護る見習いゴブリン兵。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン");
