INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 50, 1, 270, 0, 0, "ワイルドボア", 251400, 0, 4.0, 251400, 0, "【物理】【防御】【妨害】\n熱地に生きる、原始の大イノシシ。\n数多のマグマ岩を突き続けた先に、\n鎧をも砕く牙の一撃を体得した。\n\n・近接攻撃、物防ダウン\n・与えるダメージによるＴＰ回復量80％ダウン");
