INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 365, 1, 270, 0, 0, "エンゼルダーツ", 253600, 0, 10.0, 253600, 0, "【物理】【補助】\n熱帯魚のカタチをした魚の魔物。その優雅な姿\nとは裏腹に、鋭いアゴを獲物めがけて突き立てる。\n\n・近接攻撃、自身行動速度アップ\n・与えるダメージによるＴＰ回復量80％ダウン");
