INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "ウェアオルカ", 301900, 0, 1.7, 301900, 0, "【物理】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。\n\n・前方4キャラに物理中ダメージを\n　与え、物理攻撃力を下げる。\n\n・前方2キャラに魔法小ダメージを\n　与え、ＴＰを下げる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "キリングオルカ", 301901, 0, 1.5, 301901, 0, "【物理】ルナの魔力により凶悪化した大海の狩人。\n鍛え抜いた躰で、刃と化した水流の一撃を放つ。\n\n・前方４キャラに物理大ダメージを与え、\n　物理攻撃力を中ダウンさせる。さらに、\n　味方全員のＴＰ上昇を小アップする。\n・前方小範囲に魔法中ダメージを与え、\n　ＴＰを大ダウン。\n・後ろから２番目のキャラを中心にした\n　小範囲に物理中ダメージを与え、\n　魔法攻撃力を大ダウンさせる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "ワイルドグリフォン", 302000, 0, 1.6, 302000, 0, "【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n【特殊効果】\n・飛行効果無視\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・前から敵５体に魔法ダメージ\n・前から敵５体をノックバック（必中）\n・前から敵５体にスタン（必中）");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "ワイルドグリフォン", 302001, 0, 1.6, 302001, 0, "過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "ワイルドグリフォン", 302002, 0, 1.6, 302002, 0, "【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n【特殊効果】\n・飛行効果無視\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・前から敵５体に魔法ダメージ\n・前から敵５体をノックバック（必中）\n・前から敵５体にスタン（必中）\n【スキル１】\n・前から敵５体に魔法ダメージ\n・前から敵５体に魔法防御力ダウン（必中）\n・前から敵５体に物理／魔法クリティカルダメー\n　ジダウン（必中）");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "暴風の鷲獅子", 302003, 0, 1.6, 302003, 0, "【物理】苛烈な環境で生き抜いた、\n獰猛な鷲獅子。剛翼から放つ強風\nは、冒険者を容易に吹き飛ばす。\n\n・ユニオンバーストは、前から\n　３キャラに魔法中ダメージを\n　与え、さらにノックバック\n　させる。キャラのＨＰが70％\n　未満の場合、スタン状態を\n　付与する。\n　このスキルの状態異常は\n　ダメージを無効化しても\n　付与される。\n\n・前から３キャラに魔法中ダメージ\n　を与え、魔法防御力を小ダウン\n　させる。このスキルの状態異常は\n　ダメージを無効化しても\n　付与される。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "ワイルドグリフォン", 302004, 0, 1.6, 302004, 0, "【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から敵３体に魔法ダメージ（召喚体無視）\n・前から敵３体をノックバック（召喚体無視・必\n　中）\n・対象のＨＰ70％未満：スタン（必中）\n【スキル１】\n・前から敵３体に魔法ダメージ（召喚体無視）\n・前から敵３体に魔法防御力ダウン（召喚体無視・\n　必中）\n【通常攻撃】\n・前から敵１体に物理ダメージ");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "ワイルドグリフォン", 302005, 0, 1.6, 302005, 0, "【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・飛行状態の敵を攻撃対象にできる。\n・ワイルドグリフォンから受けるすべてのダメー\n　ジでは、パーティキャラのＴＰが回復しない。\n・ワイルドグリフォンから受けるすべての状態異\n　常とステータスダウン効果は、ダメージを無効\n　化しても付与される。\n・ユニオンバーストは、最も近い敵５キャラに魔\n　法大ダメージを与え、ノックバックさせ、スタ\n　ン状態を付与する。\n・最も近い敵５キャラに魔法中ダメージを与え、\n　魔法防御力と物理クリティカルダメージと魔法\n　クリティカルダメージをバトル終了まで小ダウ\n　ンさせる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 11, "ワイバーン", 302100, 0, 1.6, 302100, 0, "【物理】大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ワイバーンから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした広範囲内の敵最大３キャラに物理大ダ\n　メージを与える。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 11, "ワイバーン", 302101, 0, 1.6, 302101, 0, "【物理】大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を大アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン50％カット\n　効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を小ダウンさせる。\n・ワイバーンから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を特大ダウ\n　ンさせる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした広範囲内の敵最大３キャラに物理大ダ\n　メージを与え、物理防御力を中ダウンさせる。\n　さらに自身の物理防御力をバトル終了まで小\n　アップさせる。このスキルの効果はダメージを\n　無効化しても付与される。\n・最も近い敵３キャラをノックバックさせ、行動\n　速度を中ダウンさせる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "ワイバーン", 302102, 0, 1.6, 302102, 0, "【物理】大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・前から１番目の敵を中心にした小範囲の\n　最大３キャラに物理中ダメージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "サンドワイバーン", 302103, 0, 1.6, 302103, 0, "【物理】砂塵舞う空を縄張りにする\n翼竜。厳しい環境でも獲物を逃さず\n荒野の空を支配する。\n\n・前から１番目の敵を中心にした小\n　範囲の最大３キャラに魔法中ダメ\n　ージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 11, "サンドワイバーン", 302103, 0, 1.6, 302104, 0, "【物理】砂塵舞う空を縄張りにする\n翼竜。厳しい環境でも獲物を逃さず\n荒野の空を支配する。\n\n・前から１番目の敵を中心にした小\n　範囲の最大３キャラに魔法中ダメ\n　ージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 11, "ワイバーン", 302105, 0, 1.6, 302105, 0, "【物理】大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ワイバーンから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・ユニオンバーストは、前から３キャラに物理中\n　ダメージを与え、物理防御力を特大ダウンさせ\n　る。\n　さらに自身の物理防御力を小アップさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から１キャラをノックバックさせ、行動速度\n　を中ダウンさせる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 11, "ワイバーン", 302106, 0, 1.6, 302106, 0, "【物理】大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・前から１番目の敵と周囲の敵３体までに物理ダ\n　メージ\n・前から１番目の敵と周囲の敵３体までに物理防\n　御力ダウン（必中）\n・自身の物理防御力アップ\n【スキル１】\n・前から敵３体をノックバック\n・前から敵３体に行動速度ダウン\n【通常攻撃】\n・前から敵１体に物理ダメージ");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 2, 270, 0, 16, "イクト・ガラディオン", 302200, 0, 1.6, 302200, 0, "【魔法】ルナの魔力で目覚めた、古代の機械兵器。\n無慈悲に放たれる魔力が、侵入者を阻む。\n\n・敵全体に魔法特大ダメージを与え、魔法防御力\n　を大ダウン。\n・前から３キャラに物理大ダメージを与える。\n・前から２キャラに魔法大ダメージを\n　与え、麻痺状態にする。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 2, 270, 0, 16, "ワイズオウル", 302300, 0, 1.8, 302300, 0, "【魔法】大樹の頂に棲まう、聡明なる大梟。\n森に生ける者の英知を束ね、巧みに魔術を操る。\n\n・ユニオンバーストは敵全体に魔法ダメージを\n　与える。\n・ランダムな1キャラに物理ダメージを与え\n　さらに麻痺状態にする。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "アークガーディアン", 302400, 0, 1.6, 302400, 0, "【物理】太古より遺跡を護り続ける人工生命体。\n創造主の秘術で、無限に動力を得て動き続ける。\n\n・ユニオンバーストは敵全体に魔法大ダメージを\n　与える。\n・前方範囲内に物理ダメージを与える。\n・敵全体に魔法ダメージを与え、さらに沈黙させ\n　る。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "キマイラ", 302500, 0, 1.4, 302500, 0, "【物理】孤塔の頂に座する、暴虐なる混成魔獣。\n千変万化の猛攻は、挑む者数多を屠ってきた。\n\n・ユニオンバーストは前方範囲内に物理大\n　ダメージを与え、さらにノックバックさせる。\n・前方3キャラに物理ダメージを与え、さらに\n　火傷状態にする。\n・敵全体に魔法ダメージを与え、さらに行動速度\n　物理防御力、魔法防御力をダウン。\n・残り時間が10秒を切ると、敵全体に魔法極大\n　ダメージを与え、自身のTPを特大ダウン。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "キマイラ", 302501, 0, 1.4, 302501, 0, "【物理】孤塔の頂に座する、暴虐なる混成魔獣。\n千変万化の猛攻は、挑む者数多を屠ってきた。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前方範囲に物理ダメージ\n・敵全体に物理ダメージ\n・敵全体をノックバック（必中）\n【スキル１】\n・前方範囲に物理ダメージ\n・前方範囲に火傷（必中）\n【スキル２】\n・前方範囲に魔法ダメージ\n・前方範囲に行動速度ダウン（必中）\n・前方範囲に物理／魔法防御力ダウン（必中）\n【スキル３】\n・敵全体に魔法割合ダメージ（飛行効果無視）\n・自身の魔法攻撃力が高いほど：ダメージアップ\n・自身にＴＰダウン\n【ＨＰ40％未満時発動】\n・自身に物理攻撃力アップ\n・自身に行動速度アップ\n・行動パターン変化");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "ツインピッグス", 302600, 0, 1.6, 302600, 0, "【物理】さらなる力を得た双子座の魔星獣。\n前衛で防御しつつ、熟達した技で攻撃を行う。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・敵全体に物理／魔法攻撃力ダウン（必中）\n【スキル１】\n・後方範囲の敵３体に物理ダメージ\n・後方範囲の敵３体にスタン（必中）\n【スキル２】\n・後方範囲の敵３体に物理ダメージ\n・後方範囲の敵３体にＴＰダウン（必中）\n・左側のツインピッグスが前方へ移動する");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "ツインピッグス", 302601, 0, 1.6, 302601, 0, "【物理】さらなる力を得た双子座の魔星獣。\n前衛で防御しつつ、熟達した技で攻撃を行う。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・敵全体に物理／魔法攻撃力ダウン（必中）\n【スキル１】\n・後方範囲の敵３体に物理ダメージ\n・後方範囲の敵３体にスタン（必中）\n【スキル２】\n・後方範囲の敵３体に物理ダメージ\n・後方範囲の敵３体にＴＰダウン（必中）\n・左側のツインピッグスが前方へ移動する\n【スキル３】\n・敵全体に物理ダメージ\n・自身にＴＰ回復\n・自身に行動速度アップ");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "ツインピッグス", 302602, 0, 1.6, 302602, 0, "【物理】さらなる力を得た双子座の魔星獣。\n前衛で防御しつつ、熟達した技で攻撃を行う。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・敵全体に物理／魔法攻撃力ダウン（必中） \n【スキル１】\n・後方範囲の敵３体に物理ダメージ\n・後方範囲の敵３体にスタン（必中） \n【スキル２】\n・後方範囲の敵３体に物理ダメージ\n・後方範囲の敵３体にＴＰダウン（必中）\n・左側のツインピッグスが前方へ移動する \n【スキル３】\n・敵全体に物理ダメージ\n・自身にＴＰ回復\n・自身に行動速度アップ \n【スキル４】\n・敵全体に重複可能の行動速度ダウン\n・敵全体に物理／魔法攻撃ダメージダウン");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "カルキノス", 302700, 0, 1.8, 302700, 0, "【物理】船墓の入江に巣食う、蟹座の魔星獣。\n巨大なハサミの一撃で、無数の船を沈める。\n\n・敵全体に魔法大ダメージを与え、物理防御力\n　をダウン。\n・前方範囲内の敵すべてに物理中ダメージを与\n　え、さらにスタン状態にする。\n・敵全体に物理中ダメージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "カルキノス", 302701, 0, 1.8, 302701, 0, "【物理】幾多の船を沈めた蟹座の魔星獣。\n大海の覇者は万物を断裁し、圧潰し、殲滅する。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に魔法固定ダメージ\n・自身の魔法攻撃力が高いほど：ダメージアップ\n・自身に魔法攻撃力アップ\n【スキル１】\n・前から１番目の敵と周囲に物理ダメージ\n【スキル２】\n・敵全体に物理ダメージ\n・敵全体に行動速度ダウン（必中）");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "カルキノス", 302702, 0, 1.8, 302702, 0, "【物理】幾多の船を沈めた蟹座の魔星獣。\n大海の覇者は万物を断裁し、圧潰し、殲滅する。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に魔法固定ダメージ\n・自身の魔法攻撃力が高いほど：ダメージアップ\n・自身に魔法攻撃力アップ\n【スキル１】\n・変身前：前から１番目の敵と周囲に物理ダメージ\n・変身後：前から１番目の敵と周囲に魔法ダメージ\n【スキル２】\n・変身前：敵全体に物理ダメージ\n・変身後：敵全体に魔法ダメージ\n・敵全体に重複可能の行動速度ダウン（必中）\n【スキル３】\n・変身して赤色になる\n・自身に魔法攻撃力アップ\n・自身に行動速度アップ\n【通常攻撃】\n・変身前：前から敵１体に物理ダメージ\n・変身後：前から敵１体に魔法ダメージ");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "カルキノス", 302703, 0, 1.8, 302703, 0, "【物理】幾多の船を沈めた蟹座の魔星獣。\n大海の覇者は万物を断裁し、圧潰し、殲滅する。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体にバリア解除\n・敵全体に魔法固定ダメージ\n・自身の魔法攻撃力が高いほど：ダメージアップ\n・自身に魔法攻撃力アップ\n【スキル１】\n・変身前：前から１番目の敵と周囲に物理ダメージ\n・変身後：前から１番目の敵と周囲に魔法ダメージ\n【スキル２】\n・変身前：敵全体に物理ダメージ\n・変身後：敵全体に魔法ダメージ\n・敵全体に重複可能の行動速度ダウン（必中）\n【スキル３】\n・変身して金色になる\n・自身に魔法攻撃力アップ\n・自身に行動速度アップ\n【通常攻撃】\n・変身前：前から敵１体に物理ダメージ\n・変身後：前から敵１体に魔法ダメージ\n・前から敵１体に物理／魔法攻撃ダメージダウン\n　（必中）");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "オルレオン", 302800, 0, 1.8, 302800, 0, "サバンナの覇者と名高い、獅子座の魔星獣。\n刃向かう者は、灼熱の咆哮で焼き尽くす。\n\n・敵全体に、最大ＨＰに応じた防御力無視の\n　物理大ダメージを与え、残りＨＰの少ない\n　敵はさらに火傷状態にする。\n・敵1キャラに物理大ダメージを与え、火傷\n　状態の場合はさらに追加ダメージ。\n・前方範囲内の敵に物理中ダメージを与え、\n　火傷状態の場合はさらに追加ダメージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "オルレオン", 302801, 0, 3.0, 302801, 0, "【物理】荒涼たる原野に君臨する獅子座の魔星獣。\n鋭爪で大気を切裂き、業火の咆哮が大地を焦がす。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に物理ダメージ\n・敵全体に火傷（必中）\n・対象のＨＰ70％未満：火傷時間アップ\n【スキル１】\n・前から敵１体に物理ダメージ\n・対象が火傷：物理固定ダメージ追加\n【スキル２】\n・前方範囲に物理ダメージ\n・対象が火傷：物理固定ダメージ追加");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "オルレオン", 302802, 0, 3.0, 302802, 0, "【物理】荒涼たる原野に君臨する獅子座の魔星獣。\n鋭爪で大気を切裂き、業火の咆哮が大地を焦がす。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n【ユニオンバースト】\n・敵全体に物理ダメージ\n・敵全体に火傷（必中）\n・対象のＨＰ70％未満：火傷時間アップ\n【スキル１】\n・前から敵１体に物理ダメージ\n・対象が火傷：物理固定ダメージ追加\n【スキル２】\n・前方範囲に物理ダメージ\n・対象が火傷：物理固定ダメージ追加\n【スキル３】\n・敵全体に魔法割合ダメージ\n・敵全体にスタン（必中）\n・対象のＨＰ70％未満：スタン時間アップ");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "オルレオン", 302803, 0, 3.0, 302803, 0, "【物理】荒涼たる原野に君臨する獅子座の魔星獣。\n鋭爪で大気を切裂き、業火の咆哮が大地を焦がす。\n\n【特殊効果】\n・自身に物理／魔法防御力アップ（ダウン不可）\n・自身にＴＰダウン無効\n・敵全体にＴＰ上昇ダウン\n・敵全体にＨＰ吸収ダウン\n【ユニオンバースト】\n・敵全体に物理ダメージ\n・敵全体に火傷（必中）\n・対象のＨＰ70％未満：火傷時間アップ\n【スキル１】\n・前から敵１体に物理ダメージ\n・対象が火傷：物理固定ダメージ追加\n【スキル２】\n・前方範囲に物理ダメージ\n・対象が火傷：物理固定ダメージ追加\n【スキル３】\n・敵全体に魔法割合ダメージ\n・敵全体にスタン（必中）\n・対象のＨＰ70％未満：スタン時間アップ\n【スキル４】\n・前から３番目の敵を中心に魔法ダメージフィー\n　ルド（挑発無視）\n・前から３番目の敵を中心に物理／魔法攻撃力ダ\n　ウンフィールド（挑発無視）");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "アクアリオス", 302900, 0, 1.8, 302900, 0, "神瓶に宿る魔力に取込まれた、水瓶座の魔星獣。\n強大な力に溺れ、大海を支配せんと暴れまわる。\n\n・ユニオンバーストは、敵全体に魔法小ダメー\n　ジを計６回与える。\n　キャラのＨＰが８０％を切るとノックバック\n　させ、４０％を切るとさらに追加でノックバ\n　ックとスタン状態を付与。\n・目の前の敵３キャラに物理中ダメージ。\n・自身のステータスダウン効果と、敵全体のス\n　テータスアップ効果を打消し、自身のＴＰを\n　アップ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "アクアリオス", 302901, 0, 1.8, 302901, 0, "【物理】神瓶に宿る魔力を操る水瓶座の魔星獣。\n激流は全てを飲込み蒼海の支配者として君臨する。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・アクアリオスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、ノックバックさせる。このスキルによ\n　るダメージでは、ＴＰが回復しない。このスキ\n　ルの状態異常はダメージを無効化しても付与さ\n　れる。\n・自身の物理防御力と魔法防御力を小アップさせ、\n　ＴＰを小回復する。さらに敵全体の物理防御力\n　と魔法防御力をバトル終了まで小ダウンさせる。\n・最も近い敵３キャラに物理大ダメージを与える。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "アクアリオス", 302902, 0, 1.8, 302902, 0, "【物理】神瓶に宿る魔力を操る水瓶座の魔星獣。\n激流は全てを飲込み蒼海の支配者として君臨する。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・アクアリオスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、ノックバックさせる。このスキルによ\n　るダメージでは、ＴＰが回復しない。このスキ\n　ルの状態異常はダメージを無効化しても付与さ\n　れる。\n・自身の物理防御力と魔法防御力を中アップさせ、\n　ＴＰを小回復する。さらに敵全体の物理防御力\n　と魔法防御力をバトル終了まで中ダウンさせる。\n・最も近い敵３キャラに物理大ダメージを与える。\n\n【バトルフィールド効果】\n一定時間が経過するごとに、激流が発生する。\n・敵全体に魔法大ダメージを与え、物理防御力と\n　魔法防御力アップ効果を解除する。さらに、ア\n　クアリオスにかかっている物理防御力と魔法防\n　御力ダウン効果を解除する。このスキルによる\n　ダメージでは、ＴＰが回復しない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "アクアリオス", 302903, 0, 1.8, 302903, 0, "【物理】神瓶に宿る魔力を操る水瓶座の魔星獣。\n激流は全てを飲込み蒼海の支配者として君臨する。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・アクアリオスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、ノックバックさせる。このスキルによ\n　るダメージでは、ＴＰが回復しない。このスキ\n　ルの状態異常はダメージを無効化しても付与さ\n　れる。\n・自身の物理防御力と魔法防御力を中アップさせ、\n　ＴＰを小回復する。さらに敵全体の物理防御力\n　と魔法防御力をバトル終了まで中ダウンさせる。\n・最も近い敵３キャラに物理大ダメージを与える。\n\n【バトルフィールド効果】\n一定時間が経過するごとに、激流が発生する。\n・敵全体に魔法大ダメージを与え、物理防御力と\n　魔法防御力アップ効果を解除する。さらに、ア\n　クアリオスにかかっている物理防御力と魔法防\n　御力ダウン効果を解除する。このスキルによる\n　ダメージでは、ＴＰが回復しない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "アクアリオス", 302904, 0, 1.8, 302904, 0, "【物理】神瓶に宿る魔力を操る水瓶座の魔星獣。\n激流は全てを飲込み蒼海の支配者として君臨する。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を中ダウンさせる。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・アクアリオスから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を特大ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体に魔法中ダメージ\n　を与え、ノックバックさせる。このスキルによ\n　るダメージでは、ＴＰが回復しない。このスキ\n　ルの状態異常はダメージを無効化しても付与さ\n　れる。\n・自身の物理防御力と魔法防御力を中アップさせ、\n　ＴＰを小回復する。さらに敵全体の物理防御力\n　と魔法防御力をバトル終了まで中ダウンさせる。\n・最も近い敵３キャラに物理大ダメージを与える。\n\n【バトルフィールド効果】\n一定時間が経過するごとに、激流が発生する。\n・敵全体に魔法大ダメージを与え、物理防御力と\n　魔法防御力アップ効果を解除する。さらに、ア\n　クアリオスにかかっている物理防御力と魔法防\n　御力ダウン効果を解除する。このスキルによる\n　ダメージでは、ＴＰが回復しない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "トルペドン", 303000, 0, 1.8, 303000, 0, "大海原を爆走し、鬼シケ呼び込む魚座の魔星獣。\n取巻く手下の赤ビレはカチコミ上等ヘッドの証。\n\n・物理攻撃力アップ・ダウン効果の影響を受け\n　やすい。\n・ユニオンバーストは、敵全体に物理大ダメー\n　ジを与え、自分の行動速度と物理攻撃力をア\n　ップ。\n・敵全体に物理小ダメージ。\n・前方２キャラに物理中ダメージを与え、物理\n　防御力を小ダウン。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "トルペドン", 303001, 0, 1.8, 303001, 0, "【物理】大海を背負って立つ、爆走魚座の魔星獣。\n舎弟に魅せるその背中。共に往くのは覇道の極み。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・トルペドンから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・ユニオンバーストは、敵全体のバリアを解除し、\n　物理小ダメージを与え、さらに自身の行動速度\n　と物理攻撃力を特大アップさせる。このスキル\n　によるダメージでは、ＴＰが回復しない。\n・敵全体に物理固定ダメージを与える。このスキ\n　ルによるダメージでは、ＴＰが回復しない。\n・最も近い敵２キャラに物理小ダメージを与え、\n　物理防御力を小ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "トルペドン", 303002, 0, 1.8, 303002, 0, "【物理】大海を背負って立つ、爆走魚座の魔星獣。\n舎弟に魅せるその背中。共に往くのは覇道の極み。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・トルペドンから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・ユニオンバーストは、敵全体のバリアを解除し、\n　物理小ダメージを与え、さらに自身の行動速度\n　と物理攻撃力を特大アップさせる。このスキル\n　によるダメージでは、ＴＰが回復しない。\n・敵全体に物理固定ダメージを与える。さらに、\n　旗持ち子分の数に応じて追加の物理固定ダメー\n　ジを与える。このスキルによるダメージでは、\n　ＴＰが回復しない。\n・最も近い敵２キャラに物理小ダメージを与え、\n　物理防御力を中ダウンさせる。このスキルの効\n　果はダメージを無効化しても付与される。\n・旗持ち子分を２体召喚し、自身の物理防御力ダ\n　ウンと魔法防御力ダウン効果を解除する。\n・旗持ち子分はトルペドンのＴＰを小回復させる。\n・旗持ち子分のＨＰが一定値以下になった時、敵\n　全体の物理攻撃力と魔法攻撃力を小アップ、ト\n　ルペドンの物理防御力と魔法防御力をバトル終\n　了まで小ダウンさせ、消滅する。");
