INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 16, "オクタスキッド", 304304, 0, 1.6, 304304, 0, "【物理】塔の力で人間に復讐を目論む、軟体魔獣。\n若かりし頃に刺された銛の恨みは、海よりも深い。\n\n・敵全体に物理大ダメージを与える。\n・前から３番目以降の敵に魔法中ダメージを与え、\n　物理防御力と行動速度を中ダウン。\n　さらに、ステータスアップ効果を解除する。\n・残りＨＰの最も高い敵に、残りＨＰに応じた\n　物理大ダメージを与え、ＴＰを中ダウン。");
