INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "コア・ギガース", 307002, 0, 1.8, 307002, 0, "");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "コア・ギガース", 307002, 0, 1.8, 307003, 0, "【物理】異界の魔石から生まれた、弩級の巨人。\n強靭な肉体を武器に、苛烈な猛攻を繰り出す。\n\n・物理攻撃力アップ・ダウン効果の影響を受け\n　やすい。\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方３キャラに物理中ダメージ。\n・一定時間防御の構えをとったあと、その間に受\n　けたダメージ量に応じた物理ダメージを敵全体\n　に与え、前方２キャラを麻痺させる。\n　さらに、自身の物理攻撃力をアップ。\n・敵全体に物理ダメージを与え、大きくノック\n　バックさせる。\n・コア・ギガース自身のＨＰが40％を切ると\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "コア・ギガース", 307002, 0, 1.8, 307004, 0, "【物理】異界の魔石から生まれた、弩級の巨人。\n強靭な肉体を武器に、苛烈な猛攻を繰り出す。\n\n・物理攻撃力アップ・ダウン効果の影響を受け\n　やすい。\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方３キャラに物理中ダメージ。\n・一定時間防御の構えをとったあと、その間に受\n　けたダメージ量に応じた物理ダメージを敵全体\n　に与え、前方２キャラを麻痺させる。\n　さらに、自身の物理攻撃力をアップ。\n・敵全体に物理ダメージを与え、大きくノック\n　バックさせる。\n・コア・ギガース自身のＨＰが40％を切ると\n　さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "コア・ギガース", 307002, 0, 5.0, 307005, 0, "【MODE1】【美食殿】メンバーをシャドウ\nとして召喚。旅の思い出を盾に、３人の心の\n弱さを試す。\n\n・シャドウを出現させる\n・前方３キャラに物理中ダメージ。\n・敵全体に物理ダメージを与え、大きくノック\n　バックさせる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "コア・ギガース", 307002, 0, 5.0, 307006, 0, "【MODE2】強靭な肉体であらゆる攻撃を\n耐え凌ぐ。その怒りを糧に、巨人は\n怒涛の大反撃を見舞う。\n\n・物理攻撃力アップ・ダウン効果の影響を受け\n　やすい。\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方３キャラに物理中ダメージ。\n・一定時間防御の構えをとったあと、その間に受\n　けたダメージ量に応じた物理ダメージを敵全体\n　に与え、前方２キャラを麻痺させる。\n　さらに、自身の物理攻撃力をアップ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "コア・ギガース", 307002, 0, 3.0, 307007, 0, "【MODE3】弩級の巨人は、全身全霊の力を\n解放する。強大無比な破壊力を前に\n小手先の攻撃は通じない。\n\n・物理攻撃力アップ・ダウン効果の影響を受け\n　やすい。\n・ユニオンバーストは、敵全体に物理大ダメージ。\n・前方３キャラに物理中ダメージ。\n・一定時間防御の構えをとったあと、その間に受\n　けたダメージ量に応じた物理ダメージを敵全体\n　に与え、前方２キャラを麻痺させる。\n　さらに、自身の物理攻撃力をアップ。\n・敵全体に物理ダメージを与え、大きくノック\n　バックさせる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "コア・ギガース", 307000, 0, 1.8, 307008, 0, "");
