INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 3, "紅極鳥", 307504, 0, 1.8, 307504, 0, "【物理】塔の魔力を喰らい、暴れ回る巨大魔鳥。\n迂闊に近づくものは、暴風と雷豪が身を引き裂く。\n\n・ユニオンバーストは、敵全体に物理大ダメージ\n　を与える。\n・前から２番目のキャラを中心にした小範囲に物\n　理小ダメージを与え、スタンさせる。\n・前方範囲内の敵に物理中ダメージを与える。");
