INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "叫怖のゴーストファーザー", 311600, 0, 4.0, 311604, 0, "【ＭＯＤＥ１】美味な魂を求め、お化けたちの首\n領が再び姿を現す。残虐なファミリーを従え、\n人々を蹂躙することで街を極上の悲鳴で満たす。\n\n・バトル開始時、ゴーストミニオンズを３体召喚\n　する。\n・ゴーストミニオンズを１体倒すたびに、残り\n　ゴーストミニオンズ数が１減少する。\n　その後、ゴーストミニオンズが再度召喚される。\n・ゴーストミニオンズは、目の前の敵１キャラの\n　ＴＰを減少させ、ゴーストファーザーのＴＰを\n　回復させる。\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "叫怖のゴーストファーザー", 311600, 0, 999.0, 311605, 0, "【ＭＯＤＥ２】シマを荒らす、すべてに制裁を\n下す。ひとたび彼のシマに踏み入れば、はびこる\n魍魎が命を蝕み、生きながら地獄の苦しみを\n与える。\n\n・バトル開始時、キャラの最大ＨＰが35％減少\n　する。減少したＨＰは、バトル終了まで解除\n　できない。\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "叫怖のゴーストファーザー", 311600, 0, 4.0, 311606, 0, "【ＭＯＤＥ３】蓄えた力をすべて吐き出し、真の\n力を解放する。この世界を支配すべく、残虐非道\nの限りを尽くし、地獄へと染め上げる。\n\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。\n・自身に物理/魔法無効バリアを展開する。\n・ゴーストファーザー自身のＨＰが40％を\n　切ると、さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311700, 0, 999.0, 311700, 0, "ゴーストファーザーの側近の手下。\n自身の体を盾にして、一切の攻撃から首領を守る。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311700, 0, 999.0, 311701, 0, "ゴーストファーザーの側近の手下。\n鍛え上げた鉄砲玉で、街に特攻をしかける。血気\n盛んに突撃し、逃げ惑う人々を恐怖の底に陥れる。\n\n・ゴーストブラザーズは攻撃の対象にならない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311700, 0, 999.0, 311702, 0, "ゴーストファーザーの側近の手下。\n鉄砲玉たちと集めた上質な精気を首領へ献上する。\n\n・ゴーストブラザーズは攻撃の対象にならない。\n・ゴーストファーザーのＴＰを回復させる。\n　ゴーストファーザーが行動不能状態のときは、\n　ＴＰを回復しない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311700, 0, 999.0, 311703, 0, "いよいよ訪れた出番に、鼻息荒く推参する。\n気合十分の最強の盾が、歯向かう相手の心を折る。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311800, 0, 999.0, 311800, 0, "ゴーストファーザーの側近の手下。\n取り立てた精気を首領へ渡し、補佐を務めあげる。\n\n・ゴーストファーザーのＴＰを小回復させる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311800, 0, 999.0, 311801, 0, "ゴーストファーザーの側近の手下。\n首領の近辺を警護し、一分の隙も見せない。裏で\nは密かに街の人々を絶望へ追い込もうと暗躍する。\n\n・ゴーストブラザーズは攻撃の対象にならない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311800, 0, 999.0, 311802, 0, "ゴーストファーザーの側近の手下。\n部下と取り立てた極上の精気を首領へ差し出す。\n\n・ゴーストブラザーズは攻撃の対象にならない。\n・ゴーストファーザーのＴＰを回復させる。\n　ゴーストファーザーが行動不能状態のときは、\n　ＴＰを回復しない。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 2, 270, 0, 16, "ゴーストブラザーズ", 311800, 0, 999.0, 311803, 0, "ファミリーの危機を守るべく、冷静に戦況を見極\nめる。命尽きる直前まで、首領の忠実なる右腕と\nして補佐に徹する。\n\n・ゴーストファーザーのＴＰを回復させる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1000, 1, 270, 0, 13, "ブライトホーン", 311900, 0, 2.0, 311900, 0, "【物理】霞を食み、自身の姿も霞の\n如く掴みどころがない。人を嫌い、\n近づく者は容赦なく踏み潰す。\n\n・敵全体に魔法大ダメージを与え\n　る。さらに、火傷状態の敵はダ\n　メージがアップする。\n\n・最も物理攻撃力が高い敵を中心に\n　した範囲内に魔法中ダメージを与\n　え、火傷状態にする。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "オブシダンワイバーン", 312000, 0, 2.0, 312000, 0, "【物理】魔力を宿せし黒曜の結晶竜。美しい体躯\nの輝きは冥府を彷徨いし魂の嘆きといわれている。\n\n・挑発効果の影響を受けやすい。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・オブシダンワイバーンから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回復\n　量を中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与え、さらに\n　ＴＰを中ダウンさせる。このスキルによるダ\n　メージでは、ＴＰが回復しない。このスキルは\n　ダメージを無効化してもＴＰをダウンさせる。\n・敵全体のＨＰ吸収を特大ダウンさせ、物理防御\n　力を大ダウンさせる。\n・物理攻撃力もしくは魔法攻撃力が最も高い敵１\n　キャラに物理特大ダメージを与える。さらにそ\n　のキャラを中心にした小範囲内の敵すべてに物\n　理大ダメージを与え、ＴＰを小ダウンさせる。\n　このスキルはダメージを無効化してもＴＰをダ\n　ウンさせる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "オブシダンワイバーン", 312001, 0, 2.0, 312001, 0, "【物理】魔力を宿せし黒曜の結晶竜。美しい体躯\nの輝きは冥府を彷徨いし魂の嘆きといわれている。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・オブシダンワイバーンから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回\n　復量を中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、さらにＴ\n　Ｐを大ダウンさせる。このスキルはダメージを\n　無効化してもＴＰをダウンさせる。\n・敵全体のＨＰ吸収を特大ダウンし、さらに物理\n　防御力を大ダウンさせる。\n・物理攻撃力、魔法攻撃力いずれかで、最も攻撃\n　力が高い１キャラに物理大ダメージを与える。\n　さらにそのキャラを中心にした小範囲に物理中\n　ダメージを与え、ＴＰを中ダウンさせる。この\n　スキルはダメージを無効化してもＴＰをダウン\n　させる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "オブシダンワイバーン", 312002, 0, 2.0, 312002, 0, "【物理】魔力を宿せし黒曜の結晶竜。美しい体躯\nの輝きは冥府を彷徨いし魂の嘆きといわれている。\n\n・オブシダンワイバーンから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回\n　復量を中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、さらにＴ\n　Ｐを大ダウンさせる。このスキルはダメージを\n　無効化してもＴＰをダウンさせる。\n・敵全体のＨＰ吸収を特大ダウンし、さらに物理\n　防御力を大ダウンさせる。\n・物理攻撃力、魔法攻撃力いずれかで、最も攻撃\n　力が高い１キャラに物理大ダメージを与える。\n　さらにそのキャラを中心にした小範囲に物理中\n　ダメージを与え、ＴＰを中ダウンさせる。この\n　スキルはダメージを無効化してもＴＰをダウン\n　させる。");
