INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "アゾールド", 313800, 0, 1.5, 313800, 0, "常に幾つもの奥の手を隠し持つ、大食の紳士。\n万能人形を駆使し、知略戦略で敵を追い詰める。\n\n・ユニオンバーストは、敵全体にアゾールドの物\n　理攻撃力に応じた物理割合ダメージを与える。\n　さらに、アゾールドの物理防御力と魔法防御力\n　を大ダウン。\n・万能人形を召喚し敵全体をノックバックさせる。\n　万能人形は前から１番目のキャラに物理小ダメー\n　ジを与える。\n　万能人形のＨＰが一定値以下になった時、アゾー\n　ルドの物理防御力と魔法防御力を大ダウンさせ\n　る。\n・前から１番目のキャラに物理小ダメージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "アゾールド", 313801, 0, 1.5, 313801, 0, "常に幾つもの奥の手を隠し持つ、大食の紳士。\n万能人形を駆使し、知略戦略で敵を追い詰める。\n\n・ユニオンバーストは、敵全体にアゾールドの物\n　理攻撃力に応じた物理割合ダメージを与える。\n　さらに、アゾールドの物理防御力と魔法防御力\n　を大ダウン。\n・万能人形を召喚し敵全体をノックバックさせる。\n　万能人形は前から１番目のキャラに物理小ダメー\n　ジを与える。\n　万能人形のＨＰが一定値以下になった時、アゾー\n　ルドの物理防御力と魔法防御力を大ダウンさせ\n　る。\n・前から１番目のキャラに物理小ダメージ。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "アゾールド", 313802, 0, 1.5, 313802, 0, "【物理】常に幾つもの奥の手を隠し持つ、大食の\n紳士。万能人形を駆使し、知略戦略で敵を追い詰\nめる。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に物理割合ダメージ\n・万能人形の数が多いほど：ダメージアップ\n・自身に物理／魔法防御力ダウン\n【スキル１】\n・万能人形３体召喚\n・敵全体をノックバック\n【通常攻撃】\n・前から敵１体に物理ダメージ\n【召喚体特殊効果】\n・自身のＨＰが１％になったとき：\n　アゾールドに物理／魔法防御力ダウン\n【召喚体スキル１】\n・前から敵１体に物理ダメージ");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (313900, 3000, 1, 270, 0, 16, "ゼーン", 313900, 0, 1.0, 313900, 313900, "一騎当千、万夫不当の実力を揮う寡黙な剣士。\n覚醒することで、龍は更なる高みへと昇る。\n\n・ユニオンバーストは、敵全体にゼーンの物理\n　攻撃力と残りＨＰに応じた物理割合ダメージ\n　を与える。\n　このスキルによるダメージでは、ＴＰ回復量\n　は減少する。\n・敵全体に物理中ダメージを与え、スタンさせ\n　る。\n・ゼーンのＴＰ上昇、行動速度、物理攻撃力を\n　大アップ。\n　このスキルの効果は発動するたびにアップす\n　る。\n　３回目の発動時は、さらにＴＰ1000アップ。\n・前から１番目のキャラに物理中ダメージを与\n　え、小さく吹き飛ばす。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (313900, 3000, 1, 270, 0, 16, "ゼーン", 313901, 0, 1.0, 313901, 313900, "");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (313900, 3000, 1, 270, 0, 16, "ゼーン", 313902, 0, 1.0, 313902, 313900, "【物理】一騎当千、万夫不当の実力を揮う寡黙な\n剣士。覚醒することで、龍は更なる高みへと昇る。\n\n【特殊効果】\n・全スキルダメージＴＰ回復減少\n【ユニオンバースト】\n・敵全体に物理割合ダメージ（飛行効果無視・\n　ＴＰ回復不可）\n・自身の物理攻撃力が高いほど：ダメージアップ\n・敵全体に物理／魔法攻撃力ダウン（飛行効果無\n　視・必中）\n【スキル１】\n・敵全体に物理ダメージ（ＴＰ回復不可）\n・敵全体にスタン（必中）\n【スキル２】\n・前から敵１体に物理ダメージ\n・前から敵１体をノックバック（必中）\n【スキル３】\n・自身にＴＰ上昇アップ\n・自身に行動速度アップ\n・自身に物理攻撃力アップ\n・発動するたび：効果量アップ\n・３回目の発動時：ＴＰ上昇アップの代わりに\n　ＴＰ回復");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "宿題", 314000, 0, 1.0, 314000, 0, "");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "得意な宿題", 314001, 0, 1.0, 314001, 0, "まずは手始めに得意な宿題を終わらせよう。\nここでつまづいてはいられない。夏休みの宿題は\nほかにもまだまだあるぞ！！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "ちょっと苦手な宿題", 314002, 0, 1.0, 314002, 0, "誰にでもある苦手科目の宿題。ついつい後回しに\nしがちで、溜まってしまうとなかなか大変。\nここで立ち止まるわけにはいかないぞ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "すごく苦手な宿題", 314003, 0, 1.0, 314003, 0, "眺めているだけで眩暈のするいや～な宿題。\nペンを置いて外に飛び出したくなる衝動を耐え、\nコツコツ進めていくことが攻略のカギ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。");
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "夏休み最後まで残りし宿題", 314004, 0, 1.0, 314004, 0, "SP MODE1");
